",e(t,{lang:"css",code:"@import 'tailwindcss';

@plugin 'tailwindcss-react-aria-components';
@plugin 'tailwindcss-animate';

@variant dark (&:is(.dark *));

@theme inline {
  --font-sans: var(--font-sans), ui-sans-serif, system-ui, sans-serif;
  --font-mono: var(--font-mono), ui-monospace, monospace;

  --color-bg: var(--bg);
  --color-fg: var(--fg);

  --color-primary: var(--primary);
  --color-primary-fg: var(--primary-fg);

  --color-secondary: var(--secondary);
  --color-secondary-fg: var(--secondary-fg);

  --color-primary: var(--primary);
  --color-primary-fg: var(--primary-fg);

  --color-info: var(--info);
  --color-info-fg: var(--info-fg);

  --color-success: var(--success);
  --color-success-fg: var(--success-fg);

  --color-danger: var(--danger);
  --color-danger-fg: var(--danger-fg);

  --color-warning: var(--warning);
  --color-warning-fg: var(--warning-fg);

  --color-muted: var(--muted);
  --color-muted-fg: var(--muted-fg);

  --color-border: var(--muted);
  --color-input: var(--muted);
  --color-ring: var(--primary);

  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);

  --radius-xs: calc(var(--radius-lg) - 4px);
  --radius-sm: calc(var(--radius-lg) - 2px);
  --radius-md: calc(var(--radius-lg) - 1px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius-lg) + 4px);
}

@layer base {
  :root {
      --bg: hsl(0 0% 98.04%);
      --fg: hsl(240 10% 3.92%);
      --primary: hsl(240 10% 3.92%);
      --primary-fg: hsl(0 0% 98.04%);
      --secondary: hsl(208.24 7.3% 45.69%);
      --secondary-fg: hsl(0 0% 100%);
      --primary: hsl(240, 4.88%, 83.92%);
      --primary-fg: hsl(240 10% 3.92%);
      --info: hsl(224.3 76.3% 48%);
      --info-fg: hsl(0 0% 98.04%);
      --danger: hsl(0 72.22% 50.59%);
      --danger-fg: hsl(0 0% 98.04%);
      --success: hsl(142.1 76.2% 36.3%);
      --success-fg: hsl(0 0% 98.04%);
      --warning: hsl(24.6 95% 53.1%);
      --warning-fg: hsl(0 0% 98.04%);
      --muted: hsl(240 4.88% 83.92%);
      --muted-fg: hsl(240 3.83% 46.08%);

      --chart-1: oklch(0.546 0.245 262.881);
      --chart-2: oklch(0.707 0.165 254.624);
      --chart-3: oklch(0.809 0.105 251.813);
      --chart-4: oklch(0.882 0.059 254.128);
      --chart-5: oklch(0.932 0.032 255.585);

      --radius: 0.5rem;
  }
  .dark {
      --bg: hsl(240 10% 3.92%);
      --fg: hsl(0 0% 98.04%);
      --primary: hsl(0 0% 98.04%);
      --primary-fg: hsl(240 10% 3.92%);
      --secondary: hsl(208.24 7.36% 54.71%);
      --secondary-fg: hsl(0 0% 100%);
      --primary: hsl(240, 5.26%, 26.08%);
      --primary-fg: hsl(0 0% 98.04%);
      --info: hsl(221.2 83.2% 53.3%);
      --info-fg: hsl(0 0% 98.04%);
      --danger: hsl(0 72.2% 50.6%);
      --danger-fg: hsl(0 0% 98.04%);
      --success: hsl(142.1 76.2% 36.3%);
      --success-fg: hsl(0 0% 98.04%);
      --warning: hsl(17.5 88.3% 40.4%);
      --warning-fg: hsl(0 0% 98.04%);
      --muted: hsl(240 3.7% 15.88%);
      --muted-fg: hsl(240 5.03% 64.9%);

      --chart-1: oklch(0.488 0.243 264.376);
      --chart-2: oklch(0.623 0.214 259.815);
      --chart-3: oklch(0.707 0.165 254.624);
      --chart-4: oklch(0.809 0.105 251.813);
      --chart-5: oklch(0.882 0.059 254.128);
  }
}

@layer base {
  *,
  ::after,
  ::before,
  ::backdrop,
  ::file-selector-button {
      border-color: var(--muted, currentColor);
  }

  body {
      background-color: var(--bg);
      color: var(--fg);
  }

  *::-webkit-scrollbar {
      border-radius: var(--radius-lg);
      width: 7px;
      height: 7px;
  }

  *::-webkit-scrollbar-track {
      border-radius: var(--radius-lg);
      background: transparent;
  }

  *::-webkit-scrollbar-thumb {
      background: var(--muted-fg);
      border-radius: var(--radius-lg);
  }
}

@layer components {
  .no-scrollbar {
      -ms-overflow-style: none; /* Internet Explorer and Edge */
      scrollbar-width: none; /* Firefox */

      ::-webkit-scrollbar {
          display: none; /* Safari and Chrome */
      }
  }

  pre::-webkit-scrollbar {
      display: none;
  }
