collections/table/table-sorting-demo.raw:
'use client'

import { IconLoader } from 'hq-icons'

import { Card, Table } from '@/components/ui'
import { useAsyncList } from '@react-stately/data'

interface Character {
    title: string
    director: number
    producer: number
    release_date: number
}

export default function TableSortingDemo() {
    const list = useAsyncList<Character>({
        async load({ signal }) {
            const res = await fetch(`https://swapi.py4e.com/api/films`, {
                signal
            })
            const json = await res.json()
            return {
                items: json.results
            }
        },
        async sort({ items, sortDescriptor }) {
            return {
                items: items.sort((a, b) => {
                    // @ts-expect-error unknown-type
                    const first = a[sortDescriptor.column]
                    // @ts-expect-error unknown-type
                    const second = b[sortDescriptor.column]
                    let cmp = (parseInt(first) || first) < (parseInt(second) || second) ? -1 : 1
                    if (sortDescriptor.direction === 'descending') {
                        cmp *= -1
                    }
                    return cmp
                })
            }
        }
    })
    return (
        <Card>
            <Table
                aria-label='Movies'
                selectionMode='multiple'
                sortDescriptor={list.sortDescriptor}
                onSortChange={list.sort}
            >
                <Table.Header>
                    <Table.Column id='title' isRowHeader>
                        Title
                    </Table.Column>
                    <Table.Column id='director' allowsSorting>
                        Director
                    </Table.Column>
                    <Table.Column id='producer'>Producer</Table.Column>
                    <Table.Column id='release_date' allowsSorting>
                        Release Date
                    </Table.Column>
                </Table.Header>
                <Table.Body
                    items={list.items}
                    renderEmptyState={() => (
                        <div className='grid place-content-center p-10'>
                            <IconLoader className='animate-spin' />
                        </div>
                    )}
                >
                    {(item) => (
                        <Table.Row id={item.title}>
                            <Table.Cell>{item.title}</Table.Cell>
                            <Table.Cell>{item.director}</Table.Cell>
                            <Table.Cell>{item.producer}</Table.Cell>
                            <Table.Cell>{item.release_date}</Table.Cell>
                        </Table.Row>
                    )}
                </Table.Body>
            </Table>
        </Card>
    )
}

collections/table/table-drag-demo.raw:
'use client'

import { IconStar } from 'hq-icons'
import { useDragAndDrop } from 'react-aria-components'
import { useListData } from 'react-stately'

import { Card, Table } from '@/components/ui'

export default function TableDragDemo() {
    const list = useListData({
        initialItems: movies
    })

    const { dragAndDropHooks } = useDragAndDrop({
        getItems: (keys) =>
            [...keys].map((key) => ({
                'text/plain': list.getItem(key)?.name ?? ''
            })),
        onReorder(e) {
            if (e.target.dropPosition === 'before') {
                list.moveBefore(e.target.key, e.keys)
            } else if (e.target.dropPosition === 'after') {
                list.moveAfter(e.target.key, e.keys)
            }
        }
    })
    return (
        <Card>
            <Table aria-label='Movies' selectionMode='multiple' dragAndDropHooks={dragAndDropHooks}>
                <Table.Header>
                    <Table.Column>#</Table.Column>
                    <Table.Column isRowHeader>Name</Table.Column>
                    <Table.Column>Genre</Table.Column>
                    <Table.Column>Release</Table.Column>
                    <Table.Column>Rating</Table.Column>
                </Table.Header>
                <Table.Body items={list.items}>
                    {(item) => (
                        <Table.Row>
                            <Table.Cell>{item.id}</Table.Cell>
                            <Table.Cell>{item.name}</Table.Cell>
                            <Table.Cell>{item.genre}</Table.Cell>
                            <Table.Cell>{item.releaseYear}</Table.Cell>
                            <Table.Cell>
                                <div className='flex items-center gap-x-2'>
                                    <IconStar className='text-warning size-3.5' />{' '}
                                    <span>{item.rating}</span>
                                </div>
                            </Table.Cell>
                        </Table.Row>
                    )}
                </Table.Body>
            </Table>
        </Card>
    )
}

