import {
    IconArchive,
    IconDisc,
    IconFileImage,
    IconFileMusic,
    IconFileText,
    IconFileVideo,
    IconFolder
} from 'hq-icons'

export type FileNode = {
    file: string
    children?: FileNode[]
}

const filesInDownloads: FileNode[] = [
    {
        file: 'Compressed',
        children: [
            { file: 'laravel.zip' },
            { file: 'react.zip' },
            { file: 'nextjs.rar' },
            { file: 'ubuntu.iso' }
        ]
    },
    {
        file: 'Docs',
        children: [{ file: 'resume.pdf' }, { file: 'cv.pdf' }]
    },
    {
        file: 'Programs',
        children: [{ file: 'chrome.exe' }, { file: 'vscode.deb' }]
    }
]

const filesInDocuments: FileNode[] = [
    {
        file: 'Thesis',
        children: [
            { file: 'Bab 1.docx' },
            { file: 'Bab 2.docx' },
            { file: 'Bab 3.docx' },
            { file: 'Cover.docx' }
        ]
    },
    { file: 'Secret.txt' },
    { file: 'Project.jpg' }
]

const filesInMusic: FileNode[] = [
    { file: 'The Beatles.mp3' },
    { file: 'Led Zeppelin.mp3' },
    { file: 'Pink Floyd.mp3' },
    { file: 'Queen.wav' },
    { file: 'The Rolling Stones.mp3' },
    { file: 'The Who.wav' }
]

const filesInVideos: FileNode[] = [
    {
        file: 'Mission Impossible',
        children: [
            { file: 'Mission Impossible 1.mp4' },
            { file: 'Mission Impossible 2.mp4' },
            { file: 'Mission Impossible 3.mp4' }
        ]
    },
    { file: 'Star Wars.mp4' },
    { file: 'Avengers - Endgame.mkv' }
]

export const rootFolders: FileNode[] = [
    {
        file: 'Downloads',
        children: filesInDownloads
    },
    {
        file: 'Documents',
        children: filesInDocuments
    },
    {
        file: 'Music',
        children: filesInMusic
    },
    {
        file: 'Videos',
        children: filesInVideos
    }
]

export function mapFilePaths(nodes: FileNode[], parentPath: string = ''): string[] {
    let paths: string[] = []
    for (const node of nodes) {
        const currentPath = parentPath ? `${parentPath}/${node.file}` : node.file
        if (node.children) {
            paths = paths.concat(mapFilePaths(node.children, currentPath))
        } else {
            paths.push(currentPath)
        }
    }
    return paths
}

export const getFileIcon = (ext: string) => {
    let Icon = IconFolder
    switch (ext) {
        case 'jpg':
        case 'png':
            Icon = IconFileImage
            break
        case 'txt':
        case 'docx':
        case 'pdf':
            Icon = IconFileText
            break
        case 'mp3':
        case 'wav':
            Icon = IconFileMusic
            break
        case 'mp4':
        case 'mkv':
            Icon = IconFileVideo
            break
        case 'zip':
        case 'rar':
            Icon = IconArchive
            break
        case 'iso':
            Icon = IconDisc
            break
        default:
        case 'folder':
            Icon = IconFolder
            break
    }
    return <Icon />
}

block/components/paginator.raw:
'use client'

import { cn, Pagination, useMediaQuery } from '@/components/ui'

interface PaginateProps {
    className?: string
    show: number
    total: number
    page: number
    setPage: (page: number) => void
}

export default function Paginator({ className, show, page, total, setPage }: PaginateProps) {
    const isDesktop = useMediaQuery('(min-width: 1024px)')

    const meta = {
        total,
        from: (page - 1) * Number(show) + 1,
        to:
            total - show * page > 0
                ? total - (total - show * page)
                : show * page - Math.abs(total - show * page),
        page,
        last_page: Math.ceil(total / show)
    }

    const actions = {
        first: () => setPage(1),
        prev: () => setPage(page - 1),
        next: () => setPage(page + 1),
        last: () => setPage(meta.last_page)
    }
    return (
        <div
            className={cn(
                'mt-6 flex w-full flex-col-reverse items-center gap-3 xl:flex-row xl:justify-between',
                className
            )}
        >
            <div>
                Showing {meta.from} to {meta.to} of {meta.total} results
            </div>
            <div>
                <Pagination>
                    {isDesktop ? (
                        <Pagination.List>
                            <Pagination.Item
                                role='first'
                                isDisabled={meta.page === 1}
                                onAction={actions.first}
                            />
                            <Pagination.Item
                                role='previous'
                                isDisabled={meta.page === 1}
                                onAction={actions.prev}
                            />
                            <Pagination.Section>
                                {meta.page > 2 && (
                                    <Pagination.Item onAction={actions.first}>1</Pagination.Item>
                                )}
                                {meta.page > 3 && <Pagination.Item role='ellipsis' />}
                                {meta.page !== 1 && (
                                    <Pagination.Item onAction={() => setPage(meta.page - 1)}>
                                        {meta.page - 1}
                                    </Pagination.Item>
                                )}
                                <Pagination.Item isCurrent>{meta.page}</Pagination.Item>
                                {meta.page !== meta.last_page && (
                                    <Pagination.Item onAction={() => setPage(meta.page + 1)}>
                                        {meta.page + 1}
                                    </Pagination.Item>
                                )}
                                {meta.page < meta.last_page - 2 && (
                                    <Pagination.Item role='ellipsis' />
                                )}
                                {meta.page <= meta.last_page - 2 && (
                                    <Pagination.Item onAction={actions.last}>
                                        {meta.last_page}
                                    </Pagination.Item>
                                )}
                            </Pagination.Section>
                            <Pagination.Item
                                role='next'
                                isDisabled={meta.page === meta.last_page}
                                onAction={actions.next}
                            />
                            <Pagination.Item
                                role='last'
                                isDisabled={meta.page === meta.last_page}
                                onAction={actions.last}
                            />
                        </Pagination.List>
                    ) : (
                        <Pagination.List>
                            <Pagination.Item
                                role='first'
                                isDisabled={meta.page === 1}
                                onAction={actions.first}
                            />
                            <Pagination.Item
                                role='previous'
                                isDisabled={meta.page === 1}
                                onAction={actions.prev}
                            />
                            <Pagination.Section
                                aria-label='Pagination. Segment'
                                className='rounded-lg border'
                            >
                                <Pagination.Item role='label'>{meta.page}</Pagination.Item>
                                <Pagination.Item role='separator' />
                                <Pagination.Item className='text-muted-fg' role='label'>
                                    {meta.last_page}
                                </Pagination.Item>
                            </Pagination.Section>
                            <Pagination.Item
                                role='next'
                                isDisabled={meta.page === meta.last_page}
                                onAction={actions.next}
                            />
                            <Pagination.Item
                                role='last'
                                isDisabled={meta.page === meta.last_page}
                                onAction={actions.last}
                            />
                        </Pagination.List>
                    )}
                </Pagination>
            </div>
        </div>
    )
}

