'use client'

import React from 'react'

import { toast } from 'sonner'

import { Button, Form, Modal, Textarea } from '@/components/ui'

export default function ModalNestedDemo() {
    const [isRegistrationModalOpen, setIsRegistrationModalOpen] = React.useState(false)
    const [isProfileSetupModalOpen, setIsProfileSetupModalOpen] = React.useState(false)
    const [isTyping, setIsTyping] = React.useState(false)

    return (
        <>
            <Button onPress={() => setIsRegistrationModalOpen(true)}>Register</Button>

            <Modal.Content
                isOpen={isRegistrationModalOpen}
                onOpenChange={() => setIsRegistrationModalOpen(false)}
                aria-label='Confirm Registration'
            >
                <Modal.Header>
                    <Modal.Title>Confirm Registration</Modal.Title>
                    <Modal.Description>Please confirm your registration details.</Modal.Description>
                </Modal.Header>
                <Modal.Footer>
                    <Modal.Close>Cancel</Modal.Close>
                    <Button
                        onPress={() => {
                            setIsProfileSetupModalOpen(true)
                        }}
                    >
                        Confirm
                    </Button>
                </Modal.Footer>
            </Modal.Content>

            <Modal.Content
                isOpen={isProfileSetupModalOpen}
                onOpenChange={(isOpen) => {
                    if (!isOpen && isTyping) {
                        toast('Profile setup incomplete')
                    }
                    setIsProfileSetupModalOpen(isOpen)
                }}
                aria-label='Profile Setup'
            >
                <Modal.Header>
                    <Modal.Title>Set Up Your Profile</Modal.Title>
                    <Modal.Description>
                        We need a bit more information before you can get started.
                    </Modal.Description>
                </Modal.Header>
                <Form
                    onSubmit={(e) => {
                        e.preventDefault()
                        toast.success('Profile setup complete')
                        setIsProfileSetupModalOpen(false)
                        setIsRegistrationModalOpen(false)
                    }}
                >
                    <Modal.Body className='space-y-4'>
                        <Textarea
                            isRequired
                            label='Bio'
                            placeholder='Tell us something about yourself'
                            onInput={() => setIsTyping(true)}
                        />
                    </Modal.Body>
                    <Modal.Footer>
                        <Modal.Close>Skip for now</Modal.Close>
                        <Button type='submit'>Complete Setup</Button>
                    </Modal.Footer>
                </Form>
            </Modal.Content>
        </>
    )
}

overlays/modal/modal-triggered-by-menu-d.raw:
'use client'

import React from 'react'

import { IconEllipsisVertical } from 'hq-icons'

import { Button, buttonStyles, Loader, Menu, Modal } from '@/components/ui'
import { wait } from '@/lib/utils'

export default function ModalTriggeredByMenuDemo() {
    const [state, setState] = React.useState<string | null>(null)
    const [loading, setLoading] = React.useState<boolean>(false)
    const closeModal = () => setState(null)
    const executeAction = (action: string) => {
        console.log(`${action} is executing`)
        setLoading(true)
        wait(2000).then(() => {
            setLoading(false)
            closeModal()
        })
    }

    const actionType = (t: string | null) => {
        switch (t) {
            case 'delete':
                return {
                    title: 'Delete User',
                    description: 'Are you sure you want to delete this item?',
                    confirmText: 'Delete',
                    action: () => executeAction(t)
                }

            case 'ban':
                return {
                    title: 'Ban User',
                    description: 'Are you sure you want to ban this user?',
                    confirmText: 'Ban',
                    action: () => executeAction(t)
                }

            case 'restore':
                return {
                    title: 'Restore User',
                    description: 'Are you sure you want to restore this user?',
                    confirmText: 'Restore',
                    action: () => executeAction(t)
                }
            default:
                return
        }
    }
    return (
        <>
            <Menu>
                <Menu.Trigger className={buttonStyles({ variant: 'outline' })}>
                    <IconEllipsisVertical />
                </Menu.Trigger>
                <Menu.Content placement='bottom'>
                    <Menu.Item onAction={() => setState('delete')}>Delete</Menu.Item>
                    <Menu.Item isDanger onAction={() => setState('ban')}>
                        Ban
                    </Menu.Item>
                    <Menu.Item onAction={() => setState('restore')}>Restore</Menu.Item>
                </Menu.Content>
            </Menu>

            <Modal.Content isOpen={state !== null} onOpenChange={closeModal}>
                <Modal.Header>
                    <Modal.Title>{actionType(state)?.title}</Modal.Title>
                    <Modal.Description>{actionType(state)?.description}</Modal.Description>
                </Modal.Header>
                <Modal.Footer>
                    <Modal.Close>Cancel</Modal.Close>
                    <Button
                        variant={state === 'ban' ? 'danger' : 'primary'}
                        className='min-w-24'
                        isDisabled={loading}
                        onPress={actionType(state)?.action}
                    >
                        {loading ? <Loader variant='spin' /> : actionType(state)?.confirmText}
                    </Button>
                </Modal.Footer>
            </Modal.Content>
        </>
    )
}

