color-slider.raw:
'use client'

import {
    ColorSlider as ColorSliderPrimitive,
    type ColorSliderProps as ColorSliderPrimitiveProps,
    SliderOutput,
    SliderTrack
} from 'react-aria-components'
import { tv } from 'tailwind-variants'

import { ColorThumb } from './color-thumb'
import { Label } from './field'
import { cr } from './utils'

const trackStyles = tv({
    base: 'group col-span-2 rounded-lg',
    variants: {
        orientation: {
            horizontal: 'h-6 w-full',
            vertical: 'h-56 w-6'
        },
        isDisabled: {
            true: 'bg-muted opacity-75'
        }
    }
})

interface ColorSliderProps extends ColorSliderPrimitiveProps {
    label?: string
    showOutput?: boolean
}

const colorSliderStyles = tv({
    base: 'group relative gap-2',
    variants: {
        orientation: {
            horizontal: 'grid min-w-56 grid-cols-[1fr_auto]',
            vertical: 'flex flex-col items-center justify-center'
        },
        isDisabled: {
            true: 'bg-muted opacity-75'
        }
    }
})
const ColorSlider = ({ showOutput = true, label, className, ...props }: ColorSliderProps) => {
    return (
        <ColorSliderPrimitive
            {...props}
            data-slot='color-slider'
            className={cr(className, (className, renderProps) =>
                colorSliderStyles({ ...renderProps, className })
            )}
        >
            <div className='flex items-center'>
                {label && <Label className='text-sm [grid-area:label]'>{label}</Label>}
                {showOutput && (
                    <SliderOutput className='text-sm [grid-area:output] data-[orientation=horizontal]:ml-auto' />
                )}
            </div>
            <SliderTrack
                className={trackStyles}
                style={({ defaultStyle, isDisabled }) => ({
                    ...defaultStyle,
                    background: isDisabled
                        ? undefined
                        : `${defaultStyle.background}, repeating-conic-gradient(#CCC 0% 25%, white 0% 50%) 50% / 16px 16px`
                })}
            >
                <ColorThumb />
            </SliderTrack>
        </ColorSliderPrimitive>
    )
}

export { ColorSlider }
export type { ColorSliderProps }

radio.raw:
'use client'

import type {
    RadioGroupProps as RadioGroupPrimitiveProps,
    RadioProps as RadioPrimitiveProps,
    ValidationResult
} from 'react-aria-components'
import { RadioGroup as RadioGroupPrimitive, Radio as RadioPrimitive } from 'react-aria-components'
import { tv } from 'tailwind-variants'

import { Description, FieldError, Label } from './field'
import { ctr } from './utils'

interface RadioGroupProps extends Omit<RadioGroupPrimitiveProps, 'children'> {
    label?: string
    children?: React.ReactNode
    description?: string
    errorMessage?: string | ((validation: ValidationResult) => string)
    ref?: React.Ref<HTMLDivElement>
}

const RadioGroup = ({
    label,
    description,
    errorMessage,
    children,
    ref,
    ...props
}: RadioGroupProps) => {
    return (
        <RadioGroupPrimitive
            ref={ref}
            {...props}
            className={ctr(props.className, 'group flex flex-col gap-2')}
        >
            {label && <Label>{label}</Label>}
            <div className='flex gap-2 select-none group-data-[orientation=horizontal]:flex-wrap group-data-[orientation=horizontal]:gap-2 group-data-[orientation=vertical]:flex-col sm:group-data-[orientation=horizontal]:gap-4'>
                {children}
            </div>
            {description && <Description>{description}</Description>}
            <FieldError>{errorMessage}</FieldError>
        </RadioGroupPrimitive>
    )
}

const radioStyles = tv({
    base: 'bg-background size-4 shrink-0 rounded-full border transition',
    variants: {
        isSelected: {
            false: 'border-fg/40',
            true: 'border-primary border-[4.5px]'
        },
        isFocused: {
            true: [
                'border-primary bg-primary/20 ring-primary/20 ring-4',
                'group-data-invalid:border-danger/70 group-data-invalid:bg-danger/20 group-data-invalid:ring-danger/20'
            ]
        },
        isInvalid: {
            true: 'border-danger/70 bg-danger/20'
        },
        isDisabled: {
            false: 'group-data-hovered:border-primary/60 group-data-hovered:bg-primary/10',
            true: 'opacity-50'
        }
    }
})

interface RadioProps extends RadioPrimitiveProps {
    description?: string
    ref?: React.Ref<HTMLLabelElement>
}

const Radio = ({ description, ref, ...props }: RadioProps) => {
    return (
        <RadioPrimitive
            ref={ref}
            className={ctr(
                props.className,
                'group text-fg disabled:text-fg/50 flex items-center gap-2 text-sm transition'
            )}
