                <Text
                    slot={slot ?? 'label'}
                    className={cn('font-medium sm:text-sm', classNames?.label)}
                    {...restProps}
                >
                    {label}
                </Text>
            )}
            {description && (
                <Text
                    slot={slot ?? 'description'}
                    className={cn('text-muted-fg text-xs', classNames?.description)}
                    {...restProps}
                >
                    {description}
                </Text>
            )}
            {!title && children}
        </div>
    )
}

interface MenuLabelProps extends TextProps {
    ref?: React.Ref<HTMLDivElement>
}

const DropdownLabel = ({ className, ref, ...props }: MenuLabelProps) => (
    <Text slot='label' ref={ref} className={cn('col-start-2', className)} {...props} />
)

const DropdownSeparator = ({ className, ...props }: SeparatorProps) => (
    <Separator
        orientation='horizontal'
        className={cn('bg-secondary col-span-full -mx-1 my-1 h-px', className)}
        {...props}
    />
)

const DropdownKeyboard = ({ className, ...props }: React.ComponentProps<typeof Keyboard>) => {
    return <Keyboard className={cn('absolute right-2 pl-2', className)} {...props} />
}

export {
    DropdownItem,
    DropdownItemDetails,
    dropdownItemStyles,
    DropdownKeyboard,
    DropdownLabel,
    DropdownSection,
    dropdownSectionStyles,
    DropdownSeparator
}
export type { DropdownItemDetailProps, DropdownItemProps, DropdownSectionProps }

chart.raw:
'use client'

import { createContext, use, useId, useMemo } from 'react'

import type { LegendProps } from 'recharts'
import { Legend, ResponsiveContainer, Tooltip } from 'recharts'

import { cn } from './utils'

const THEMES = { light: '', dark: '.dark' } as const

type ChartConfig = {
    [k in string]: {
        label?: React.ReactNode
        icon?: React.ComponentType
    } & (
        | { color?: string; theme?: never }
        | { color?: never; theme: Record<keyof typeof THEMES, string> }
    )
}

type ChartContextProps = {
    config: ChartConfig
}

const ChartContext = createContext<ChartContextProps | null>(null)

function useChart() {
    const context = use(ChartContext)

    if (!context) {
        throw new Error('useChart must be used within a <Chart />')
    }

    return context
}

const Chart = ({
    id,
    className,
    children,
    config,
    ref,
    ...props
}: React.ComponentProps<'div'> & {
    config: ChartConfig
    children: React.ComponentProps<typeof ResponsiveContainer>['children']
}) => {
    const uniqueId = useId()
    const chartId = `chart-${id || uniqueId.replace(/:/g, '')}`

    return (
        <ChartContext.Provider value={{ config }}>
            <div
                data-chart={chartId}
                ref={ref}
                className={cn(
                    "[&_.recharts-cartesian-axis-tick_text]:fill-muted-fg [&_.recharts-cartesian-grid_line[stroke='#ccc']]:stroke-border/80 [&_.recharts-curve.recharts-tooltip-cursor]:stroke-border [&_.recharts-polar-grid_[stroke='#ccc']]:stroke-border [&_.recharts-radial-bar-background-sector]:fill-muted [&_.recharts-rectangle.recharts-tooltip-cursor]:fill-muted [&_.recharts-reference-line_[stroke='#ccc']]:stroke-border flex aspect-video justify-center text-xs [&_.recharts-dot[stroke='#fff']]:stroke-transparent [&_.recharts-layer]:outline-hidden [&_.recharts-sector]:outline-hidden [&_.recharts-sector[stroke='#fff']]:stroke-transparent [&_.recharts-surface]:outline-hidden",
                    className
                )}
                {...props}
            >
                <ChartStyle id={chartId} config={config} />
                <ResponsiveContainer>{children}</ResponsiveContainer>
            </div>
        </ChartContext.Provider>
    )
}

const ChartStyle = ({ id, config }: { id: string; config: ChartConfig }) => {
    const colorConfig = Object.entries(config).filter(([, config]) => config.theme || config.color)

    if (!colorConfig.length) {
        return null
    }

    return (
        <style
            dangerouslySetInnerHTML={{
                __html: Object.entries(THEMES)
                    .map(
                        ([theme, prefix]) => `
${prefix} [data-chart=${id}] {
${colorConfig
    .map(([key, itemConfig]) => {
        const color = itemConfig.theme?.[theme as keyof typeof itemConfig.theme] || itemConfig.color
        return color ? `  --color-${key}: ${color};` : null
    })
    .join('\n')}
}
`
                    )
                    .join('\n')
            }}
        />
    )
}

const ChartTooltip = Tooltip

const ChartTooltipContent = ({
    active,
    payload,
    className,
    indicator = 'dot',
    hideLabel = false,
    hideIndicator = false,
    label,
    labelFormatter,
    labelClassName,
    formatter,
    color,
    nameKey,
    labelKey,
    ref
}: React.ComponentProps<typeof Tooltip> &
    React.ComponentProps<'div'> & {
        hideLabel?: boolean
        hideIndicator?: boolean
        indicator?: 'line' | 'dot' | 'dashed'
        nameKey?: string
        labelKey?: string
    }) => {
    const { config } = useChart()

    const tooltipLabel = useMemo(() => {
        if (hideLabel || !payload?.length) {
            return null
        }

        const [item] = payload

        if (!item) {
            return null
        }

        const key = `${labelKey || item.dataKey || item.name || 'value'}`
        const itemConfig = getPayloadConfigFromPayload(config, item, key)
        const value =
            !labelKey && typeof label === 'string'
                ? config[label as keyof typeof config]?.label || label
                : itemConfig?.label

        if (labelFormatter) {
            return <div className={labelClassName}>{labelFormatter(value, payload)}</div>
        }

        if (!value) {
            return null
        }

        return <div className={labelClassName}>{value}</div>
    }, [label, labelFormatter, payload, hideLabel, labelClassName, config, labelKey])

    if (!active || !payload?.length) {
        return null
    }

    const nestLabel = payload.length === 1 && indicator !== 'dot'

    return (
        <div
            ref={ref}
            className={cn(
                'bg-bg text-fg grid min-w-[12rem] items-start gap-1.5 rounded-lg border px-3 py-2 text-xs shadow-xl',
                className
            )}
        >
            {!nestLabel ? tooltipLabel : null}
            <div className='grid gap-1.5'>
                {payload.map((item, index) => {
                    const key = `${nameKey || item.name || item.dataKey || 'value'}`
                    const itemConfig = getPayloadConfigFromPayload(config, item, key)
                    const indicatorColor = color || item.payload.fill || item.color

                    return (
                        <div
                            key={item.dataKey}
                            className={cn(
                                '*:data-[slot=icon]:text-muted-fg flex w-full flex-wrap items-stretch gap-2 *:data-[slot=icon]:size-2.5',
                                indicator === 'dot' && 'items-center'
                            )}
                        >
                            {formatter && item?.value !== undefined && item.name ? (
                                formatter(item.value, item.name, item, index, item.payload)
                            ) : (
                                <>
                                    {itemConfig?.icon ? (
                                        <itemConfig.icon />
                                    ) : (
                                        !hideIndicator && (
                                            <div
                                                className={cn(
                                                    'shrink-0 rounded-[2px] border-(--color-border) bg-(--color-bg)',
                                                    indicator === 'dot' && 'size-2.5',
                                                    indicator === 'line' && 'w-1',
                                                    indicator === 'dashed' &&
                                                        'w-0 border-[1.5px] border-dashed bg-transparent',
                                                    nestLabel && indicator === 'dashed' && 'my-0.5'
                                                )}
                                                style={
                                                    {
                                                        '--color-bg': indicatorColor,
                                                        '--color-border': indicatorColor
                                                    } as React.CSSProperties
                                                }
                                            />
                                        )
                                    )}
                                    <div
                                        className={cn(
                                            'flex flex-1 justify-between leading-none',
                                            nestLabel ? 'items-end' : 'items-center'
                                        )}
                                    >
                                        <div className='grid gap-1.5'>
                                            {nestLabel ? tooltipLabel : null}
                                            <span className='text-muted-fg'>
                                                {itemConfig?.label || item.name}
                                            </span>
                                        </div>
                                        {item.value && (
                                            <span className='text-fg font-mono font-medium tabular-nums'>
                                                {item.value.toLocaleString()}
                                            </span>
                                        )}
                                    </div>
                                </>
                            )}
                        </div>
                    )
                })}
            </div>
        </div>
    )
}

const ChartLegend = Legend

const ChartLegendContent = ({
    className,
    hideIcon = false,
    payload,
    verticalAlign = 'bottom',
    nameKey,
    ref
}: React.ComponentProps<'div'> &
    Pick<LegendProps, 'payload' | 'verticalAlign'> & {
        hideIcon?: boolean
        nameKey?: string
    }) => {
    const { config } = useChart()

    if (!payload?.length) {
        return null
    }

    return (
        <div
            ref={ref}
            className={cn(
                'flex items-center justify-center gap-4',
                verticalAlign === 'top' ? 'pb-3' : 'pt-3',
                className
            )}
        >
            {payload.map((item) => {
                const key = `${nameKey || item.dataKey || 'value'}`
                const itemConfig = getPayloadConfigFromPayload(config, item, key)

                return (
                    <div
                        key={item.value}
                        className={cn(
                            '*:data-[slot=icon]:text-muted-fg flex items-center gap-1.5 *:data-[slot=icon]:size-3'
                        )}
                    >
                        {itemConfig?.icon && !hideIcon ? (
                            <itemConfig.icon />
                        ) : (
                            <div
                                className='h-2 w-2 shrink-0 rounded-[2px]'
                                style={{
                                    backgroundColor: item.color
                                }}
                            />
                        )}
                        {itemConfig?.label}
                    </div>
                )
            })}
        </div>
    )
}

function getPayloadConfigFromPayload(config: ChartConfig, payload: unknown, key: string) {
    if (typeof payload !== 'object' || payload === null) {
        return undefined
    }

    const payloadPayload =
        'payload' in payload && typeof payload.payload === 'object' && payload.payload !== null
            ? payload.payload
            : undefined

    let configLabelKey: string = key

    if (key in payload && typeof payload[key as keyof typeof payload] === 'string') {
        configLabelKey = payload[key as keyof typeof payload] as string
    } else if (
        payloadPayload &&
        key in payloadPayload &&
        typeof payloadPayload[key as keyof typeof payloadPayload] === 'string'
    ) {
        configLabelKey = payloadPayload[key as keyof typeof payloadPayload] as string
    }

    return configLabelKey in config ? config[configLabelKey] : config[key as keyof typeof config]
}

Chart.Legend = ChartLegend
Chart.Tooltip = ChartTooltip
Chart.LegendContent = ChartLegendContent
Chart.TooltipContent = ChartTooltipContent

export { Chart, ChartStyle }
export type { ChartConfig }

popover.raw:
'use client'

import type {
    DialogTriggerProps,
    ModalOverlayProps,
    PopoverProps as PopoverPrimitiveProps
} from 'react-aria-components'
import {
    type DialogProps,
    DialogTrigger,
    Modal,
    ModalOverlay,
    OverlayArrow,
    PopoverContext,
    Popover as PopoverPrimitive,
    useSlottedContext
} from 'react-aria-components'
import { twMerge } from 'tailwind-merge'
import { tv } from 'tailwind-variants'

import type {
    DialogBodyProps,
    DialogFooterProps,
    DialogHeaderProps,
    DialogTitleProps
} from './dialog'
import { Dialog } from './dialog'
import { cn, cr, useMediaQuery } from './utils'

type PopoverProps = DialogTriggerProps
const Popover = (props: PopoverProps) => {
    return <DialogTrigger {...props} />
}

const PopoverTitle = ({ level = 2, className, ...props }: DialogTitleProps) => (
    <Dialog.Title
        className={twMerge('sm:leading-none', level === 2 && 'sm:text-lg', className)}
        {...props}
    />
)

const PopoverHeader = ({ className, ...props }: DialogHeaderProps) => (
    <Dialog.Header className={twMerge('sm:p-4', className)} {...props} />
)

const PopoverFooter = ({ className, ...props }: DialogFooterProps) => (
    <Dialog.Footer className={cn('sm:p-4', className)} {...props} />
)

const PopoverBody = ({ className, ref, ...props }: DialogBodyProps) => (
    <Dialog.Body ref={ref} className={cn('sm:px-4 sm:pt-0', className)} {...props} />
)

const content = tv({
    base: [
        'peer/popover-content bg-bg text-fg max-w-xs rounded-xl border bg-clip-padding shadow-xs transition-transform sm:max-w-3xl sm:text-sm dark:backdrop-saturate-200'
    ],
    variants: {
        isPicker: {
            true: 'max-h-72 min-w-(--trigger-width) overflow-y-auto p-0',
            false: 'min-w-80'
        },
        isMenu: {
            true: 'p-0'
        },
        isEntering: {
            true: [
                'fade-in animate-in duration-150 ease-out',
                'data-[placement=left]:slide-in-from-right-1 data-[placement=right]:slide-in-from-left-1 data-[placement=top]:slide-in-from-bottom-1 data-[placement=bottom]:slide-in-from-top-1'
            ]
        },
        isExiting: {
            true: [
                'fade-out animate-out duration-100 ease-in',
                'data-[placement=left]:slide-out-to-right-1 data-[placement=right]:slide-out-to-left-1 data-[placement=top]:slide-out-to-bottom-1 data-[placement=bottom]:slide-out-to-top-1'
            ]
        }
    }
})

const drawer = tv({
    base: [
        'bg-bg fixed top-auto bottom-0 z-50 max-h-full w-full max-w-2xl border border-b-transparent outline-hidden'
    ],
    variants: {
        isMenu: {
            true: 'rounded-t-xl p-0 [&_[role=dialog]]:*:not-has-[[data-slot=dialog-body]]:px-1',
            false: 'rounded-t-2xl'
        },
        isEntering: {
            true: [
                '[will-change:transform] [transition:transform_0.5s_cubic-bezier(0.32,_0.72,_0,_1)]',
                'fade-in-0 slide-in-from-bottom-56 animate-in duration-200',
                '[transition:translate3d(0,_100%,_0)]',
                'sm:slide-in-from-bottom-auto sm:slide-in-from-top-[20%]'
            ]
        },
        isExiting: {
            true: 'slide-out-to-bottom-56 animate-out duration-200 ease-in'
        }
    }
})

interface PopoverContentProps
    extends Omit<React.ComponentProps<typeof Modal>, 'children'>,
        Omit<PopoverPrimitiveProps, 'children' | 'className'>,
        Omit<ModalOverlayProps, 'className'> {
    children: React.ReactNode
    showArrow?: boolean
    style?: React.CSSProperties
    respectScreen?: boolean
    'aria-label'?: DialogProps['aria-label']
    'aria-labelledby'?: DialogProps['aria-labelledby']
    className?: string | ((values: { defaultClassName?: string }) => string)
}

const PopoverContent = ({
    respectScreen = true,
    children,
    showArrow = true,
    className,
    ...props
}: PopoverContentProps) => {
    const isMobile = useMediaQuery('(max-width: 600px)')
    const popoverContext = useSlottedContext(PopoverContext)!
    const isMenuTrigger = popoverContext?.trigger === 'MenuTrigger'
    const isSubmenuTrigger = popoverContext?.trigger === 'SubmenuTrigger'
    const isMenu = isMenuTrigger || isSubmenuTrigger
    const offset = showArrow ? 12 : 8
    const effectiveOffset = isSubmenuTrigger ? offset - 5 : offset
    return isMobile && respectScreen ? (
        <ModalOverlay
            className='bg-bg/10 fixed top-0 left-0 isolate z-50 h-(--visual-viewport-height) w-full [--visual-viewport-vertical-padding:16px]'
            {...props}
            isDismissable
        >
            <Modal
                className={cr(className, (className, renderProps) =>
                    drawer({ ...renderProps, isMenu, className })
                )}
            >
                <Dialog
                    data-dialog
                    aria-label={props['aria-label'] || isMenu ? 'Menu' : undefined}
                    className='has-input:overflow-visible outline-hidden'
                >
                    {children}
                </Dialog>
            </Modal>
        </ModalOverlay>
    ) : (
        <PopoverPrimitive
            offset={effectiveOffset}
            {...props}
            className={cr(className, (className, renderProps) =>
                content({
                    ...renderProps,
                    className
                })
            )}
        >
            {showArrow && (
                <OverlayArrow className='group'>
                    <svg
                        width={12}
                        height={12}
                        viewBox='0 0 12 12'
                        className='fill-bg stroke-border block group-data-[placement=bottom]:rotate-180 group-data-[placement=left]:-rotate-90 group-data-[placement=right]:rotate-90'
                    >
                        <path d='M0 0 L6 6 L12 0' />
                    </svg>
                </OverlayArrow>
            )}
            <Dialog
                role='dialog'
                aria-label={props['aria-label'] || isMenu ? 'Menu' : undefined}
                className='outline-hidden'
            >
                {children}
            </Dialog>
        </PopoverPrimitive>
    )
}

const PopoverPicker = ({ children, className, ...props }: PopoverContentProps) => {
    return (
        <PopoverPrimitive
            {...props}
            className={cr(className, (className, renderProps) =>
                content({
                    ...renderProps,
                    isPicker: true,
                    className
                })
            )}
        >
            {children}
        </PopoverPrimitive>
    )
}

const PopoverTrigger = Dialog.Trigger
const PopoverClose = Dialog.Close
const PopoverDescription = Dialog.Description

Popover.Trigger = PopoverTrigger
Popover.Close = PopoverClose
Popover.Description = PopoverDescription
Popover.Content = PopoverContent
Popover.Body = PopoverBody
Popover.Footer = PopoverFooter
Popover.Header = PopoverHeader
Popover.Picker = PopoverPicker
Popover.Title = PopoverTitle

export { Popover }
export type { PopoverContentProps, PopoverProps }

badge.raw:
'use client'

