text-field.raw:
'use client'

import { useState } from 'react'

import { IconEye, IconEyeClosed } from 'hq-icons'
import {
    Button as ButtonPrimitive,
    TextField as TextFieldPrimitive,
    type TextFieldProps as TextFieldPrimitiveProps
} from 'react-aria-components'
import { twJoin } from 'tailwind-merge'

import type { TextInputDOMProps } from '@react-types/shared'

import type { FieldProps } from './field'
import { Description, FieldError, FieldGroup, Input, Label } from './field'
import { Loader } from './loader'
import { ctr } from './utils'

type InputType = Exclude<TextInputDOMProps['type'], 'password'>

interface BaseTextFieldProps extends TextFieldPrimitiveProps, FieldProps {
    prefix?: React.ReactNode
    suffix?: React.ReactNode
    isPending?: boolean
    className?: string
}

interface RevealableTextFieldProps extends BaseTextFieldProps {
    isRevealable: true
    type: 'password'
}

interface NonRevealableTextFieldProps extends BaseTextFieldProps {
    isRevealable?: never
    type?: InputType
}

type TextFieldProps = RevealableTextFieldProps | NonRevealableTextFieldProps

const TextField = ({
    placeholder,
    label,
    description,
    errorMessage,
    prefix,
    suffix,
    isPending,
    className,
    isRevealable,
    type,
    ...props
}: TextFieldProps) => {
    const [isPasswordVisible, setIsPasswordVisible] = useState(false)
    const inputType = isRevealable ? (isPasswordVisible ? 'text' : 'password') : type
    const handleTogglePasswordVisibility = () => {
        setIsPasswordVisible((prev) => !prev)
    }
    return (
        <TextFieldPrimitive
            type={inputType}
            {...props}
            className={ctr(className, 'group flex flex-col gap-y-1.5')}
        >
            {!props.children ? (
                <>
                    {label && <Label>{label}</Label>}
                    <FieldGroup
                        isInvalid={!!errorMessage}
                        isDisabled={props.isDisabled}
                        className={twJoin(
                            '**:[button]:h-8 **:[button]:rounded-[calc(var(--radius-lg)*0.5)] **:[button]:px-3.5 **:[button]:inset-ring-0 **:[button]:inset-shadow-none **:[button]:has-data-[slot=icon]:w-8 **:[button]:has-data-[slot=icon]:p-0 dark:**:[button]:inset-ring-0',
                            '[&>[data-slot=suffix]>button]:mr-[calc(var(--spacing)*-1.7)] [&>[data-slot=suffix]>button]:data-focus-visible:outline-1 [&>[data-slot=suffix]>button]:data-focus-visible:outline-offset-1',
                            '[&>[data-slot=prefix]>button]:ml-[calc(var(--spacing)*-1.7)] [&>[data-slot=prefix]>button]:data-focus-visible:outline-1 [&>[data-slot=prefix]>button]:data-focus-visible:outline-offset-1'
                        )}
                        data-loading={isPending ? 'true' : undefined}
                    >
                        {prefix ? (
                            <span data-slot='prefix' className='atrs x2e2'>
                                {prefix}
                            </span>
                        ) : null}
                        <Input placeholder={placeholder} />
                        {isRevealable ? (
                            <ButtonPrimitive
                                type='button'
                                aria-label='Toggle password visibility'
                                onPress={handleTogglePasswordVisibility}
                                className='data-focus-visible:*:data-[slot=icon]:text-primary *:data-[slot=icon]:text-muted-fg relative mr-1 grid shrink-0 place-content-center rounded-sm border-transparent outline-hidden'
                            >
                                {isPasswordVisible ? <IconEyeClosed /> : <IconEye />}
                            </ButtonPrimitive>
                        ) : isPending ? (
                            <Loader variant='spin' data-slot='suffix' />
                        ) : suffix ? (
                            <span data-slot='suffix'>{suffix}</span>
                        ) : null}
                    </FieldGroup>
                    {description && <Description>{description}</Description>}
                    <FieldError>{errorMessage}</FieldError>
                </>
            ) : (
                props.children
            )}
        </TextFieldPrimitive>
    )
}

export { TextField }
export type { TextFieldProps }

menu.raw:
'use client'

import { createContext, use } from 'react'

import { IconCheck, IconChevronRight } from 'hq-icons'
import type {
    ButtonProps,
    MenuItemProps as MenuItemPrimitiveProps,
    MenuProps as MenuPrimitiveProps,
    MenuSectionProps as MenuSectionPrimitiveProps,
    MenuTriggerProps as MenuTriggerPrimitiveProps,
    PopoverProps
} from 'react-aria-components'
import {
    Button,
    Collection,
    Header,
    MenuItem as MenuItemPrimitive,
    Menu as MenuPrimitive,
    MenuSection as MenuSectionPrimitive,
    MenuTrigger as MenuTriggerPrimitive,
    SubmenuTrigger as SubmenuTriggerPrimitive
} from 'react-aria-components'
import type { VariantProps } from 'tailwind-variants'
import { tv } from 'tailwind-variants'

import {
    DropdownItemDetails,
    DropdownKeyboard,
    DropdownLabel,
    DropdownSeparator,
    dropdownItemStyles,
    dropdownSectionStyles
} from './dropdown'
import { Popover } from './popover'
import { cn, cr } from './utils'

interface MenuContextProps {
    respectScreen: boolean
}

const MenuContext = createContext<MenuContextProps>({ respectScreen: true })

interface MenuProps extends MenuTriggerPrimitiveProps {
    respectScreen?: boolean
}

const Menu = ({ respectScreen = true, ...props }: MenuProps) => {
    return (
        <MenuContext value={{ respectScreen }}>
            <MenuTriggerPrimitive {...props}>{props.children}</MenuTriggerPrimitive>
        </MenuContext>
    )
}

const MenuSubMenu = ({ delay = 0, ...props }) => (
    <SubmenuTriggerPrimitive {...props} delay={delay}>
        {props.children}
    </SubmenuTriggerPrimitive>
)

const menuStyles = tv({
    slots: {
        menu: "grid max-h-[calc(var(--visual-viewport-height)-10rem)] grid-cols-[auto_1fr] overflow-auto rounded-xl p-1 outline-hidden [clip-path:inset(0_0_0_0_round_calc(var(--radius-lg)-2px))] sm:max-h-[inherit] *:[[role='group']+[role=group]]:mt-4 *:[[role='group']+[role=separator]]:mt-1",
        popover: 'z-50 p-0 shadow-xs outline-hidden sm:min-w-40',
        trigger: [
            'data-focus-visible:ring-primary relative inline text-left outline-hidden data-focus-visible:ring-1'
        ]
    }
})

const { menu, popover, trigger } = menuStyles()

interface MenuTriggerProps extends ButtonProps {
    className?: string
    ref?: React.Ref<HTMLButtonElement>
}

const MenuTrigger = ({ className, ref, ...props }: MenuTriggerProps) => (
    <Button ref={ref} data-slot='menu-trigger' className={trigger({ className })} {...props}>
        {(values) => (
            <>{typeof props.children === 'function' ? props.children(values) : props.children}</>
        )}
    </Button>
)

interface MenuContentProps<T>
    extends Omit<PopoverProps, 'children' | 'style'>,
        MenuPrimitiveProps<T> {
    className?: string
    popoverClassName?: string
    showArrow?: boolean
    respectScreen?: boolean
}

const MenuContent = <T extends object>({
    className,
    showArrow = false,
    popoverClassName,
    ...props
}: MenuContentProps<T>) => {
    const { respectScreen } = use(MenuContext)
    return (
        <Popover.Content
            respectScreen={respectScreen}
            showArrow={showArrow}
            className={popover({
                className: popoverClassName
            })}
            {...props}
        >
            <MenuPrimitive className={menu({ className })} {...props} />
        </Popover.Content>
    )
}

interface MenuItemProps extends MenuItemPrimitiveProps, VariantProps<typeof dropdownItemStyles> {
    isDanger?: boolean
}

const MenuItem = ({ className, isDanger = false, children, ...props }: MenuItemProps) => {
    const textValue = props.textValue || (typeof children === 'string' ? children : undefined)
    return (
        <MenuItemPrimitive
            className={cr(className, (className, renderProps) =>
                dropdownItemStyles({
                    ...renderProps,
                    className: renderProps.hasSubmenu
                        ? cn([
                              'data-open:data-danger:bg-danger/10 data-open:data-danger:text-danger',
                              'data-open:bg-primary data-open:text-primary-fg data-open:*:data-[slot=icon]:text-primary-fg data-open:*:[.text-muted-fg]:text-primary-fg',
                              className
                          ])
                        : className
                })
            )}
            textValue={textValue}
            data-danger={isDanger ? 'true' : undefined}
            {...props}
        >
            {(values) => (
                <>
                    {values.isSelected && (
                        <>
                            {values.selectionMode === 'single' && (
                                <span
                                    data-slot='bullet-icon'
                                    className='-mx-0.5 mr-2 flex size-4 shrink-0 items-center justify-center **:data-[slot=indicator]:size-4 **:data-[slot=indicator]:shrink-0'
                                >
                                    <IconCheck data-slot='indicator' />
                                </span>
                            )}
                            {values.selectionMode === 'multiple' && (
                                <IconCheck
                                    className='-mx-0.5 mr-2 size-4'
                                    data-slot='checked-icon'
                                />
                            )}
                        </>
                    )}

                    {typeof children === 'function' ? children(values) : children}

                    {values.hasSubmenu && (
                        <IconChevronRight
                            data-slot='chevron'
                            className='absolute right-2 size-3.5'
                        />
                    )}
                </>
            )}
        </MenuItemPrimitive>
    )
}

export interface MenuHeaderProps extends React.ComponentProps<typeof Header> {
    separator?: boolean
}

const MenuHeader = ({ className, separator = false, ...props }: MenuHeaderProps) => (
    <Header
        className={cn(
            'col-span-full px-2.5 py-2 text-base font-semibold sm:text-sm',
            separator && '-mx-1 mb-1 border-b sm:px-3 sm:pb-[0.625rem]',
            className
        )}
        {...props}
    />
)

const { section, header } = dropdownSectionStyles()

interface MenuSectionProps<T> extends MenuSectionPrimitiveProps<T> {
    ref?: React.Ref<HTMLDivElement>
    title?: string
}

const MenuSection = <T extends object>({ className, ref, ...props }: MenuSectionProps<T>) => {
    return (
        <MenuSectionPrimitive ref={ref} className={section({ className })} {...props}>
            {'title' in props && <Header className={header()}>{props.title}</Header>}
            <Collection items={props.items}>{props.children}</Collection>
        </MenuSectionPrimitive>
    )
}

const MenuSeparator = DropdownSeparator
const MenuItemDetails = DropdownItemDetails
const MenuKeyboard = DropdownKeyboard
const MenuLabel = DropdownLabel

Menu.Keyboard = MenuKeyboard
Menu.Content = MenuContent
Menu.Header = MenuHeader
Menu.Item = MenuItem
Menu.Section = MenuSection
Menu.Separator = MenuSeparator
Menu.ItemDetails = MenuItemDetails
Menu.Label = MenuLabel
Menu.Trigger = MenuTrigger
Menu.Submenu = MenuSubMenu

export { Menu }
export type { MenuContentProps, MenuItemProps, MenuProps, MenuSectionProps, MenuTriggerProps }

