context-menu.raw:
'use client'

import { createContext, use, useRef, useState } from 'react'

import { tv } from 'tailwind-variants'

import type { MenuContentProps } from './menu'
import { Menu } from './menu'
import { focusButtonStyles } from './utils'

interface ContextMenuTriggerContextType {
    buttonRef: React.RefObject<HTMLButtonElement | null>
    contextMenuOffset: { offset: number; crossOffset: number } | null
    setContextMenuOffset: React.Dispatch<
        React.SetStateAction<{ offset: number; crossOffset: number } | null>
    >
}

const ContextMenuTriggerContext = createContext<ContextMenuTriggerContextType | undefined>(
    undefined
)

const useContextMenuTrigger = () => {
    const context = use(ContextMenuTriggerContext)
    if (!context) {
        throw new Error('useContextMenuTrigger must be used within a ContextMenuTrigger')
    }
    return context
}

interface ContextMenuProps {
    children: React.ReactNode
}

const ContextMenu = ({ children }: ContextMenuProps) => {
    const [contextMenuOffset, setContextMenuOffset] = useState<{
        offset: number
        crossOffset: number
    } | null>(null)
    const buttonRef = useRef<HTMLButtonElement>(null)

    return (
        <ContextMenuTriggerContext.Provider
            value={{ buttonRef, contextMenuOffset, setContextMenuOffset }}
        >
            {children}
        </ContextMenuTriggerContext.Provider>
    )
}

const contextMenuTriggerStyles = tv({
    extend: focusButtonStyles,
    base: 'cursor-default data-focused:outline-hidden',
    variants: {
        isDisabled: {
            true: 'cursor-default opacity-50'
        }
    }
})

type ContextMenuTriggerProps = React.ButtonHTMLAttributes<HTMLButtonElement>

const ContextMenuTrigger = ({ className, ...props }: ContextMenuTriggerProps) => {
    const { buttonRef, setContextMenuOffset } = useContextMenuTrigger()

    const onContextMenu = (e: React.MouseEvent<HTMLButtonElement>) => {
        e.preventDefault()
        const rect = e.currentTarget.getBoundingClientRect()
        setContextMenuOffset({
            offset: e.clientY - rect.bottom,
            crossOffset: e.clientX - rect.left
        })
    }
    return (
        <button
            className={contextMenuTriggerStyles({ isDisabled: props.disabled, className })}
            ref={buttonRef}
            aria-haspopup='menu'
            onContextMenu={onContextMenu}
            {...props}
        />
    )
}

type ContextMenuContentProps<T> = Omit<
    MenuContentProps<T>,
    'showArrow' | 'isOpen' | 'onOpenChange' | 'triggerRef' | 'placement' | 'shouldFlip'
>

const ContextMenuContent = <T extends object>(props: ContextMenuContentProps<T>) => {
    const { contextMenuOffset, setContextMenuOffset, buttonRef } = useContextMenuTrigger()
    return contextMenuOffset ? (
        <Menu.Content
            isOpen={!!contextMenuOffset}
            onOpenChange={() => setContextMenuOffset(null)}
            triggerRef={buttonRef}
            shouldFlip={false}
            placement='bottom left'
            offset={contextMenuOffset?.offset}
            crossOffset={contextMenuOffset?.crossOffset}
            onClose={() => setContextMenuOffset(null)}
            {...props}
        />
    ) : null
}

const ContextMenuItem = Menu.Item
const ContextMenuSeparator = Menu.Separator
const ContextMenuItemDetails = Menu.ItemDetails
const ContextMenuSection = Menu.Section
const ContextMenuHeader = Menu.Header
const ContextMenuKeyboard = Menu.Keyboard
const ContextMenuLabel = Menu.Label

ContextMenu.Trigger = ContextMenuTrigger
ContextMenu.Content = ContextMenuContent
ContextMenu.Item = ContextMenuItem
ContextMenu.Label = ContextMenuLabel
ContextMenu.Separator = ContextMenuSeparator
ContextMenu.ItemDetails = ContextMenuItemDetails
ContextMenu.Section = ContextMenuSection
ContextMenu.Header = ContextMenuHeader
ContextMenu.Keyboard = ContextMenuKeyboard

export { ContextMenu }
export type { ContextMenuProps }

multi-select.raw:
'use client'

import { useCallback, useEffect, useId, useRef, useState } from 'react'

import { IconChevronDown } from 'hq-icons'
import { useFilter } from 'react-aria'
import type {
    ComboBoxProps as ComboBoxPrimitiveProps,
    Key,
    ValidationResult
} from 'react-aria-components'
import { ComboBox } from 'react-aria-components'
import type { ListData } from 'react-stately'
import { useListData } from 'react-stately'
import { tv } from 'tailwind-variants'

import { Button } from './button'
import type { FieldProps } from './field'
import { Description, FieldError, Input, Label } from './field'
import { ListBox } from './list-box'
import { Popover } from './popover'
import type { RestrictedVariant, TagGroupProps } from './tag-group'
import { Tag } from './tag-group'
import { VisuallyHidden, cn } from './utils'

const multiSelectStyles = tv({
    slots: {
        multiSelectField: 'group flex w-full min-w-80 flex-col',
        multiSelect: [
            'border-muted relative flex min-h-10 flex-row flex-wrap items-center rounded-lg border px-1 transition',
            'has-[input[data-focused=true]]:border-primary/70',
            'has-[input[data-invalid=true][data-focused=true]]:border-primary/70',
            'has-[input[data-invalid=true]]:border-danger',
            'has-[input[data-focused=true]]:ring-primary/20 has-[input[data-focused=true]]:ring-4',
            'has-[input[data-disabled=false][data-focused=true]]:border-primary/60'
        ],
        chevronButton:
            'text-muted-fg data-focused:text-fg data-hovered:text-fg -mr-2 grid size-8 place-content-center rounded-sm',
        input: 'ml-1 flex-1 px-0.5 py-1 ring-0 shadow-none',
        comboBoxChild: 'inline-flex flex-1 flex-wrap items-center px-0',
        comboBox: 'group peer flex flex-1'
    }
})

const { multiSelectField, multiSelect, chevronButton, input, comboBox, comboBoxChild } =
    multiSelectStyles()

interface SelectedKey {
    id: Key
    name: string
}

interface MultiSelectProps<T extends object>
    extends FieldProps,
        Omit<
            ComboBoxPrimitiveProps<T>,
            | 'children'
            | 'validate'
            | 'allowsEmptyCollection'
            | 'inputValue'
            | 'selectedKey'
            | 'className'
            | 'value'
            | 'onSelectionChange'
            | 'onInputChange'
        >,
        Pick<TagGroupProps, 'shape'> {
    variant?: RestrictedVariant
    items: Array<T>
    selectedItems: ListData<T>
    className?: string
    onItemInserted?: (key: Key) => void
    onItemCleared?: (key: Key) => void
    renderEmptyState?: (inputValue: string) => React.ReactNode
    tag: (item: T) => React.ReactNode
    children: React.ReactNode | ((item: T) => React.ReactNode)
    errorMessage?: string | ((validation: ValidationResult) => string)
}

const MultiSelect = <T extends SelectedKey>({
    children,
    items,
    selectedItems,
    onItemCleared,
    onItemInserted,
    className,
    name,
    renderEmptyState,
    errorMessage,
    ...props
}: MultiSelectProps<T>) => {
    const tagGroupIdentifier = useId()
    const triggerRef = useRef<HTMLDivElement | null>(null)
    const [width, setWidth] = useState(0)

    const { contains } = useFilter({ sensitivity: 'base' })
    const selectedKeys = selectedItems.items.map((i) => i.id)

    const filter = useCallback(
        (item: T, filterText: string) => {
            return !selectedKeys.includes(item.id) && contains(item.name, filterText)
        },
        [contains, selectedKeys]
    )

    const accessibleList = useListData({
        initialItems: items,
        filter
    })

    const [fieldState, setFieldState] = useState<{
        selectedKey: Key | null
        inputValue: string
    }>({
        selectedKey: null,
        inputValue: ''
    })

    const onRemove = useCallback(
        (keys: Set<Key>) => {
            const key = keys.values().next().value
            if (key) {
                selectedItems.remove(key)
                setFieldState({
                    inputValue: '',
                    selectedKey: null
                })
                onItemCleared?.(key)
            }
        },
        [selectedItems, onItemCleared]
    )

    const onSelectionChange = (id: Key | null) => {
        if (!id) {
            return
        }

        const item = accessibleList.getItem(id)

        if (!item) {
            return
        }

        if (!selectedKeys.includes(id)) {
            selectedItems.append(item)
            setFieldState({
                inputValue: '',
                selectedKey: id
            })
            onItemInserted?.(id)
        }

        accessibleList.setFilterText('')
    }

    const onInputChange = (value: string) => {
        setFieldState((prev) => ({
            inputValue: value,
            selectedKey: value === '' ? null : prev.selectedKey
        }))

        accessibleList.setFilterText(value)
    }

    const popLast = useCallback(() => {
        if (selectedItems.items.length === 0) {
            return
        }

        const endKey = selectedItems.items[selectedItems.items.length - 1]

        if (endKey) {
            selectedItems.remove(endKey.id)
            onItemCleared?.(endKey.id)
        }

        setFieldState({
            inputValue: '',
            selectedKey: null
        })
    }, [selectedItems, onItemCleared])

    const onKeyDownCapture = useCallback(
        (e: React.KeyboardEvent<HTMLInputElement>) => {
            if (e.key === 'Backspace' && fieldState.inputValue === '') {
                popLast()
            }
        },
        [popLast, fieldState.inputValue]
    )

    useEffect(() => {
        const trigger = triggerRef.current
        if (!trigger) return

        const observer = new ResizeObserver((entries) => {
            for (const entry of entries) {
                setWidth(entry.target.clientWidth)
            }
        })

        observer.observe(trigger)
        return () => {
            observer.unobserve(trigger)
        }
    }, [])

    const triggerButtonRef = useRef<HTMLButtonElement | null>(null)

    return (
        <div className={multiSelectField({ className })}>
            {props.label && <Label className='mb-1'>{props.label}</Label>}
            <div className={props.isDisabled ? 'opacity-50' : ''}>
                <div
                    ref={triggerRef}
                    className={cn(
                        multiSelect({ className }),
                        !props.isDisabled && 'group-hover:border-primary/60'
                    )}
                >
                    <Tag.Group
                        shape={props.shape}
                        variant={props.variant}
                        aria-label='Selected items'
                        id={tagGroupIdentifier}
                        onRemove={onRemove}
                    >
                        <Tag.List
                            items={selectedItems.items}
                            className={cn(
                                selectedItems.items.length !== 0 && 'px-1 py-1.5',
                                'gap-1.5 outline-hidden [&_.tag]:last:-mr-1',
                                props.shape === 'rounded' &&
                                    '[&_.tag]:rounded-[calc(var(--radius-lg)-4px)]'
                            )}
                        >
                            {props.tag}
                        </Tag.List>
                    </Tag.Group>
                    <ComboBox
                        menuTrigger='focus'
                        {...props}
                        allowsEmptyCollection
                        aria-label='Available items'
                        className={comboBox()}
                        items={accessibleList.items}
                        selectedKey={fieldState.selectedKey}
                        inputValue={fieldState.inputValue}
                        onSelectionChange={onSelectionChange}
                        onInputChange={onInputChange}
                    >
                        <div className={comboBoxChild({ className })}>
                            <Input
                                placeholder={props.placeholder}
                                className={input()}
                                onBlur={() => {
                                    setFieldState({
                                        inputValue: '',
                                        selectedKey: null
                                    })
                                    accessibleList.setFilterText('')
                                }}
                                onKeyDownCapture={onKeyDownCapture}
                            />

                            <VisuallyHidden>
                                <Button
                                    slot='remove'
                                    type='button'
                                    aria-label='Remove'
                                    size='icon'
                                    variant='ghost'
                                    ref={triggerButtonRef}
                                >
                                    <IconChevronDown />
                                </Button>
                            </VisuallyHidden>
                        </div>
                        <Popover.Picker
                            isNonModal
                            className='max-w-none'
                            style={{ width: `${width}px` }}
                            triggerRef={triggerRef}
                            trigger='ComboBox'
                        >
                            <ListBox.Picker
                                className='grid-cols-none'
                                renderEmptyState={() =>
                                    renderEmptyState ? (
                                        renderEmptyState(fieldState.inputValue)
                                    ) : (
                                        <Description className='block p-3'>
                                            {fieldState.inputValue ? (
                                                <>
                                                    No results found for:{' '}
                                                    <strong className='text-fg font-medium'>
                                                        {fieldState.inputValue}
                                                    </strong>
                                                </>
                                            ) : (
                                                'No options'
                                            )}
                                        </Description>
                                    )
                                }
                                selectionMode='multiple'
                            >
                                {children}
                            </ListBox.Picker>
                        </Popover.Picker>
                    </ComboBox>
                    <div
                        className='relative ml-auto flex items-center justify-center px-1 [&_button>svg]:transition peer-data-[open]:[&_button>svg]:rotate-180'
                        aria-hidden
                    >
                        <button
                            type='button'
                            className={chevronButton()}
                            onClick={() => triggerButtonRef.current?.click()}
                            tabIndex={-1}
                        >
                            <IconChevronDown className='size-4' />
                        </button>
                    </div>
                </div>
            </div>
            {props.description && <Description>{props.description}</Description>}
            {<FieldError>{errorMessage}</FieldError>}
            {name && <input hidden name={name} value={selectedKeys.join(',')} readOnly />}
        </div>
    )
}

MultiSelect.Tag = Tag
MultiSelect.Item = ListBox.Item

export { MultiSelect }
export type { MultiSelectProps, SelectedKey }

