                                <p className='text-muted-fg absolute bottom-2 left-3'>
                                    Write something...
                                </p>
                            }
                            contentEditable={
                                <TextField
                                    aria-label='Editor'
                                    id='parent'
                                    className={cn('group relative flex flex-col gap-1', className)}
                                >
                                    {label && <Label>{label}</Label>}

                                    {!hideToolbar && <ToolbarPlugin />}
                                    <ContentEditable
                                        autoFocus
                                        aria-disabled={isDisabled}
                                        disabled={isDisabled}
                                        className={cn(
                                            'bg-bg min-h-32 w-full min-w-0 rounded-lg border px-2.5 py-2 text-base transition outline-none focus:outline-none sm:text-sm',
                                            'focus:border-primary/70 hover:border-primary/60 focus:ring-primary/20 focus:ring-4',
                                            'invalid:border-danger invalid:ring-danger/20 invalid:ring-4',
                                            isDisabled && 'opacity-50'
                                        )}
                                    />
                                    {description && <Description>{description}</Description>}
                                    <FieldError>{errorMessage}</FieldError>
                                </TextField>
                            }
                            ErrorBoundary={LexicalErrorBoundary}
                        />
                    ) : (
                        <PlainTextPlugin
                            placeholder={
                                <p className='text-muted-fg absolute bottom-2 left-3'>
                                    Write something...
                                </p>
                            }
                            contentEditable={
                                <TextField
                                    aria-label='Editor'
                                    id='parent'
                                    className={cn('group relative flex flex-col gap-1', className)}
                                >
                                    {label && <Label>{label}</Label>}

                                    {!hideToolbar && <Toolbar />}
                                    <ContentEditable
                                        autoFocus
                                        aria-disabled={isDisabled}
                                        disabled={isDisabled}
                                        className={cn(
                                            'bg-bg min-h-32 w-full min-w-0 rounded-lg border px-2.5 py-2 text-base shadow-sm transition outline-none focus:outline-none sm:text-sm',
                                            'focus:border-primary/70 focus:ring-primary/20 hover:border-primary/60 focus:ring-4',
                                            'invalid:border-danger invalid:ring-danger/20 invalid:ring-4',
                                            isDisabled && 'opacity-50'
                                        )}
                                    />
                                    {description && <Description>{description}</Description>}
                                    <FieldError>{errorMessage}</FieldError>
                                </TextField>
                            }
                            ErrorBoundary={LexicalErrorBoundary}
                        />
                    )}
                    <HistoryPlugin />
                </div>
                <ListPlugin />
                <LinkPlugin />
                {as === 'rich-text' && <MarkdownShortcutPlugin transformers={TRANSFORMERS} />}
                <OnChange returnType={returnType} value={props.value} onChange={props.onChange} />
            </div>
        </LexicalComposer>
    )
}

interface OnChangeProps {
    value?: string
    onChange?: (value: string) => void
    returnType: 'markdown' | 'html'
}

function OnChange({ value, onChange, returnType }: OnChangeProps) {
    const [editor] = useLexicalComposerContext()
    const [isFirstRender, setIsFirstRender] = React.useState(true)

    React.useEffect(() => {
        if (returnType === 'html') {
            if (!value || !isFirstRender) return

            setIsFirstRender(false)
            editor.update(() => {
                const currentHTML = $generateHtmlFromNodes(editor)
                if (currentHTML !== value) {
                    $getRoot().clear()
                    const parser = new DOMParser()
                    const dom = parser.parseFromString(value, 'text/html')
                    const nodes = $generateNodesFromDOM(editor, dom)
                    $insertNodes(nodes)
                }
            })
        }
    }, [editor, value, isFirstRender, returnType])

    React.useEffect(() => {
        setIsFirstRender(true)
    }, [value])

    return (
        <OnChangePlugin
            onChange={(editorState) => {
                editorState.read(() => {
                    onChange?.(
                        returnType === 'markdown'
                            ? $convertToMarkdownString(TRANSFORMERS, undefined, true)
                            : $generateHtmlFromNodes(editor)
                    )
                })
            }}
        />
    )
}

function BlockTypeDropdown({ blockType }: { blockType: string }) {
    const blockIcons = {
        h1: <IconHeading1 />,
        h2: <IconHeading2 />,
        h3: <IconHeading3 />,
        paragraph: <IconParagraph />,
        bullet: <IconList />,
        number: <IconListOrdered />,
        quote: <IconTextQuote />
    }
    const [editor] = useLexicalComposerContext()

    const formatHeading = (headingLevel: HeadingTagType) => {
        editor.update(() => {
            const selection = $getSelection()
            $setBlocksType(selection, () => $createHeadingNode(headingLevel))
        })
    }

    const formatParagraph = () => {
        editor.update(() => {
            const selection = $getSelection()
            $setBlocksType(selection, () => $createParagraphNode())
        })
    }

    const formatOrderedList = () => {
        if (blockType !== 'number') {
            editor.dispatchCommand(INSERT_ORDERED_LIST_COMMAND, undefined)
        } else {
            editor.dispatchCommand(REMOVE_LIST_COMMAND, undefined)
        }
    }

    const formatUnorderedList = () => {
        if (blockType !== 'bullet') {
            editor.dispatchCommand(INSERT_UNORDERED_LIST_COMMAND, undefined)
        } else {
            editor.dispatchCommand(REMOVE_LIST_COMMAND, undefined)
        }
    }

    const formatQuote = () => {
        editor.update(() => {
            const selection = $getSelection()
            $setBlocksType(selection, () => $createQuoteNode())
        })
    }

    return (
        <Menu>
            <Button isDisabled={!editor.isEditable()} variant='outline' size='icon'>
                {blockIcons[blockType as keyof typeof blockIcons]}
            </Button>
            <Menu.Content selectionMode='single' aria-label='Block type' selectedKeys={[blockType]}>
                <Menu.Item id='paragraph' onAction={formatParagraph}>
                    <IconParagraph />
                    <Menu.Label>Paragraph</Menu.Label>
                </Menu.Item>
                <Menu.Item id='h1' onAction={() => formatHeading('h1')}>
                    <IconHeading1 />
                    <Menu.Label>Heading 1</Menu.Label>
                </Menu.Item>
                <Menu.Item id='h2' onAction={() => formatHeading('h2')}>
                    <IconHeading2 />
                    <Menu.Label>Heading 2</Menu.Label>
                </Menu.Item>
                <Menu.Item id='h3' onAction={() => formatHeading('h3')}>
                    <IconHeading3 />
                    <Menu.Label>Heading 3</Menu.Label>
                </Menu.Item>
                <Menu.Item id='bullet' onAction={formatUnorderedList}>
                    <IconList />
                    <Menu.Label>Unordered List</Menu.Label>
                </Menu.Item>
                <Menu.Item id='number' onAction={formatOrderedList}>
                    <IconListOrdered />
                    <Menu.Label>Ordered List</Menu.Label>
                </Menu.Item>
                <Menu.Item id='quote' onAction={formatQuote}>
                    <IconTextQuote />
                    <Menu.Label>Quote</Menu.Label>
                </Menu.Item>
            </Menu.Content>
        </Menu>
    )
}

export function ToolbarPlugin() {
    const [editor] = useLexicalComposerContext()
    const [formatText, setFormatText] = React.useState({
        bold: false,
        italic: false,
        underline: false,
        strikethrough: false,
        subscript: false,
        superscript: false
    })
    const [blockType, setBlockType] = useState<string>('paragraph')

    const [canUndo, setCanUndo] = useState<boolean>(false)
    const [canRedo, setCanRedo] = useState<boolean>(false)

    const $updateToolbar = useCallback(() => {
        const selection = $getSelection()
        if ($isRangeSelection(selection)) {
            setFormatText({
                bold: selection.hasFormat('bold'),
                italic: selection.hasFormat('italic'),
                underline: selection.hasFormat('underline'),
                strikethrough: selection.hasFormat('strikethrough'),
                subscript: selection.hasFormat('subscript'),
                superscript: selection.hasFormat('superscript')
            })
            const anchorNode = selection.anchor.getNode()

            let element =
                anchorNode.getKey() === 'root'
                    ? anchorNode
                    : $findMatchingParent(anchorNode, (e) => {
                          const parent = e.getParent()
                          return parent !== null && $isRootOrShadowRoot(parent)
                      })

            if (element === null) {
                element = anchorNode.getTopLevelElementOrThrow()
            }

            const elementDOM = editor.getElementByKey(element.getKey())

            if (elementDOM !== null) {
                if ($isListNode(element)) {
                    const parentList = $getNearestNodeOfType<ListNode>(anchorNode, ListNode)
                    const type = parentList ? parentList.getListType() : element.getListType()
                    setBlockType(type)
                } else {
                    const type = $isHeadingNode(element) ? element.getTag() : element.getType()
                    setBlockType(type)
                }
            }
        }
    }, [editor])

    useEffect(() => {
        return mergeRegister(
            editor.registerCommand(
                SELECTION_CHANGE_COMMAND,
                () => {
                    $updateToolbar()
                    return false
                },
                COMMAND_PRIORITY_CRITICAL
            ),
            editor.registerUpdateListener(({ editorState }) => {
                editorState.read(() => {
                    $updateToolbar()
                })
            })
        )
    }, [editor, $updateToolbar])

    useEffect(() => {
        return mergeRegister(
            editor.registerCommand(
                CAN_UNDO_COMMAND,
                (payload) => {
                    setCanUndo(payload)
                    return false
                },
                COMMAND_PRIORITY_CRITICAL
            ),
            editor.registerCommand(
                CAN_REDO_COMMAND,
                (payload) => {
                    setCanRedo(payload)
                    return false
                },
                COMMAND_PRIORITY_CRITICAL
            )
        )
    }, [editor])

    return (
        <Toolbar className='mb-2' aria-label='Toolbar'>
            <Toolbar.Group aria-label='Formats'>
                <BlockTypeDropdown blockType={blockType} />
                <Toolbar.Item
                    isDisabled={!editor.isEditable()}
                    size='icon'
                    isSelected={formatText.bold}
                    onChange={() => {
                        editor.dispatchCommand(FORMAT_TEXT_COMMAND, 'bold')
                    }}
                >
                    <IconBold />
                </Toolbar.Item>
                <Toolbar.Item
                    isDisabled={!editor.isEditable()}
                    size='icon'
                    isSelected={formatText.italic}
                    onChange={() => {
                        editor.dispatchCommand(FORMAT_TEXT_COMMAND, 'italic')
                    }}
                >
                    <IconItalic />
                </Toolbar.Item>
                <Toolbar.Item
                    isDisabled={!editor.isEditable()}
                    size='icon'
                    isSelected={formatText.underline}
                    onChange={() => {
                        editor.dispatchCommand(FORMAT_TEXT_COMMAND, 'underline')
                    }}
                >
                    <IconUnderline />
                </Toolbar.Item>
                <Toolbar.Item
                    isDisabled={!editor.isEditable()}
                    size='icon'
                    isSelected={formatText.strikethrough}
                    onChange={() => {
                        editor.dispatchCommand(FORMAT_TEXT_COMMAND, 'strikethrough')
                    }}
                >
                    <IconStrikethrough />
                </Toolbar.Item>
                <Toolbar.Item
                    isDisabled={!editor.isEditable()}
                    size='icon'
                    isSelected={formatText.subscript}
                    onChange={() => {
                        editor.dispatchCommand(FORMAT_TEXT_COMMAND, 'subscript')
                    }}
                >
                    <IconSubscript />
                </Toolbar.Item>
                <Toolbar.Item
                    isDisabled={!editor.isEditable()}
                    size='icon'
                    isSelected={formatText.superscript}
                    onChange={() => {
                        editor.dispatchCommand(FORMAT_TEXT_COMMAND, 'superscript')
                    }}
                >
                    <IconSuperscript />
                </Toolbar.Item>
            </Toolbar.Group>
            <Toolbar.Separator />
            <Toolbar.Group aria-label='Actions'>
                <Toolbar.Item
                    size='icon'
                    isDisabled={!canUndo || !editor.isEditable()}
                    isSelected={false}
                    onPress={() => editor.dispatchCommand(UNDO_COMMAND, undefined)}
                >
                    <IconUndo />
                </Toolbar.Item>
                <Toolbar.Item
                    size='icon'
                    isDisabled={!canRedo || !editor.isEditable()}
                    isSelected={false}
                    onPress={() => editor.dispatchCommand(REDO_COMMAND, undefined)}
                >
                    <IconRedo />
                </Toolbar.Item>
            </Toolbar.Group>
        </Toolbar>
    )
}

export { RichTextField }

sidebar.raw:
'use client'

import { createContext, use, useCallback, useEffect, useMemo, useState } from 'react'

import { IconMenu, IconPanelLeftClose } from 'hq-icons'
import type {
    ButtonProps,
    DisclosureGroupProps,
    DisclosureProps,
    LinkProps,
    LinkRenderProps,
    SeparatorProps as SidebarSeparatorProps
} from 'react-aria-components'
import {
    Disclosure,
    DisclosureGroup,
    DisclosurePanel,
    Header,
    Heading,
    Link,
    Separator,
    Text,
    Button as Trigger
} from 'react-aria-components'
import { twJoin } from 'tailwind-merge'
import { tv } from 'tailwind-variants'

import { Badge } from './badge'
import { Button } from './button'
import { Indicator } from './disclosure'
import { Sheet } from './sheet'
import { Tooltip } from './tooltip'
import { cn, cr, ctr, useMediaQuery } from './utils'

const SIDEBAR_COOKIE_NAME = 'sidebar:state'
const SIDEBAR_COOKIE_MAX_AGE = 60 * 60 * 24 * 7
type SidebarContextProps = {
    state: 'expanded' | 'collapsed'
    open: boolean
    setOpen: (open: boolean) => void
    isOpenOnMobile: boolean
    setIsOpenOnMobile: (open: boolean) => void
    isMobile: boolean
    toggleSidebar: () => void
    fixed: boolean
}

const SidebarContext = createContext<SidebarContextProps | null>(null)

const useSidebar = () => {
    const context = use(SidebarContext)
    if (!context) {
        throw new Error('useSidebar must be used within a Sidebar.')
    }

    return context
}

interface SidebarProviderProps extends React.ComponentProps<'div'> {
    defaultOpen?: boolean
    isOpen?: boolean
    shortcut?: string
    onOpenChange?: (open: boolean) => void
    fixed?: boolean
}

const SidebarProvider = ({
    defaultOpen = true,
    isOpen: openProp,
    onOpenChange: setOpenProp,
    className,
    children,
    shortcut = 'b',
    fixed = false,
    ref,
    ...props
}: SidebarProviderProps) => {
    const isMobile = useMediaQuery('(max-width: 767px)')
    const [openMobile, setOpenMobile] = useState(false)

    const [internalOpenState, setInternalOpenState] = useState(defaultOpen)
    const open = openProp ?? internalOpenState
    const setOpen = useCallback(
        (value: boolean | ((value: boolean) => boolean)) => {
            const openState = typeof value === 'function' ? value(open) : value

            if (setOpenProp) {
                setOpenProp(openState)
            } else {
                setInternalOpenState(openState)
            }

            document.cookie = `${SIDEBAR_COOKIE_NAME}=${openState}; path=/; max-age=${SIDEBAR_COOKIE_MAX_AGE}`
        },
        [setOpenProp, open]
    )

    const toggleSidebar = useCallback(() => {
        return isMobile ? setOpenMobile((open) => !open) : setOpen((open) => !open)
    }, [isMobile, setOpen])

    useEffect(() => {
        const handleKeyDown = (event: KeyboardEvent) => {
            if (event.key === shortcut && (event.metaKey || event.ctrlKey) && !fixed) {
                event.preventDefault()
                toggleSidebar()
            }
        }

        window.addEventListener('keydown', handleKeyDown)
        return () => window.removeEventListener('keydown', handleKeyDown)
    }, [toggleSidebar, shortcut, fixed])

    const state = open ? 'expanded' : 'collapsed'

    const contextValue = useMemo<SidebarContextProps>(
        () => ({
            fixed,
            state,
            open,
            setOpen,
            isMobile,
            isOpenOnMobile: openMobile,
            setIsOpenOnMobile: setOpenMobile,
            toggleSidebar
        }),
        [state, fixed, open, setOpen, isMobile, openMobile, toggleSidebar]
    )

    return (
        <SidebarContext value={contextValue}>
            <div
                className={cn(
                    '@container **:data-[slot=icon]:shrink-0',
                    '[--sidebar-width-dock:3.25rem] [--sidebar-width-mobile:18rem] [--sidebar-width:17rem]',
                    '[--sidebar-border:color-mix(in_oklch,var(--color-bg)_25%,black_6%)]',
                    'dark:[--sidebar-border:color-mix(in_oklch,var(--color-bg)_55%,white_10%)]',
                    '[--sidebar-accent:color-mix(in_oklab,var(--color-bg)_95%,black_5%)]',
                    'dark:[--sidebar-accent:color-mix(in_oklab,var(--color-bg)_90%,white_10%)]',
                    'text-fg flex min-h-svh w-full',
                    'group/sidebar-root has-data-[sidebar-variant=inset]:bg-bg',
                    className
                )}
                ref={ref}
                {...props}
            >
                {children}
            </div>
        </SidebarContext>
    )
}

const gap = tv({
    base: [
        'w-(--sidebar-width) group-data-[sidebar-collapsible=hidden]/sidebar-container:w-0',
        'relative h-svh bg-transparent transition-[width] duration-200 ease-linear',
