sheet.raw:
'use client'

import type { DialogProps, DialogTriggerProps, ModalOverlayProps } from 'react-aria-components'
import { DialogTrigger, Modal, ModalOverlay } from 'react-aria-components'
import { type VariantProps, tv } from 'tailwind-variants'

import { Dialog } from './dialog'
import { cr } from './utils'

const overlayStyles = tv({
    base: [
        'bg-fg/15 dark:bg-bg/40 fixed top-0 left-0 isolate z-50 flex h-(--visual-viewport-height) w-full items-center justify-center p-4'
    ],
    variants: {
        isBlurred: {
            true: 'bg-bg/15 dark:bg-bg/40 backdrop-blur'
        },
        isEntering: {
            true: 'fade-in animate-in duration-300 ease-out'
        },
        isExiting: {
            true: 'fade-out animate-out duration-200 ease-in'
        }
    }
})

type Sides = 'top' | 'bottom' | 'left' | 'right'
const generateCompoundVariants = (sides: Array<Sides>) => {
    return sides.map((side) => ({
        side,
        isFloat: true,
        className:
            side === 'top'
                ? 'top-2 inset-x-2 rounded-xl ring-1 border-b-0'
                : side === 'bottom'
                  ? 'bottom-2 inset-x-2 rounded-xl ring-1 border-t-0'
                  : side === 'left'
                    ? 'left-2 inset-y-2 rounded-xl ring-1 border-r-0'
                    : 'right-2 inset-y-2 rounded-xl ring-1 border-l-0'
    }))
}

const contentStyles = tv({
    base: 'border-fg/5 bg-bg text-fg dark:border-muted fixed z-50 grid gap-4 shadow-lg transition ease-in-out',
    variants: {
        isEntering: {
            true: 'animate-in duration-300'
        },
        isExiting: {
            true: 'animate-out duration-200'
        },
        side: {
            top: 'data-entering:slide-in-from-top data-exiting:slide-out-to-top inset-x-0 top-0 rounded-b-2xl border-b',
            bottom: 'data-entering:slide-in-from-bottom data-exiting:slide-out-to-bottom inset-x-0 bottom-0 rounded-t-2xl border-t',
            left: 'data-entering:slide-in-from-left data-exiting:slide-out-to-left inset-y-0 left-0 h-auto w-full max-w-xs overflow-y-auto border-r',
            right: 'data-entering:slide-in-from-right data-exiting:slide-out-to-right inset-y-0 right-0 h-auto w-full max-w-xs overflow-y-auto border-l'
        },
        isFloat: {
            false: 'border-fg/20 dark:border-muted',
            true: 'ring-fg/5 dark:ring-border'
        }
    },
    compoundVariants: generateCompoundVariants(['top', 'bottom', 'left', 'right'])
})

type SheetProps = DialogTriggerProps
const Sheet = (props: SheetProps) => {
    return <DialogTrigger {...props} />
}

interface SheetContentProps
    extends Omit<React.ComponentProps<typeof Modal>, 'children' | 'className'>,
        Omit<ModalOverlayProps, 'className'>,
        VariantProps<typeof overlayStyles> {
    'aria-label'?: DialogProps['aria-label']
    'aria-labelledby'?: DialogProps['aria-labelledby']
    role?: DialogProps['role']
    closeButton?: boolean
    isBlurred?: boolean
    isFloat?: boolean
    side?: Sides
    classNames?: {
        overlay?: ModalOverlayProps['className']
        content?: ModalOverlayProps['className']
    }
}

const SheetContent = ({
    classNames,
    isBlurred = false,
    isDismissable = true,
    side = 'right',
    role = 'dialog',
    closeButton = true,
    isFloat = true,
    children,
    ...props
}: SheetContentProps) => {
    const _isDismissable = role === 'alertdialog' ? false : isDismissable
    return (
        <ModalOverlay
            isDismissable={_isDismissable}
            className={cr(classNames?.overlay, (className, renderProps) => {
                return overlayStyles({
                    ...renderProps,
                    isBlurred,
                    className
                })
            })}
            {...props}
        >
            <Modal
                className={cr(classNames?.content, (className, renderProps) =>
                    contentStyles({
                        ...renderProps,
                        side,
                        isFloat,
                        className
                    })
                )}
                {...props}
            >
                {(values) => (
                    <Dialog
                        role={role}
                        aria-label={props['aria-label'] ?? undefined}
                        className='h-full'
                    >
                        <>
                            {typeof children === 'function' ? children(values) : children}
                            {closeButton && (
                                <Dialog.CloseIndicator
                                    className='top-2.5 right-2.5'
                                    isDismissable={_isDismissable}
                                />
                            )}
                        </>
                    </Dialog>
                )}
            </Modal>
        </ModalOverlay>
    )
}

const SheetTrigger = Dialog.Trigger
const SheetFooter = Dialog.Footer
const SheetHeader = Dialog.Header
const SheetTitle = Dialog.Title
const SheetDescription = Dialog.Description
const SheetBody = Dialog.Body
const SheetClose = Dialog.Close

Sheet.Trigger = SheetTrigger
Sheet.Footer = SheetFooter
Sheet.Header = SheetHeader
Sheet.Title = SheetTitle
Sheet.Description = SheetDescription
Sheet.Body = SheetBody
Sheet.Close = SheetClose
Sheet.Content = SheetContent

export { Sheet }
export type { SheetContentProps, SheetProps, Sides }

breadcrumbs.raw:
'use client'

import { createContext, use } from 'react'

import { IconChevronRight } from 'hq-icons'
import type { BreadcrumbProps, BreadcrumbsProps, LinkProps } from 'react-aria-components'
import { Breadcrumb, Breadcrumbs as BreadcrumbsPrimitive } from 'react-aria-components'

import { Link } from './link'
import { cn } from './utils'

type BreadcrumbsContextProps = { separator?: 'chevron' | 'slash' | boolean }
const BreadcrumbsProvider = createContext<BreadcrumbsContextProps>({
    separator: 'chevron'
})

const Breadcrumbs = <T extends object>({
    className,
    ...props
}: BreadcrumbsProps<T> & BreadcrumbsContextProps) => {
    return (
        <BreadcrumbsProvider value={{ separator: props.separator }}>
            <BreadcrumbsPrimitive {...props} className={cn('flex items-center gap-2', className)} />
        </BreadcrumbsProvider>
    )
}

interface BreadcrumbsItemProps extends BreadcrumbProps, BreadcrumbsContextProps {
    href?: string
}

const BreadcrumbsItem = ({
    href,
    separator = true,
    className,
    ...props
}: BreadcrumbsItemProps & Partial<Omit<LinkProps, 'className'>>) => {
    const { separator: contextSeparator } = use(BreadcrumbsProvider)
    separator = contextSeparator ?? separator
    const separatorValue = separator === true ? 'chevron' : separator

    return (
        <Breadcrumb {...props} className={cn(className, 'flex items-center gap-2 text-sm')}>
            {({ isCurrent }) => (
                <>
                    <Link href={href} {...props} />
                    {!isCurrent && separator !== false && <Separator separator={separatorValue} />}
                </>
            )}
        </Breadcrumb>
    )
}

const Separator = ({
    separator = 'chevron'
}: {
    separator?: BreadcrumbsItemProps['separator']
}) => {
    return (
        <span className='*:text-muted-fg *:shrink-0 *:data-[slot=icon]:size-3.5'>
            {separator === 'chevron' && <IconChevronRight />}
            {separator === 'slash' && <span className='text-muted-fg'>/</span>}
        </span>
    )
}

Breadcrumbs.Item = BreadcrumbsItem

export { Breadcrumbs }
export type { BreadcrumbsItemProps, BreadcrumbsProps }

file-trigger.raw:
'use client'

import { IconFolder, IconPaperclip, IconUpload } from 'hq-icons'
import {
    FileTrigger as FileTriggerPrimitive,
    type FileTriggerProps as FileTriggerPrimitiveProps
} from 'react-aria-components'
import { VariantProps } from 'tailwind-variants'

import { Button, buttonStyles } from './button'

interface FileTriggerProps extends FileTriggerPrimitiveProps, VariantProps<typeof buttonStyles> {
    isDisabled?: boolean
    ref?: React.RefObject<HTMLInputElement>
}

const FileTrigger = ({ variant, size, shape, ref, ...props }: FileTriggerProps) => {
    return (
        <FileTriggerPrimitive ref={ref} {...props}>
            <Button isDisabled={props.isDisabled} size={size} shape={shape} variant={variant}>
                {props.children ? (
                    props.children
                ) : (
                    <>
                        {props.allowsMultiple ? (
                            <>
                                <IconUpload /> Browse a files
                            </>
                        ) : props.acceptDirectory ? (
                            <>
                                <IconFolder /> Browse
                            </>
                        ) : (
                            <>
                                <IconPaperclip /> Browse a file
                            </>
                        )}
                        ...
                    </>
                )}
            </Button>
        </FileTriggerPrimitive>
    )
