    base: 'group-data-focused:border-primary/70 group-data-hovered:border-primary/60',
    variants: {
        isInvalid: {
            true: 'group-data-focused:border-danger/70'
        },
        isDisabled: {
            true: 'group-data-focused:border-muted'
        }
    }
})

const numberFieldStyles = tv({
    slots: {
        base: 'group flex flex-col gap-y-1.5',
        stepperButton:
            'text-muted-fg data-pressed:bg-primary data-pressed:text-primary-fg group-data-disabled:bg-secondary/70 h-10 cursor-default px-3'
    }
})

const { base, stepperButton } = numberFieldStyles()

interface NumberFieldProps extends NumberFieldPrimitiveProps {
    label?: string
    description?: string
    placeholder?: string
    errorMessage?: string | ((validation: ValidationResult) => string)
}

const NumberField = ({
    label,
    placeholder,
    description,
    className,
    errorMessage,
    ...props
}: NumberFieldProps) => {
    const isMobile = useMediaQuery('(max-width: 768px)')
    return (
        <NumberFieldPrimitive {...props} className={ctr(className, base())}>
            {label && <Label>{label}</Label>}
            <FieldGroup className='overflow-hidden'>
                {(renderProps) => (
                    <>
                        {isMobile ? <StepperButton slot='decrement' className='border-r' /> : null}
                        <Input className='tabular-nums' placeholder={placeholder} />
                        <div
                            className={fieldBorderStyles({
                                ...renderProps,
                                className: 'grid h-10 place-content-center border-s'
                            })}
                        >
                            {isMobile ? (
                                <StepperButton slot='increment' />
                            ) : (
                                <div className='flex h-full flex-col'>
                                    <StepperButton
                                        slot='increment'
                                        emblemType='chevron'
                                        className='h-5 px-1'
                                    />
                                    <div
                                        className={fieldBorderStyles({
                                            ...renderProps,
                                            className: 'border-muted border-b'
                                        })}
                                    />
                                    <StepperButton
                                        slot='decrement'
                                        emblemType='chevron'
                                        className='h-5 px-1'
                                    />
                                </div>
                            )}
                        </div>
                    </>
                )}
            </FieldGroup>
            {description && <Description>{description}</Description>}
            <FieldError>{errorMessage}</FieldError>
        </NumberFieldPrimitive>
    )
}

interface StepperButtonProps extends ButtonProps {
    slot: 'increment' | 'decrement'
    emblemType?: 'chevron' | 'default'
    className?: string
}

const StepperButton = ({
    slot,
    className,
    emblemType = 'default',
    ...props
}: StepperButtonProps) => {
    const icon =
        emblemType === 'chevron' ? (
            slot === 'increment' ? (
                <IconChevronUp className='size-5' />
            ) : (
                <IconChevronDown className='size-5' />
            )
        ) : slot === 'increment' ? (
            <IconPlus />
        ) : (
            <IconMinus />
        )
    return (
        <Button className={stepperButton({ className })} slot={slot} {...props}>
            {icon}
        </Button>
    )
}

export { NumberField }
export type { NumberFieldProps }

