        },
        isText: {
            true: 'size-auto px-1.5 text-xs has-[svg]:px-[3px] *:[svg]:size-4 *:[svg]:shrink-0'
        }
    },
    defaultVariants: {
        variant: 'primary',
        position: 'top',
        isInverse: false,
        shape: 'circle',
        size: 'sm'
    }
})

interface IndicatorProps extends ComponentPropsWithRef<'div'>, VariantProps<typeof indicatorStyle> {
