            <Button
                className='inline-flex size-7 shrink-0 items-center justify-center rounded-md border border-border/40 bg-background pressed:bg-muted/50 text-muted-foreground shadow-sm outline-hidden hover:bg-muted/40 focus-visible:ring-2 focus-visible:ring-ring'
                slot='previous'
            >
                {direction === 'rtl' ? <IconChevronRight /> : <IconChevronLeft />}
            </Button>
            {isRange ? (
                <Heading className='font-normal text-sm' />
            ) : (
                <div className='flex items-center'>
                    <SelectMonth state={state} />
                    <SelectYear state={state} />
                </div>
            )}
            <Button
                className='inline-flex size-7 shrink-0 items-center justify-center rounded-md border border-border/40 bg-background pressed:bg-muted/50 text-muted-foreground shadow-sm outline-hidden hover:bg-muted/40 focus-visible:ring-2 focus-visible:ring-ring'
                slot='next'
            >
                {direction === 'rtl' ? <IconChevronLeft /> : <IconChevronRight />}
            </Button>
        </header>
    )
}

const CalendarGridHeader = () => {
    return (
        <RACCalendarGridHeader>
            {(day) => (
                <CalendarHeaderCell className='pb-2 font-semibold text-muted-foreground text-sm sm:px-0 sm:py-0.5 lg:text-xs'>
                    {day}
                </CalendarHeaderCell>
            )}
        </RACCalendarGridHeader>
    )
}

const SelectMonth = ({ state }: { state: CalendarState }) => {
    const months = []

    const formatter = useDateFormatter({
        month: 'long',
        timeZone: state.timeZone
    })

    const numMonths = state.focusedDate.calendar.getMonthsInYear(state.focusedDate)
    for (let i = 1; i <= numMonths; i++) {
        const date = state.focusedDate.set({ month: i })
        months.push(formatter.format(date.toDate(state.timeZone)))
    }
    const selectedMonth = state.focusedDate.month

    return (
        <Menu aria-label='Select month'>
            <Menu.Trigger className='p-0.5 text-sm outline-primary' slot={null}>
                {months[selectedMonth - 1]}
            </Menu.Trigger>
            <Menu.Content
                items={months.map((month, i) => ({ id: i + 1, textValue: month }))}
                onSelectionChange={(v) => state.setFocusedDate(state.focusedDate.set({ month: Number([...v][0]) }))}
                selectedKeys={[selectedMonth]}
                selectionMode='single'
            >
                {(item) => (
                    <Menu.Item id={item.id} key={item.id} textValue={item.textValue}>
                        <Menu.Label>{item.textValue}</Menu.Label>
                    </Menu.Item>
                )}
            </Menu.Content>
        </Menu>
    )
}

const SelectYear = ({ state }: { state: CalendarState }) => {
    const years: { value: CalendarDate; formatted: string }[] = []
    const formatter = useDateFormatter({
        year: 'numeric',
        timeZone: state.timeZone
    })

    for (let i = -20; i <= 20; i++) {
        const date = state.focusedDate.add({ years: i })
        years.push({
            value: date,
            formatted: formatter.format(date.toDate(state.timeZone))
        })
    }

    const selectedYear = state.focusedDate.year

    return (
        <Menu>
            <Menu.Trigger className='p-0.5 text-sm outline-primary' slot={null}>
                {years[selectedYear - years[0].value.year].formatted}
            </Menu.Trigger>
            <Menu.Content
                aria-label='Select year'
                items={years.map((year, i) => ({ id: i, textValue: year.formatted }))}
                onSelectionChange={(value) => {
                    state.setFocusedDate(state.focusedDate.set({ year: years[Number([...value][0])].value.year }))
                }}
                selectedKeys={[20]}
                selectionMode='single'
            >
                {(item) => (
                    <Menu.Item id={item.id} key={item.id} textValue={item.textValue}>
                        <Menu.Label>{item.textValue}</Menu.Label>
                    </Menu.Item>
                )}
            </Menu.Content>
        </Menu>
    )
}

export { Calendar, RangeCalendar }

avatar.raw:
'use client'

import { Collection, type CollectionProps } from '@react-aria/collections'
import { IconUser } from '@tabler/icons-react'
import { type ComponentProps, useEffect, useState } from 'react'
import { tv, type VariantProps } from 'tailwind-variants'
import { cn } from '@/lib/utils'

interface AvatarGroupProps<T extends object> extends CollectionProps<T> {
    className?: string
}

const getInitials = (name: string): string =>
    name.split(' ').slice(0, 2).length > 1
        ? name
              .split(' ')
              .slice(0, 2)
              .map((part) => part.charAt(0))
              .join('')
        : name.split('').slice(0, 2).join('')

const AvatarGroup = <T extends object>({ className, ...props }: AvatarGroupProps<T>) => {
    return (
        <div
            className={cn(
                '-space-x-2 flex items-center justify-center **:data-avatar:ring **:data-avatar:ring-border',
                '**:data-avatar:transition-transform **:data-avatar:hover:z-30 **:data-avatar:hover:scale-110',
                className
            )}
        >
            <Collection {...props} />
        </div>
    )
}

const avatarStyle = tv({
    base: [
        'inline-grid shrink-0 items-center justify-center align-middle *:col-start-1 *:row-start-1',
        '-outline-offset-1 text-center outline-1 outline-foreground/20'
    ],
    variants: {
        shape: {
            square: 'rounded-lg *:rounded-lg',
            circle: 'rounded-full *:rounded-full'
        },
        size: {
            xs: 'size-6',
            sm: 'size-7',
            md: 'size-8',
            lg: 'size-9',
            xl: 'size-12',
            '2xl': 'size-14',
            '3xl': 'size-16',
            '4xl': 'size-20',
            '5xl': 'size-24'
        }
    },
    defaultVariants: {
        shape: 'circle',
        size: 'lg'
    }
})

interface AvatarProps extends VariantProps<typeof avatarStyle> {
    src?: string | undefined
    alt?: string
    className?: string
}

const Avatar = ({ src, shape, size, alt, className, ...props }: AvatarProps & ComponentProps<'img'>) => {
    const [error, setError] = useState(!src)

    function handleError() {
        setError(true)
    }

    useEffect(() => {
        setError(!src)
    }, [src])

    if (error) {
        return (
            <span className={avatarStyle({ shape, size, className })} data-avatar={true}>
                <FallbackImage alt={alt} />
            </span>
        )
    }
    return (
        <img
            alt={alt || 'Avatar'}
            className={avatarStyle({ shape, size, className })}
            data-avatar
            onError={handleError}
            src={src}
            {...props}
        />
    )
}

const FallbackImage = ({ alt }: { alt: string | undefined }) => {
    return alt ? (
        <svg
            aria-hidden='true'
            className='size-full select-none bg-background fill-current p-[5%] font-medium text-[48px] uppercase'
            viewBox='0 0 100 100'
        >
            {alt && <title>{alt}</title>}
            <text alignmentBaseline='middle' dominantBaseline='middle' dy='.125em' textAnchor='middle' x='50%' y='50%'>
                {getInitials(alt)}
            </text>
        </svg>
    ) : (
        <IconUser className='size-full place-self-center bg-background p-1.5' />
    )
}

export { Avatar, AvatarGroup }
export type { AvatarProps }

