    Group,
    Input,
    ListBox,
    Tag,
    TagGroup,
    TagList
} from 'react-aria-components'
import { cn, fuzzyMatch } from '@/lib/utils'
import { Description, FieldGroup, type FieldProps, Label } from './form'
import { ListBoxDetails, ListBoxItem, ListBoxSection } from './list-box'
import { PopoverContent } from './popover'

interface MultiSelectProps<T>
    extends ListBoxProps<T>,
        Pick<ComboBoxProps<T & { selectedKeys: Selection }>, 'isRequired' | 'validate' | 'validationBehavior'>,
        FieldProps,
        Pick<GroupProps, 'isDisabled' | 'isInvalid'> {
    className?: string
    errorMessage?: string
    maxItems?: number
    ref?: Ref<HTMLDivElement>
}

function mapToNewObject<T extends object>(array: T[]): { id: T[keyof T]; textValue: T[keyof T] }[] {
    return array.map((item) => {
        const idProperty = Object.keys(item).find((key) => key === 'id' || key === 'key')
        const textProperty = Object.keys(item).find((key) => key !== 'id' && key !== 'key')
        return {
            id: item[idProperty as keyof T],
            textValue: item[textProperty as keyof T]
        }
    })
}

const MultiSelect = <T extends object>({
    className,
    maxItems = Number.POSITIVE_INFINITY,
    children,
    ref,
    ...props
}: MultiSelectProps<T>) => {
    const triggerRef = useRef<HTMLDivElement>(null)
    const inputRef = useRef<HTMLInputElement>(null)
    const triggerButtonRef = useRef<HTMLButtonElement>(null)
    const popoverRef = useRef<HTMLDivElement>(null)
    const [inputValue, setInputValue] = useState('')
    const [selectedKeys, onSelectionChange] = useState<Selection>(new Set(props.selectedKeys))

    const isMax = [...selectedKeys].length >= maxItems

    useEffect(() => {
        setInputValue('')
        return () => {
            inputRef.current?.focus()
        }
    }, [props?.selectedKeys, selectedKeys])

    const addItem = (e: Key | null) => {
        if (!e || isMax) return
        onSelectionChange?.((s) => new Set([...s, e!]))
        // @ts-expect-error incompatible type Key and Selection
        props.onSelectionChange?.((s) => new Set([...s, e!]))
    }

    const removeItem = (e: Set<Key>) => {
        onSelectionChange?.((s) => new Set([...s].filter((i) => i !== e.values().next().value)))
        props.onSelectionChange?.(
            // @ts-expect-error incompatible type Key and Selection
            (s) => new Set([...s].filter((i) => i !== e.values().next().value))
        )
    }

    const onKeyDownCapture = (e: KeyboardEvent<HTMLInputElement>) => {
        if (e.key === 'Backspace' && inputValue === '') {
            onSelectionChange?.((s) => new Set([...s].slice(0, -1)))
            // @ts-expect-error incompatible type Key and Selection
            props.onSelectionChange?.((s) => new Set([...s].slice(0, -1)))
        }
    }

    const parsedItems = props.items
        ? mapToNewObject(props.items as T[])
        : mapToNewObject(Children.map(children as ReactNode, (child) => isValidElement(child) && child.props) as T[])

    const availableItemsToSelect = props.items
        ? parsedItems.filter((item) => ![...selectedKeys].includes(item.id as Key))
        : parsedItems

    const filteredChildren = props.items
        ? parsedItems.filter((item) => ![...selectedKeys].includes(item.id as Key))
        : Children.map(children as ReactNode, (child) => isValidElement(child) && child.props)?.filter(
              (item: T & any) => ![...selectedKeys].includes(item.id)
          )
    return (
        <Group
            className={composeRenderProps(className, (className) =>
                cn('group/field flex h-fit flex-col gap-y-1.5', className)
            )}
            ref={ref}
        >
            {props.label && <Label onClick={() => inputRef.current?.focus()}>{props.label}</Label>}
            <FieldGroup
                className={cn(
                    'flex h-fit min-h-9 flex-wrap items-center py-1 **:[input]:py-1',
                    'group-has-invalid/field:group-has-focus/field:border-destructive group-has-invalid/field:group-has-open/field:border-destructive group-has-invalid/field:group-has-open/field:ring-invalid group-has-invalid/field:hover:border-destructive/70'
                )}
                isDisabled={props.isDisabled}
                isInvalid={props.isInvalid || !!props.errorMessage}
                ref={triggerRef}
            >
                {({ isInvalid, isDisabled }) => (
                    <>
                        <TagGroup aria-hidden aria-label='selected-items' onRemove={removeItem}>
                            <TagList
                                className='flex flex-1 flex-wrap gap-1 pl-2 empty:pl-0'
                                items={[...selectedKeys].map((key) => ({
                                    id: key,
                                    textValue: parsedItems.find((item) => item.id === key)?.textValue as string
                                }))}
                            >
                                {(item: { id: Key; textValue: Key }) => (
                                    <Tag
                                        className={({ isFocusVisible }) =>
                                            cn(
                                                'inline-flex items-center justify-between gap-1 rounded-md border px-2 py-0.5 text-sm outline-hidden',
                                                isInvalid
                                                    ? 'border-destructive/70 bg-destructive/10 text-destructive'
                                                    : 'border-primary/70 bg-primary/10 text-primary',
                                                isFocusVisible &&
                                                    `ring-2 ${isInvalid ? 'ring-destructive/70' : 'ring-primary/70'}`
                                            )
                                        }
                                        isDisabled={isDisabled}
                                        textValue={item.textValue as string}
                                    >
                                        {item.textValue as string}
                                        <Button
                                            className='-mr-1 flex size-4 cursor-pointer items-center justify-center rounded-md pressed:bg-primary/70 pressed:text-primary-foreground outline-hidden hover:bg-primary hover:text-primary-foreground'
                                            slot='remove'
                                        >
                                            <IconX className='size-3 shrink-0' />
                                        </Button>
                                    </Tag>
                                )}
                            </TagList>
                        </TagGroup>
                        <ComboBox
                            aria-label='Search'
                            className='flex-1 text-sm/5'
                            defaultFilter={fuzzyMatch}
                            inputValue={inputValue}
                            isDisabled={isDisabled}
                            isInvalid={isInvalid}
                            isReadOnly={isMax}
                            isRequired={props.isRequired}
                            onInputChange={isMax ? () => {} : setInputValue}
                            onSelectionChange={addItem}
                            validate={props.validate}
                            validationBehavior={props.validationBehavior}
                        >
                            <div className='flex flex-row items-center'>
                                <Input
                                    className='w-full text-sm/7 outline-hidden'
                                    onFocus={() => triggerButtonRef.current?.click()}
                                    onKeyDownCapture={onKeyDownCapture}
                                    placeholder={
                                        isMax
                                            ? 'Max items reached'
                                            : !availableItemsToSelect || !filteredChildren?.length
                                              ? ''
                                              : 'Pick some items'
                                    }
                                    ref={inputRef}
                                />
                                <Button
                                    aria-label='Chevron'
                                    className='mr-2 ml-auto inline-flex w-auto flex-1 items-center justify-center rounded-md text-muted-foreground outline-hidden'
                                    ref={triggerButtonRef}
                                >
                                    <IconChevronDown className='group-has-open/field:-rotate-180 size-4 transition' />
                                </Button>
                            </div>
                            <PopoverContent
                                isPicker
                                ref={popoverRef}
                                respectScreen={false}
                                showArrow={false}
                                style={{
                                    minWidth: triggerRef.current?.offsetWidth,
                                    width: triggerRef.current?.offsetWidth
                                }}
                                trigger='focus'
                                triggerRef={triggerRef}
                            >
                                <ListBox
                                    className='grid w-full grid-cols-[auto_1fr_1.5rem_0.5rem_auto] gap-y-1 overflow-y-auto rounded-md outline-hidden'
                                    items={(availableItemsToSelect as T[]) ?? props.items}
                                    renderEmptyState={() => <div>No Items</div>}
                                    selectionMode='multiple'
                                    {...props}
                                >
                                    {filteredChildren?.map((item: any) => (
                                        <MultiSelect.Item
                                            id={item.id as Key}
                                            key={item.id as Key}
                                            textValue={item.textValue as string}
                                        >
                                            {item.textValue as string}
                                        </MultiSelect.Item>
                                    )) ?? children}
                                </ListBox>
                            </PopoverContent>
                        </ComboBox>
                    </>
                )}
            </FieldGroup>
            {props.description && <Description>{props.description}</Description>}
            {props.errorMessage && (
                <Description className='text-destructive text-sm/5'>{props.errorMessage}</Description>
            )}
        </Group>
    )
}

MultiSelect.Item = ListBoxItem
MultiSelect.Section = ListBoxSection
MultiSelect.ItemDetails = ListBoxDetails

export { MultiSelect }

meter.raw:
'use client'

import { IconAlertCircle } from '@tabler/icons-react'
import { Meter as RACMeter, type MeterProps as RACMeterProps } from 'react-aria-components'
import { cn } from '@/lib/utils'
import { Label } from './form'

interface MeterProps extends RACMeterProps {
    label?: string
}

const Meter = ({ label, className, ...props }: MeterProps) => {
    return (
        <RACMeter
            aria-label={props['aria-label'] ?? 'Meter'}
            {...props}
            className={cn('flex flex-col gap-y-1.5', className)}
        >
            {({ percentage, valueText }) => (
                <>
                    <div className='flex w-full justify-between gap-2'>
                        {label && <Label>{label}</Label>}
                        <span
                            className={`flex items-center text-sm tabular-nums ${percentage >= 80 ? 'text-destructive' : 'text-muted-foreground'}`}
                        >
                            {percentage >= 80 && (
                                <IconAlertCircle
                                    aria-label='Alert'
                                    className='mr-1 inline size-4 fill-invalid text-destructive'
                                />
                            )}
                            {valueText}
                        </span>
                    </div>
                    <div className='relative h-2 min-w-64 rounded-lg bg-muted outline-hidden'>
                        <div
                            className='absolute top-0 left-0 h-full rounded-full transition-[width,background-color]'
                            style={{
                                width: `${percentage}%`,
                                backgroundColor: getColor(percentage)
                            }}
                        />
                    </div>
                </>
            )}
        </RACMeter>
    )
}

const getColor = (percentage: number) => {
    if (percentage < 25) {
        return 'blue'
    }
    if (percentage < 50) {
        return 'green'
    }
    if (percentage < 75) {
        return 'orange'
    }
    return 'var(--destructive)'
}

export { Meter }

text-field.raw:
'use client'

import type { FieldProps } from './form'
import { IconEye, IconEyeClosed, IconLoader3 } from '@tabler/icons-react'
import { type ReactNode, type Ref, useState } from 'react'
import {
    Button,
    composeRenderProps,
    Input,
    TextField as RACTextField,
    type TextFieldProps as RACTextFieldProps
} from 'react-aria-components'
import { cn } from '@/lib/utils'
import { Description, FieldError, FieldGroup, Label } from './form'

interface TextFieldProps extends RACTextFieldProps, FieldProps {
    prefix?: ReactNode
    suffix?: ReactNode
    isPending?: boolean
    placeholder?: string
    ref?: Ref<HTMLInputElement>
}

const TextField = ({
    label,
    placeholder,
    description,
    errorMessage,
    className,
    type,
    ref,
    ...props
}: TextFieldProps) => {
    const [masked, setMasked] = useState<boolean>(type === 'password')
    const inputType = type === 'password' ? (masked ? 'password' : 'text') : type

    return (
        <RACTextField
            className={composeRenderProps(className, (className) =>
                cn('group/field flex flex-col gap-y-1.5', className)
            )}
            ref={ref}
            type={inputType}
            {...props}
        >
            {(values) => (
                <>
                    {label && <Label>{label}</Label>}
                    <FieldGroup
                        data-loading={props.isPending ? 'true' : undefined}
                        isDisabled={values.isDisabled}
                        isInvalid={!!errorMessage || values.isInvalid}
                    >
                        {props.prefix ? (
                            <span className='ml-2 text-muted-foreground has-[button]:ml-0' data-prefix>
                                {props.prefix}
                            </span>
                        ) : null}
                        <Input placeholder={placeholder} />
                        {type === 'password' ? (
                            <Button
                                aria-label='Toggle visibility'
                                className='mr-2 inline-flex items-center justify-center rounded-md p-0.5 text-muted-foreground focus-visible:text-primary focus-visible:outline-2 focus-visible:outline-ring'
                                onPress={() => setMasked((e) => !e)}
                                type='button'
                            >
                                <IconEye
                                    aria-hidden
                                    className={cn('size-4 scale-0 transition-transform', masked && 'scale-100')}
                                />
                                <IconEyeClosed
                                    aria-hidden
                                    className={cn(
                                        'absolute size-4 scale-0 transition-transform',
                                        !masked && 'scale-100'
                                    )}
                                />
                            </Button>
                        ) : props.isPending ? (
                            <IconLoader3 className='mr-2 size-3.5 animate-spin text-muted-foreground' data-suffix />
                        ) : props.suffix ? (
                            <span className='mr-2 text-muted-foreground has-[button]:mr-0' data-suffix>
                                {props.suffix}
                            </span>
                        ) : null}
                    </FieldGroup>
                    {description && <Description>{description}</Description>}
                    <FieldError>{errorMessage}</FieldError>
                </>
            )}
        </RACTextField>
    )
}

export { TextField }

navbar.raw:
'use client'

import type { ButtonProps, LinkProps } from 'react-aria-components'
import { IconMenu } from '@tabler/icons-react'
import { type ComponentPropsWithRef, createContext, type Ref, use, useCallback, useMemo, useState } from 'react'
import { composeRenderProps, Link } from 'react-aria-components'
import { useIsMobile } from '@/lib/hooks'
import { cn } from '@/lib/utils'
import { Button } from './button'
import { Sheet } from './sheet'

const HEIGHT = '3.5rem'

type NavbarOptions = {
    isSticky?: boolean
    variant?: 'default' | 'float' | 'inset'
}

type NavbarContextProps = {
    open: boolean
    setOpen: (open: boolean) => void
    isCompact: boolean
    toggleNavbar: () => void
} & NavbarOptions

const NavbarContext = createContext<NavbarContextProps | null>(null)

function useNavbar() {
    const context = use(NavbarContext)
    if (!context) {
        throw new Error('useNavbar must be used within a Navbar.')
    }

    return context
}

interface NavbarProps extends ComponentPropsWithRef<'header'>, NavbarOptions {
    defaultOpen?: boolean
    isOpen?: boolean
    onOpenChange?: (open: boolean) => void
}

const Navbar = ({
    children,
    isOpen: openProp,
    onOpenChange: setOpenProp,
    defaultOpen = false,
    className,
    isSticky = false,
    variant = 'default',
    ...props
}: NavbarProps) => {
    const isCompact = useIsMobile()
    const [_open, _setOpen] = useState(defaultOpen)
    const open = openProp ?? _open

    const setOpen = useCallback(
        (value: boolean | ((value: boolean) => boolean)) => {
            if (setOpenProp) {
                return setOpenProp?.(typeof value === 'function' ? value(open) : value)
            }

            _setOpen(value)
        },
        [setOpenProp, open]
    )

    const toggleNavbar = useCallback(() => {
        setOpen((open) => !open)
    }, [setOpen])

    const contextValue = useMemo<NavbarContextProps>(
        () => ({
            open,
            setOpen,
            isCompact,
            toggleNavbar,
            variant,
            isSticky
        }),
        [open, setOpen, isCompact, toggleNavbar, variant, isSticky]
    )
    return (
        <NavbarContext value={contextValue}>
            <header
                className={cn(
                    'group/navbar [--navbar-breadcrumbs-height:0px] has-data-navbar-breadcrumbs:[--navbar-breadcrumbs-height:3rem]',
                    'relative isolate flex w-full flex-col',
                    isSticky && 'sticky top-0 z-50',
                    variant === 'float' && 'px-2.5 pt-2',
                    variant === 'inset' && 'min-h-dvh bg-sidebar',
                    className
                )}
                data-navbar-variant={variant}
                style={{
                    // @ts-ignore
                    '--navbar-height': HEIGHT
                }}
                {...props}
            >
                {children}
            </header>
        </NavbarContext>
    )
}

interface NavbarNavProps extends ComponentPropsWithRef<'div'> {
    variant?: 'navbar' | 'float' | 'inset'
    isSticky?: boolean
    useDefaultResponsive?: boolean
}

const NavbarNav = ({ useDefaultResponsive = true, className, ref, children, ...props }: NavbarNavProps) => {
    const { isCompact, variant, open, setOpen } = useNavbar()

    if (isCompact && useDefaultResponsive) {
        return (
            <Sheet isOpen={open} onOpenChange={setOpen} {...props}>
                <Sheet.Trigger className='sr-only' />
                <Sheet.Content aria-label='Compact Navbar' data-navbar='compact' side='left'>
                    <Sheet.Body className='px-2 md:px-4'>{children}</Sheet.Body>
                </Sheet.Content>
            </Sheet>
        )
    }

    return (
        <div
            className={cn(
                'group peer hidden w-full items-center px-4 md:flex md:px-0',
                'h-(--navbar-height)',
                '[&>div]:mx-auto [&>div]:w-full [&>div]:max-w-7xl [&>div]:items-center md:[&>div]:flex lg:[&>div]:max-w-(--breakpoint-xl) 2xl:[&>div]:max-w-(--breakpoint-2xl)',
                variant === 'float' &&
                    'mx-auto w-full max-w-7xl rounded-lg border border-sidebar-border bg-sidebar px-4 text-sidebar-foreground md:px-6 lg:max-w-(--breakpoint-xl) lg:px-8 2xl:max-w-(--breakpoint-2xl)',
                variant === 'default' &&
                    'border-sidebar-border border-b bg-sidebar text-sidebar-foreground sm:[&>div]:px-6 lg:[&>div]:px-8',
                variant === 'inset' &&
                    'mx-auto md:px-6 [&>div]:mx-auto [&>div]:w-full [&>div]:items-center md:[&>div]:flex',
                className
            )}
            data-navbar-nav='true'
            ref={ref}
            {...props}
        >
            <div>{children}</div>
        </div>
    )
}

interface NavbarTriggerProps extends ButtonProps {
    ref?: Ref<HTMLButtonElement>
}

const NavbarTrigger = ({ className, onPress, ref, ...props }: NavbarTriggerProps) => {
    const { toggleNavbar } = useNavbar()
    return (
        <Button
            aria-label={props['aria-label'] || 'Toggle Navbar'}
            className={className}
            data-navbar-trigger='true'
            icon
            onPress={(event) => {
                onPress?.(event)
                toggleNavbar()
            }}
            ref={ref}
            variant='ghost'
            {...props}
        >
            <IconMenu />
            <span className='sr-only'>Toggle Navbar</span>
        </Button>
    )
}

const NavbarSection = ({ className, ...props }: ComponentPropsWithRef<'div'>) => {
    const { isCompact } = useNavbar()
    return (
        <div
            className={cn('flex', isCompact ? 'flex-col gap-y-1' : 'flex-row items-center gap-x-3', className)}
            data-navbar-section
            {...props}
        >
            {'title' in props && <h4 className='-mx-2 my-4 px-5 font-medium text-sm md:hidden'>{props.title}</h4>}
            {props.children}
        </div>
    )
}

interface NavbarItemProps extends LinkProps {
    isCurrent?: boolean
}

const NavbarItem = ({ className, isCurrent, ...props }: NavbarItemProps) => (
    <Link
        aria-current={isCurrent ? 'page' : undefined}
        className={composeRenderProps(className, (className) =>
            cn(
                'relative flex cursor-pointer items-center gap-x-2 rounded-md px-3 py-2 text-muted-foreground no-underline outline-hidden transition-colors md:text-sm',
                '**:data-[slot=chevron]:size-4 **:data-[slot=chevron]:transition-transform',
                '*:[svg]:-mx-0.5 *:[svg]:size-4 *:[svg]:shrink-0',
                'pressed:text-sidebar-primary hover:text-sidebar-primary focus:text-sidebar-primary focus-visible:ring-2 focus-visible:ring-sidebar-ring/50 disabled:cursor-default disabled:opacity-50 pressed:**:data-[slot=chevron]:rotate-180',
                isCurrent && 'pointer-events-none cursor-default bg-sidebar-accent text-sidebar-accent-foreground',
                className
            )
        )}
        data-navbar-item
        {...props}
    />
)

const NavbarLogo = ({ className, ...props }: LinkProps) => {
    return (
        <Link
            className={composeRenderProps(className, (className) =>
                cn(
                    'relative flex items-center gap-x-2 px-2 py-4 text-sidebar-foreground focus:outline-hidden focus-visible:outline-1 focus-visible:outline-sidebar-ring md:mr-4 md:px-0 md:py-0',
                    className
                )
            )}
            {...props}
        />
    )
}

const NavbarFlex = ({ className, ref, ...props }: ComponentPropsWithRef<'div'>) => {
    return <div className={cn('flex items-center gap-2', className)} ref={ref} {...props} />
}

interface NavbarCompactProps extends ComponentPropsWithRef<'div'> {
    variant?: 'float' | 'inset' | 'navbar'
}

const NavbarCompact = ({ className, ref, ...props }: NavbarCompactProps) => {
    const { variant } = useNavbar()
    return (
        <div
            className={cn(
                'flex justify-between bg-sidebar text-sidebar-foreground peer-has-[[data-navbar-variant=float]]:border peer-has-[[data-navbar-variant=float]]:border-sidebar-border md:hidden',
                variant === 'float' && 'h-12 rounded-lg border px-3.5',
                variant === 'inset' && 'h-14 border-b px-4',
                variant === 'default' && 'h-14 border-b px-4',
                className
            )}
            ref={ref}
            {...props}
        />
    )
}

const NavbarBreadcrumbs = ({ className, ref, children, ...props }: ComponentPropsWithRef<'div'>) => {
    const { variant } = useNavbar()
    return (
        <div
            className={cn(
                'flex h-(--navbar-breadcrumbs-height) items-center',
                variant === 'default' &&
                    'w-full border-sidebar-border border-b bg-sidebar text-sidebar-foreground *:max-w-7xl *:px-4 md:*:px-6 lg:*:max-w-(--breakpoint-xl) 2xl:*:max-w-(--breakpoint-2xl)',
                variant === 'inset' &&
                    'w-full rounded-lg rounded-b-none border-sidebar-border border-b px-4 md:mx-auto md:max-w-[calc(100vw-16px)] md:border md:px-6',
                variant === 'float' &&
                    'mx-auto w-full max-w-7xl rounded-lg bg-background px-2 text-foreground md:px-4 lg:max-w-(--breakpoint-xl) 2xl:max-w-(--breakpoint-2xl)',
                className
            )}
            data-navbar-breadcrumbs={true}
            ref={ref}
            {...props}
        >
            <div className='mx-auto w-full'>{children}</div>
        </div>
    )
}

const NavbarInset = ({ className, ref, ...props }: ComponentPropsWithRef<'div'>) => {
    const { variant } = useNavbar()
    return (
        <div
            className={cn(
                'relative flex w-full flex-col overflow-auto',
                variant === 'default' &&
                    'max-h-[calc(100vh-var(--navbar-height,0px)-var(--navbar-breadcrumbs-height,0px)-8px)]',
                variant === 'inset' &&
                    'max-h-[calc(100vh-var(--navbar-height,0px)-var(--navbar-breadcrumbs-height,0px))] overflow-auto bg-background pb-2 md:px-2',
                variant === 'float' &&
                    'h-[calc(100vh-var(--navbar-height,0px)-var(--navbar-breadcrumbs-height,0px)-8px)] bg-background pb-2 md:px-0',
                className
            )}
            ref={ref}
        >
            <main
                className={cn(
                    'mx-auto flex size-full flex-1 grow flex-col',
                    variant === 'inset' &&
                        'max-h-fit overflow-auto border-sidebar-border bg-background px-4 shadow-sm md:rounded-lg md:border md:group-has-data-navbar-breadcrumbs/navbar:rounded-t-none md:group-has-data-navbar-breadcrumbs/navbar:border-t-0',
                    variant === 'default' && 'overflow-auto',
                    variant === 'float' && 'max-w-7xl lg:max-w-(--breakpoint-xl) 2xl:max-w-(--breakpoint-2xl)'
                )}
            >
                {props.children}
            </main>
        </div>
    )
}

Navbar.Nav = NavbarNav
Navbar.Inset = NavbarInset
Navbar.Compact = NavbarCompact
Navbar.Flex = NavbarFlex
Navbar.Trigger = NavbarTrigger
Navbar.Logo = NavbarLogo
Navbar.Item = NavbarItem
Navbar.Breadcrumbs = NavbarBreadcrumbs
Navbar.Section = NavbarSection

export { Navbar, NavbarBreadcrumbs, NavbarInset }

note.raw:
