export { Note }

select.raw:
'use client'
import type { ReactNode, Ref } from 'react'
import type { SelectProps as RACSelectProps } from 'react-aria-components'
import { IconChevronDown, IconLoader, IconSearch, IconX } from '@tabler/icons-react'
import {
    Autocomplete,
    Button,
    composeRenderProps,
    Group,
    Input,
    ListBox,
    Select as RACSelect,
    SearchField,
    SelectValue
} from 'react-aria-components'
import { cn, fuzzyMatch } from '@/lib/utils'
import { Description, FieldError, type FieldProps, fieldGroupStyle, Label } from './form'
import { ListBoxDetails, ListBoxItem, ListBoxLabel, ListBoxSection } from './list-box'
import { PopoverContent } from './popover'

interface SelectProps<T extends object> extends Omit<RACSelectProps<T>, 'children'>, FieldProps {
    className?: string
    items?: Iterable<T>
    children: ReactNode | ((item: T) => ReactNode)
    prefix?: ReactNode
    searchable?: boolean
    isPending?: boolean
    ref?: Ref<HTMLDivElement>
}

const Select = <T extends object>({
    label,
    description,
    errorMessage,
    children,
    items,
    searchable = false,
    className,
    ref,
    ...props
}: SelectProps<T>) => {
    const renderOptions = (
        <ListBox
            aria-label='items'
            className='grid w-full grid-cols-[auto_1fr_1.5rem_0.5rem_auto] gap-y-1 overflow-y-auto rounded-md outline-hidden'
            items={items}
            renderEmptyState={() => (
                <div className='col-span-full p-4 text-center text-muted-foreground'>No results found</div>
            )}
        >
            {children}
        </ListBox>
    )

    return (
        <RACSelect
            className={composeRenderProps(className, (className) =>
                cn('group/field flex flex-col gap-y-1.5', className)
            )}
            ref={ref}
            {...props}
        >
            {label && <Label>{label}</Label>}
            <Button
                className={fieldGroupStyle({
                    className:
                        'w-full gap-x-2 p-2 text-base outline-hidden! **:data-placeholder:text-muted-foreground sm:text-sm'
                })}
            >
                {props.prefix ? (
                    <span className='ml-2 text-muted-foreground has-[button]:ml-0'>{props.prefix}</span>
                ) : null}
                <SelectValue className='**:data-avatar:-mx-0.5 grid grid-cols-[auto_1fr] items-center text-base **:data-avatar:mr-2 **:data-[slot=description]:hidden **:data-avatar:size-6 sm:text-sm **:[svg]:mr-2' />
                <IconChevronDown
                    className='group-open/field:-rotate-180 ml-auto size-4 text-muted-foreground transition'
                    data-slot='chevron'
                />
            </Button>
            {description && <Description>{description}</Description>}
            <FieldError>{errorMessage}</FieldError>
            <PopoverContent isPicker respectScreen={false} showArrow={false} trigger='focus'>
                {searchable ? (
                    <Autocomplete filter={fuzzyMatch}>
                        <SearchField aria-label='Search' autoFocus className='-mx-1 -mt-1 mb-1 border-b'>
                            {({ isEmpty }) => (
                                <Group className='flex items-center px-2'>
                                    {props.isPending ? (
                                        <IconLoader className='size-4 shrink-0 animate-spin text-muted-foreground' />
                                    ) : (
                                        <IconSearch className='size-4 shrink-0 text-muted-foreground' />
                                    )}
                                    <Input
                                        className='w-full p-2 text-sm outline-hidden [&::-webkit-search-cancel-button]:hidden'
                                        placeholder='Search...'
                                    />
                                    {!isEmpty && (
                                        <Button
                                            aria-label='Clear'
                                            className='mr-2 inline-flex items-center justify-center rounded-md text-muted-foreground outline-offset-4'
                                            type='button'
                                        >
                                            <IconX aria-hidden />
                                        </Button>
                                    )}
                                </Group>
                            )}
                        </SearchField>
                        {renderOptions}
                    </Autocomplete>
                ) : (
                    renderOptions
                )}
            </PopoverContent>
        </RACSelect>
    )
}

Select.Item = ListBoxItem
Select.Details = ListBoxDetails
Select.Section = ListBoxSection
Select.Label = ListBoxLabel

export { Select }

tabs.raw:
'use client'

import type { Ref } from 'react'
import type {
    TabListProps as RACTabListProps,
    TabPanelProps as RACTabPanelProps,
    TabProps as RACTabProps,
    TabsProps as RACTabsProps
} from 'react-aria-components'
import { composeRenderProps, Tab as RACTab, Tabs as RACTabs, TabList, TabPanel } from 'react-aria-components'
import { cn } from '@/lib/utils'

interface TabsProps extends RACTabsProps {
    ref?: Ref<HTMLDivElement>
}

const Tabs = ({ className, ref, ...props }: TabsProps) => {
    return (
        <RACTabs
            className={composeRenderProps(className, (className) =>
                cn(
                    'group/tabs grid orientation-horizontal:grid-cols-1 orientation-vertical:grid-cols-[auto_1fr] gap-2',
                    className
                )
            )}
            ref={ref}
            {...props}
        />
    )
}

interface TabListProps<T extends object> extends RACTabListProps<T> {
    ref?: Ref<HTMLDivElement>
}

const List = <T extends object>({ className, ref, ...props }: TabListProps<T>) => {
    return (
        <TabList
            ref={ref}
            {...props}
            className={composeRenderProps(className, (className, { orientation }) =>
                cn(
                    'no-scrollbar inline-flex h-9 items-center justify-center rounded-lg bg-muted p-[3px] text-muted-foreground',
                    orientation === 'horizontal' ? 'max-w-full items-center overflow-x-auto' : 'grid h-fit items-start',
                    className
                )
            )}
        />
    )
}

interface TabProps extends RACTabProps {
    ref?: Ref<HTMLDivElement>
}

const Tab = ({ className, ref, ...props }: TabProps) => {
    return (
        <RACTab
            ref={ref}
            {...props}
            className={composeRenderProps(className, (className) =>
                cn(
                    'inline-flex h-[calc(100%-1px)] flex-1 select-none items-center justify-center gap-1.5 whitespace-nowrap rounded-md border border-transparent px-2 py-1 font-medium text-sm transition-[color,box-shadow]',
                    'text-foreground dark:text-muted-foreground',
                    "[&_svg:not([class*='size-'])]:size-4 [&_svg]:pointer-events-none [&_svg]:shrink-0",
                    'selected:bg-background selected:shadow-sm',
                    'dark:selected:border-input dark:selected:bg-input/30 dark:selected:text-foreground',
                    'focus-visible:border-ring focus-visible:outline-1 focus-visible:outline-ring focus-visible:ring-[3px] focus-visible:ring-ring/50',
                    'disabled:pointer-events-none disabled:opacity-50',
                    className
                )
            )}
        />
    )
}

interface TabPanelProps extends RACTabPanelProps {
    ref?: Ref<HTMLDivElement>
}

const Panel = ({ className, ref, ...props }: TabPanelProps) => {
    return <TabPanel {...props} className={cn(className, 'flex-1 outline-hidden')} ref={ref} />
}

Tabs.List = List
Tabs.Label = Tab
Tabs.Content = Panel

export { Tabs }

disclosure.raw:
'use client'

import { IconChevronDown } from '@tabler/icons-react'
import { type Ref, use, useRef } from 'react'
import {
    Button,
    type ButtonProps,
    composeRenderProps,
    type DisclosurePanelProps,
    DisclosureStateContext,
    Disclosure as RACDisclosure,
    DisclosureGroup as RACDisclosureGroup,
    type DisclosureGroupProps as RACDisclosureGroupProps,
    DisclosurePanel as RACDisclosurePanel,
    type DisclosureProps as RACDisclosureProps
} from 'react-aria-components'
import { cn } from '@/lib/utils'

interface AccordionProps extends RACDisclosureGroupProps {
    ref?: Ref<HTMLDivElement>
    hideIndicator?: boolean
}

const DisclosureGroup = ({ children, ref, className, ...props }: AccordionProps) => {
    return (
        <RACDisclosureGroup
            data-slot='disclosure-group'
            ref={ref}
            {...props}
            className={composeRenderProps(className, (className, { isDisabled }) =>
                cn(
                    'space-y-1',
                    props.hideIndicator && '**:data-[slot=indicator]:text-transparent',
                    isDisabled ? 'cursor-default opacity-50' : 'cursor-pointer',
                    className
                )
            )}
        >
            {(values) => <>{typeof children === 'function' ? children(values) : children}</>}
        </RACDisclosureGroup>
    )
}

interface CollapsibleProps extends RACDisclosureProps {
    ref?: Ref<HTMLDivElement>
}

const Disclosure = ({ className, ref, children, ...props }: CollapsibleProps) => {
    return (
        <RACDisclosure
            data-slot='disclosure'
            ref={ref}
            {...props}
            className={composeRenderProps(className, (className, { isDisabled }) =>
                cn(
                    'group w-full border-b last:border-b-0',
                    isDisabled ? 'cursor-default opacity-50' : 'cursor-pointer',
                    className
                )
            )}
        >
            {(values) => (typeof children === 'function' ? children(values) : children)}
        </RACDisclosure>
    )
}

interface DisclosureTriggerProps extends ButtonProps {
    ref?: Ref<HTMLButtonElement>
}

const DisclosureTrigger = ({ className, children, ref, ...props }: DisclosureTriggerProps) => {
    return (
        <Button
            className={composeRenderProps(className, (className) =>
                cn(
                    'flex w-full flex-1 items-start justify-between gap-4 rounded-md py-4 text-left font-medium text-sm outline-none transition-all',
                    'aria-expanded:**:data-[slot=indicator]:rotate-180 **:[svg]:size-4 **:[svg]:shrink-0',
                    'focus-visible:border-primary focus-visible:ring-4 focus-visible:ring-ring',
                    'disabled:cursor-default disabled:opacity-50',
                    className
                )
            )}
            ref={ref}
            slot='trigger'
            {...props}
        >
            {(values) => (
                <>
                    {typeof children === 'function' ? children(values) : children}
                    <IconChevronDown className='text-muted-foreground transition-transform' data-slot='indicator' />
                </>
            )}
        </Button>
    )
}
const DisclosurePanel = ({ className, children, ...props }: DisclosurePanelProps) => {
    const { isExpanded } = use(DisclosureStateContext)!
    const contentRef = useRef<HTMLDivElement>(null)
    return (
        <RACDisclosurePanel
            className={cn('overflow-hidden text-sm')}
            data-slot='disclosure-content'
            style={{
                height: isExpanded ? contentRef?.current?.scrollHeight : 0,
                transition: 'height 0.2s ease-in-out'
            }}
            {...props}
        >
            <div className={cn('pt-0 pb-4', className)} ref={contentRef}>
                {children}
            </div>
        </RACDisclosurePanel>
    )
}
const Accordion = (props: AccordionProps) => <DisclosureGroup {...props} />
Accordion.Item = Disclosure
Accordion.Trigger = DisclosureTrigger
Accordion.Content = DisclosurePanel

const Collapsible = (props: CollapsibleProps) => <Disclosure {...props} />
Collapsible.Trigger = DisclosureTrigger
Collapsible.Content = DisclosurePanel

export { Accordion, Collapsible }

