import React from 'react'

import { IconCamera, IconMic, IconPaperclip, IconSend } from 'hq-icons'

import { Button, Form } from '@/components/ui'

interface MessageFormProps {
    value: string
    onChange: (value: string) => void
    onSend: (e: React.FormEvent<HTMLFormElement>) => void
}
export default function MessageForm({ value, onChange, onSend }: MessageFormProps) {
    const editorRef = React.useRef<HTMLDivElement>(null)
    React.useEffect(() => {
        const editor = editorRef.current

        const handleInput = () => {
            if (editor) {
                const text = editor.innerText
                onChange(text)
                editor.innerHTML = formatText(text)
                placeCaretAtEnd(editor)
            }
        }

        const placeCaretAtEnd = (el: HTMLElement) => {
            el.focus()
            const range = document.createRange()
            range.selectNodeContents(el)
            range.collapse(false)
            const sel = window.getSelection()
            sel?.removeAllRanges()
            sel?.addRange(range)
        }

        editor?.addEventListener('input', handleInput)

        return () => {
            editor?.removeEventListener('input', handleInput)
        }
    }, [onChange])

    function onSubmit(e: React.FormEvent<HTMLFormElement>) {
        e.preventDefault()
        onSend(e)
        if (editorRef.current) {
            editorRef.current.innerHTML = ''
        }
    }
    return (
        <Form
            onSubmit={onSubmit}
            className='bg-bg relative overflow-hidden rounded-lg border-t md:border'
        >
            <div
                contentEditable
                ref={editorRef}
                onKeyDown={(e: React.KeyboardEvent) => {
                    if (e.key === 'Enter' && !e.shiftKey && value.trim() !== '') {
                        onSubmit(e as unknown as React.FormEvent<HTMLFormElement>)
                    }
                }}
                aria-label='Message'
                id='message'
                className='no-scrollbar min-h-32 w-full resize-none border-0 p-3 outline-none'
            />
            <div className='flex items-center p-2'>
                <Button variant='ghost' size='icon' type='button'>
                    <IconCamera className='size-4' />
                    <span className='sr-only'>Upload Image</span>
                </Button>
                <Button variant='ghost' size='icon' type='button'>
                    <IconMic className='size-4' />
                    <span className='sr-only'>Use Microphone</span>
                </Button>
                <Button variant='ghost' size='icon' type='button'>
                    <IconPaperclip className='size-4' />
                    <span className='sr-only'>Attach File</span>
                </Button>
                <Button
                    isDisabled={value.trim() === ''}
                    type='submit'
                    size='sm'
                    className='ml-auto gap-1.5'
                >
                    Send Message
                    <IconSend />
                </Button>
            </div>
        </Form>
    )
}

const formatText = (text: string) => {
    return text
        .replace(/_(.*?)_/g, '<em>_$1_</em>')
        .replace(/_/g, '<span class="text-muted-fg">_</span>')
        .replace(/\*(.*?)\*/g, '<strong>*$1*</strong>')
        .replace(/\*/g, '<span class="text-muted-fg">*</span>')
        .replace(/~(.*?)~/g, '<del>~$1~</del>')
        .replace(/~/g, '<span class="text-muted-fg">~</span>')
        .replace(/\^(.*?)\^/g, '<sup>^$1^</sup>')
        .replace(/\^/g, '<span class="text-muted-fg">^</span>')
        .replace(
            /(https?:\/\/[^\s]+)/g,
            '<a href="$1" class="text-primary underline" target="_blank" rel="noopener noreferrer">$1</a>'
        )
        .replace(/\n/g, '<br />')
}

block/components/paginator.raw:
'use client'

import { Pagination } from '@/components/ui'
import { useMediaQuery } from '@/lib/hooks'
import { cn } from '@/lib/utils'

interface PaginateProps {
    className?: string
    show: number
    total: number
    page: number
    setPage: (page: number) => void
}

export default function Paginator({ className, show, page, total, setPage }: PaginateProps) {
    const isDesktop = useMediaQuery('(min-width: 1024px)')

    const meta = {
        total,
        from: (page - 1) * Number(show) + 1,
        to:
            total - show * page > 0
                ? total - (total - show * page)
                : show * page - Math.abs(total - show * page),
        page,
        last_page: Math.ceil(total / show)
    }

    const actions = {
        first: () => setPage(1),
        prev: () => setPage(page - 1),
        next: () => setPage(page + 1),
        last: () => setPage(meta.last_page)
    }
    return (
        <div
            className={cn(
                'flex w-full flex-col-reverse items-center gap-3 xl:flex-row xl:justify-between',
                className
            )}
        >
            <div>
                Showing {meta.from} to {meta.to} of {meta.total} results
            </div>
            <div>
                <Pagination>
                    {isDesktop ? (
                        <>
                            <Pagination.Item
                                role='first'
                                isDisabled={meta.page === 1}
                                onAction={actions.first}
                            />
                            <Pagination.Item
                                role='previous'
                                isDisabled={meta.page === 1}
                                onAction={actions.prev}
                            />
                            {meta.page > 2 && (
                                <Pagination.Item onAction={actions.first}>1</Pagination.Item>
                            )}
                            {meta.page > 3 && <Pagination.Item role='ellipsis' />}
                            {meta.page !== 1 && (
                                <Pagination.Item onAction={() => setPage(meta.page - 1)}>
                                    {meta.page - 1}
                                </Pagination.Item>
                            )}
                            <Pagination.Item isCurrent>{meta.page}</Pagination.Item>
                            {meta.page !== meta.last_page && (
                                <Pagination.Item onAction={() => setPage(meta.page + 1)}>
                                    {meta.page + 1}
                                </Pagination.Item>
                            )}
                            {meta.page < meta.last_page - 2 && <Pagination.Item role='ellipsis' />}
                            {meta.page <= meta.last_page - 2 && (
                                <Pagination.Item onAction={actions.last}>
                                    {meta.last_page}
                                </Pagination.Item>
                            )}
                            <Pagination.Item
                                role='next'
                                isDisabled={meta.page === meta.last_page}
                                onAction={actions.next}
                            />
                            <Pagination.Item
                                role='last'
                                isDisabled={meta.page === meta.last_page}
                                onAction={actions.last}
                            />
                        </>
                    ) : (
                        <>
                            <Pagination.Item
                                role='first'
                                isDisabled={meta.page === 1}
                                onAction={actions.first}
                            />
                            <Pagination.Item
                                role='previous'
                                isDisabled={meta.page === 1}
                                onAction={actions.prev}
                            />
                            <Pagination.Label current={meta.page} total={meta.last_page} />
                            <Pagination.Item
                                role='next'
                                isDisabled={meta.page === meta.last_page}
                                onAction={actions.next}
                            />
                            <Pagination.Item
                                role='last'
                                isDisabled={meta.page === meta.last_page}
                                onAction={actions.last}
                            />
                        </>
                    )}
                </Pagination>
            </div>
        </div>
    )
}

block/examples/file-manager.raw:
'use client'

import React from 'react'

import { getFileIcon, mapFilePaths, rootFolders } from 'components/file-manager/file-list'
import {
    IconLayoutGrid,
    IconLayoutList,
    IconSearch,
    IconSquare,
    IconSquareCheckBig
} from 'hq-icons'
import FileManagerLayout from 'layouts/file-manager-layout'

import { Breadcrumbs, Button, GridList, SearchField, Toggle } from '@/components/ui'

export default function FileManager() {
    const [view, setView] = React.useState<'grid' | 'stack'>('grid')
    const [selectionMode, setSelectionMode] = React.useState<'single' | 'multiple'>('single')
    const [dir, setDir] = React.useState('')
    const paths = mapFilePaths(rootFolders)

    const [files, setFiles] = React.useState(paths)

    React.useEffect(() => {
        setFiles(paths.filter((path) => path.startsWith(dir)))
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [dir])

    return (
        <FileManagerLayout rootDir={dir} setRootDir={setDir}>
            <header className='sticky top-0 flex h-[3.57rem] items-center justify-between gap-x-2 px-4'>
                <span className='flex items-center gap-x-4'>
                    <Breadcrumbs
                        onAction={(v) => setDir(v + '/')}
                        className='hidden md:flex'
                        items={dir
                            .split('/')
                            .filter((item) => item !== '')
                            .map((item) => ({ id: item }))}
                    >
                        {(item) => <Breadcrumbs.Item id={item.id}>{item.id}</Breadcrumbs.Item>}
                    </Breadcrumbs>
                </span>
                <div className='flex items-center gap-x-2'>
                    <Button
                        variant='ghost'
                        className='md:hidden'
                        aria-label='Search...'
                        size='icon'
                    >
                        <IconSearch />
                    </Button>
                    <SearchField aria-label='Search' className='hidden md:inline-flex' />
                    <Button
                        size='icon'
                        variant='ghost'
                        aria-label='View'
                        onPress={() => setView(view === 'stack' ? 'grid' : 'stack')}
                    >
                        {view === 'stack' ? (
                            <IconLayoutList />
                        ) : (
                            <IconLayoutGrid className='size-5' />
                        )}
                    </Button>
                    <Toggle
                        size='icon'
                        aria-label='Selection Mode'
                        isSelected={selectionMode === 'multiple'}
                        onChange={() =>
                            setSelectionMode(selectionMode === 'multiple' ? 'single' : 'multiple')
                        }
                    >
                        {({ isSelected }) => (isSelected ? <IconSquareCheckBig /> : <IconSquare />)}
                    </Toggle>
                </div>
            </header>
            <div className='p-4 lg:p-6'>
                <GridList
                    aria-label='Files'
                    selectionBehavior='toggle'
                    selectionMode={selectionMode}
                    columns={view === 'stack' ? 1 : 'auto'}
                    gap={view === 'stack' ? 0 : 2}
                    items={[
                        ...new Map(
                            files.map((item) => [
                                item.replace(dir, '').split('/')[0],
                                { id: item.replace(dir, '').split('/')[0] }
                            ])
                        ).values()
                    ]}
                >
                    {(item) => (
                        <GridList.Item
                            textValue={item.id}
                            id={item.id}
                            onAction={() =>
                                !item.id.includes('.') && setDir(dir + item.id.split('/')[0] + '/')
                            }
                        >
                            {getFileIcon(item.id.split('.').pop() || 'folder')}
                            {item.id}
                        </GridList.Item>
                    )}
                </GridList>
            </div>
        </FileManagerLayout>
    )
}

