drag-and-drop/drop-zone-and-file-trigger.raw:
'use client'

import React from 'react'

import { IconImage } from 'hq-icons'
import Image from 'next/image'
import { isFileDropItem } from 'react-aria-components'

import { Description, DropZone, FileTrigger } from '@/components/ui'
import type { DropEvent } from '@react-types/shared'

export default function DropZoneAndFileTriggerDemo() {
    const [droppedImage, setDroppedImage] = React.useState<string | null>(null)

    const onDropHandler = async (e: DropEvent) => {
        const item = e.items
            .filter(isFileDropItem)
            .find((item) => item.type === 'image/jpeg' || item.type === 'image/png')
        if (item) {
            const file = await item.getFile()
            setDroppedImage(URL.createObjectURL(file))
        }
    }

    const onSelectHandler = async (e: FileList | null) => {
        if (e) {
            const files = Array.from([...e])
            const item = files[0]

            if (item) {
                setDroppedImage(URL.createObjectURL(item))
            }
        }
    }

    return (
        <DropZone
            getDropOperation={(types) =>
                types.has('image/jpeg') || types.has('image/png') ? 'copy' : 'cancel'
            }
            onDrop={onDropHandler}
        >
            <div className='grid space-y-3'>
                <div className='bg-muted/70 group-drop-target:bg-primary/20 group-drop-target:border-primary/70 mx-auto grid size-12 place-content-center rounded-full overflow-hidden border'>
                    {droppedImage ? (
                        <Image
                            width={20}
                            height={20}
                            alt='Uploaded Image'
                            src={droppedImage}
                            className='aspect-square size-full object-cover'
                        />
                    ) : (
                        <IconImage className='size-5' />
                    )}
                </div>
                <div className='flex justify-center'>
                    <FileTrigger
                        acceptedFileTypes={['image/png', 'image/jpeg']}
                        allowsMultiple={false}
                        onSelect={onSelectHandler}
                    >
                        Upload an image
                    </FileTrigger>
                </div>
                <Description>Or drag and drop PNG, JPG, GIF up to 10MB</Description>
            </div>
            <input type='hidden' name='image' value={droppedImage ?? ''} />
        </DropZone>
    )
}

