    IconItalic,
    IconList,
    IconListOrdered,
    IconParagraph,
    IconRedo,
    IconStrikethrough,
    IconSubscript,
    IconSuperscript,
    IconTextQuote,
    IconUnderline,
    IconUndo
} from 'hq-icons'
import {
    $createParagraphNode,
    $getRoot,
    $getSelection,
    $insertNodes,
    $isRangeSelection,
    $isRootOrShadowRoot,
    CAN_REDO_COMMAND,
    CAN_UNDO_COMMAND,
    COMMAND_PRIORITY_CRITICAL,
    FORMAT_TEXT_COMMAND,
    REDO_COMMAND,
    SELECTION_CHANGE_COMMAND,
    UNDO_COMMAND
} from 'lexical'
import { TextField, type ValidationResult } from 'react-aria-components'

import { cn } from '@/lib/utils'
import { CodeHighlightNode, CodeNode } from '@lexical/code'
import { $generateHtmlFromNodes, $generateNodesFromDOM } from '@lexical/html'
import { AutoLinkNode, LinkNode } from '@lexical/link'
import {
    $isListNode,
    INSERT_ORDERED_LIST_COMMAND,
    INSERT_UNORDERED_LIST_COMMAND,
    ListItemNode,
    ListNode,
    REMOVE_LIST_COMMAND
} from '@lexical/list'
import { $convertToMarkdownString, TRANSFORMERS } from '@lexical/markdown'
import { type InitialConfigType, LexicalComposer } from '@lexical/react/LexicalComposer'
import { useLexicalComposerContext } from '@lexical/react/LexicalComposerContext'
import { ContentEditable } from '@lexical/react/LexicalContentEditable'
import { LexicalErrorBoundary } from '@lexical/react/LexicalErrorBoundary'
import { HistoryPlugin } from '@lexical/react/LexicalHistoryPlugin'
import { LinkPlugin } from '@lexical/react/LexicalLinkPlugin'
import { ListPlugin } from '@lexical/react/LexicalListPlugin'
import { MarkdownShortcutPlugin } from '@lexical/react/LexicalMarkdownShortcutPlugin'
import { OnChangePlugin } from '@lexical/react/LexicalOnChangePlugin'
import { PlainTextPlugin } from '@lexical/react/LexicalPlainTextPlugin'
import { RichTextPlugin } from '@lexical/react/LexicalRichTextPlugin'
import type { HeadingTagType } from '@lexical/rich-text'
import {
    $createHeadingNode,
    $createQuoteNode,
    $isHeadingNode,
    HeadingNode,
    QuoteNode
} from '@lexical/rich-text'
import { $setBlocksType } from '@lexical/selection'
import { $findMatchingParent, $getNearestNodeOfType, mergeRegister } from '@lexical/utils'

import { Button } from './button'
import { Description, FieldError, Label } from './field'
import { Menu } from './menu'
import { Toolbar } from './toolbar'

interface RichTextFieldProps {
    className?: string
    value?: string
    onChange?: (value: string) => void
    label?: string
    description?: string
    errorMessage?: string | ((validation: ValidationResult) => string)
    isDisabled?: boolean
    returnType?: 'markdown' | 'html'
    hideToolbar?: boolean
    as?: 'rich-text' | 'plain-text'
}

const RichTextField = ({
    className,
    label,
    description,
    errorMessage,
    isDisabled = false,
    returnType = 'html',
    hideToolbar = false,
    as = 'rich-text',
    ...props
}: RichTextFieldProps) => {
    const config: InitialConfigType = {
        editable: !isDisabled,
        namespace: 'rich-text-field',
        theme: {
            text: {
                strikethrough: 'line-through',
                underline: 'underline'
            }
        },
        nodes: [
            HeadingNode,
            ListNode,
            ListItemNode,
            QuoteNode,
            CodeNode,
            CodeHighlightNode,
            AutoLinkNode,
            LinkNode
        ],
        onError: (error) => {
            console.error(error)
        }
    }

    return (
        <LexicalComposer initialConfig={config}>
            <div>
                <div className='relative'>
                    {as === 'rich-text' ? (
                        <RichTextPlugin
                            placeholder={
                                <p className='text-muted-fg absolute bottom-2 left-3'>
                                    Write something...
                                </p>
                            }
                            contentEditable={
                                <TextField
                                    aria-label='Editor'
                                    id='parent'
                                    className={cn('group relative flex flex-col gap-1', className)}
                                >
                                    {label && <Label>{label}</Label>}

                                    {!hideToolbar && <ToolbarPlugin />}
                                    <ContentEditable
                                        autoFocus
                                        aria-disabled={isDisabled}
                                        disabled={isDisabled}
                                        className={cn(
                                            'bg-bg min-h-32 w-full min-w-0 rounded-lg border px-2.5 py-2 text-base transition outline-none focus:outline-none sm:text-sm',
                                            'focus:border-primary/70 focus:ring-primary/20 focus:ring-4',
                                            'invalid:border-danger invalid:ring-danger/20 invalid:ring-4',
                                            isDisabled ? 'opacity-50' : 'hover:border-primary/60 '
                                        )}
                                    />
                                    {description && <Description>{description}</Description>}
                                    <FieldError>{errorMessage}</FieldError>
                                </TextField>
                            }
                            ErrorBoundary={LexicalErrorBoundary}
                        />
                    ) : (
                        <PlainTextPlugin
                            placeholder={
                                <p className='text-muted-fg absolute bottom-2 left-3'>
                                    Write something...
                                </p>
                            }
                            contentEditable={
                                <TextField
                                    aria-label='Editor'
                                    id='parent'
                                    className={cn('group relative flex flex-col gap-1', className)}
                                >
                                    {label && <Label>{label}</Label>}

                                    {!hideToolbar && <Toolbar />}
                                    <ContentEditable
                                        autoFocus
                                        aria-disabled={isDisabled}
                                        disabled={isDisabled}
                                        className={cn(
                                            'bg-bg min-h-32 w-full min-w-0 rounded-lg border px-2.5 py-2 text-base shadow-sm transition outline-none focus:outline-none sm:text-sm',
                                            'focus:border-primary/70 focus:ring-primary/20 focus:ring-4',
                                            'invalid:border-danger invalid:ring-danger/20 invalid:ring-4',
                                            isDisabled ? 'opacity-50' : 'hover:border-primary/60 '
                                        )}
                                    />
                                    {description && <Description>{description}</Description>}
                                    <FieldError>{errorMessage}</FieldError>
                                </TextField>
                            }
                            ErrorBoundary={LexicalErrorBoundary}
                        />
                    )}
                    <HistoryPlugin />
                </div>
                <ListPlugin />
                <LinkPlugin />
                {as === 'rich-text' && <MarkdownShortcutPlugin transformers={TRANSFORMERS} />}
                <OnChange returnType={returnType} value={props.value} onChange={props.onChange} />
            </div>
        </LexicalComposer>
    )
}

interface OnChangeProps {
    value?: string
    onChange?: (value: string) => void
    returnType: 'markdown' | 'html'
}

function OnChange({ value, onChange, returnType }: OnChangeProps) {
    const [editor] = useLexicalComposerContext()
    const [isFirstRender, setIsFirstRender] = React.useState(true)

    React.useEffect(() => {
        if (returnType === 'html') {
            if (!value || !isFirstRender) return

            setIsFirstRender(false)
            editor.update(() => {
                const currentHTML = $generateHtmlFromNodes(editor)
                if (currentHTML !== value) {
                    $getRoot().clear()
                    const parser = new DOMParser()
                    const dom = parser.parseFromString(value, 'text/html')
                    const nodes = $generateNodesFromDOM(editor, dom)
                    $insertNodes(nodes)
                }
            })
        }
    }, [editor, value, isFirstRender, returnType])

    React.useEffect(() => {
        setIsFirstRender(true)
    }, [value])

    return (
        <OnChangePlugin
            onChange={(editorState) => {
                editorState.read(() => {
                    onChange?.(
                        returnType === 'markdown'
                            ? $convertToMarkdownString(TRANSFORMERS, undefined, true)
                            : $generateHtmlFromNodes(editor)
                    )
                })
            }}
        />
    )
}

function BlockTypeDropdown({ blockType }: { blockType: string }) {
    const blockIcons = {
        h1: <IconHeading1 />,
        h2: <IconHeading2 />,
        h3: <IconHeading3 />,
        paragraph: <IconParagraph />,
        bullet: <IconList />,
        number: <IconListOrdered />,
        quote: <IconTextQuote />
    }
    const [editor] = useLexicalComposerContext()

    const formatHeading = (headingLevel: HeadingTagType) => {
        editor.update(() => {
            const selection = $getSelection()
            $setBlocksType(selection, () => $createHeadingNode(headingLevel))
        })
    }

    const formatParagraph = () => {
        editor.update(() => {
            const selection = $getSelection()
            $setBlocksType(selection, () => $createParagraphNode())
        })
    }

    const formatOrderedList = () => {
        if (blockType !== 'number') {
            editor.dispatchCommand(INSERT_ORDERED_LIST_COMMAND, undefined)
        } else {
            editor.dispatchCommand(REMOVE_LIST_COMMAND, undefined)
        }
    }

    const formatUnorderedList = () => {
        if (blockType !== 'bullet') {
            editor.dispatchCommand(INSERT_UNORDERED_LIST_COMMAND, undefined)
        } else {
            editor.dispatchCommand(REMOVE_LIST_COMMAND, undefined)
        }
    }

    const formatQuote = () => {
        editor.update(() => {
            const selection = $getSelection()
            $setBlocksType(selection, () => $createQuoteNode())
        })
    }

    return (
        <Menu>
            <Button isDisabled={!editor.isEditable()} variant='outline' size='icon'>
                {blockIcons[blockType as keyof typeof blockIcons]}
            </Button>
            <Menu.Content selectionMode='single' aria-label='Block type' selectedKeys={[blockType]}>
                <Menu.Item id='paragraph' onAction={formatParagraph}>
                    <IconParagraph />
                    <Menu.Label>Paragraph</Menu.Label>
                </Menu.Item>
                <Menu.Item id='h1' onAction={() => formatHeading('h1')}>
                    <IconHeading1 />
                    <Menu.Label>Heading 1</Menu.Label>
                </Menu.Item>
                <Menu.Item id='h2' onAction={() => formatHeading('h2')}>
                    <IconHeading2 />
                    <Menu.Label>Heading 2</Menu.Label>
                </Menu.Item>
                <Menu.Item id='h3' onAction={() => formatHeading('h3')}>
                    <IconHeading3 />
                    <Menu.Label>Heading 3</Menu.Label>
                </Menu.Item>
                <Menu.Item id='bullet' onAction={formatUnorderedList}>
                    <IconList />
                    <Menu.Label>Unordered List</Menu.Label>
                </Menu.Item>
                <Menu.Item id='number' onAction={formatOrderedList}>
                    <IconListOrdered />
                    <Menu.Label>Ordered List</Menu.Label>
                </Menu.Item>
                <Menu.Item id='quote' onAction={formatQuote}>
                    <IconTextQuote />
                    <Menu.Label>Quote</Menu.Label>
                </Menu.Item>
            </Menu.Content>
        </Menu>
    )
}

export function ToolbarPlugin() {
    const [editor] = useLexicalComposerContext()
    const [formatText, setFormatText] = React.useState({
        bold: false,
        italic: false,
        underline: false,
        strikethrough: false,
        subscript: false,
        superscript: false
    })
    const [blockType, setBlockType] = useState<string>('paragraph')

    const [canUndo, setCanUndo] = useState<boolean>(false)
    const [canRedo, setCanRedo] = useState<boolean>(false)

    const $updateToolbar = useCallback(() => {
        const selection = $getSelection()
        if ($isRangeSelection(selection)) {
            setFormatText({
                bold: selection.hasFormat('bold'),
                italic: selection.hasFormat('italic'),
                underline: selection.hasFormat('underline'),
                strikethrough: selection.hasFormat('strikethrough'),
                subscript: selection.hasFormat('subscript'),
                superscript: selection.hasFormat('superscript')
            })
            const anchorNode = selection.anchor.getNode()

            let element =
                anchorNode.getKey() === 'root'
                    ? anchorNode
                    : $findMatchingParent(anchorNode, (e) => {
                          const parent = e.getParent()
                          return parent !== null && $isRootOrShadowRoot(parent)
                      })

            if (element === null) {
                element = anchorNode.getTopLevelElementOrThrow()
            }

            const elementDOM = editor.getElementByKey(element.getKey())

            if (elementDOM !== null) {
                if ($isListNode(element)) {
                    const parentList = $getNearestNodeOfType<ListNode>(anchorNode, ListNode)
                    const type = parentList ? parentList.getListType() : element.getListType()
                    setBlockType(type)
                } else {
                    const type = $isHeadingNode(element) ? element.getTag() : element.getType()
                    setBlockType(type)
                }
            }
        }
    }, [editor])

    useEffect(() => {
        return mergeRegister(
            editor.registerCommand(
                SELECTION_CHANGE_COMMAND,
                () => {
                    $updateToolbar()
                    return false
                },
                COMMAND_PRIORITY_CRITICAL
            ),
            editor.registerUpdateListener(({ editorState }) => {
                editorState.read(() => {
                    $updateToolbar()
                })
            })
        )
    }, [editor, $updateToolbar])

    useEffect(() => {
        return mergeRegister(
            editor.registerCommand(
                CAN_UNDO_COMMAND,
                (payload) => {
                    setCanUndo(payload)
                    return false
                },
                COMMAND_PRIORITY_CRITICAL
            ),
            editor.registerCommand(
                CAN_REDO_COMMAND,
                (payload) => {
                    setCanRedo(payload)
                    return false
                },
                COMMAND_PRIORITY_CRITICAL
            )
        )
    }, [editor])

    return (
        <Toolbar className='mb-2' aria-label='Toolbar'>
            <Toolbar.Group aria-label='Formats'>
                <BlockTypeDropdown blockType={blockType} />
                <Toolbar.Item
                    isDisabled={!editor.isEditable()}
                    size='icon'
                    isSelected={formatText.bold}
                    onChange={() => {
                        editor.dispatchCommand(FORMAT_TEXT_COMMAND, 'bold')
                    }}
                >
                    <IconBold />
                </Toolbar.Item>
                <Toolbar.Item
                    isDisabled={!editor.isEditable()}
                    size='icon'
                    isSelected={formatText.italic}
                    onChange={() => {
                        editor.dispatchCommand(FORMAT_TEXT_COMMAND, 'italic')
                    }}
                >
                    <IconItalic />
                </Toolbar.Item>
                <Toolbar.Item
                    isDisabled={!editor.isEditable()}
                    size='icon'
                    isSelected={formatText.underline}
                    onChange={() => {
                        editor.dispatchCommand(FORMAT_TEXT_COMMAND, 'underline')
                    }}
                >
                    <IconUnderline />
                </Toolbar.Item>
                <Toolbar.Item
                    isDisabled={!editor.isEditable()}
                    size='icon'
                    isSelected={formatText.strikethrough}
                    onChange={() => {
                        editor.dispatchCommand(FORMAT_TEXT_COMMAND, 'strikethrough')
                    }}
                >
                    <IconStrikethrough />
                </Toolbar.Item>
                <Toolbar.Item
                    isDisabled={!editor.isEditable()}
                    size='icon'
                    isSelected={formatText.subscript}
                    onChange={() => {
                        editor.dispatchCommand(FORMAT_TEXT_COMMAND, 'subscript')
                    }}
                >
                    <IconSubscript />
                </Toolbar.Item>
                <Toolbar.Item
                    isDisabled={!editor.isEditable()}
                    size='icon'
                    isSelected={formatText.superscript}
                    onChange={() => {
                        editor.dispatchCommand(FORMAT_TEXT_COMMAND, 'superscript')
                    }}
                >
                    <IconSuperscript />
                </Toolbar.Item>
            </Toolbar.Group>
            <Toolbar.Separator />
            <Toolbar.Group aria-label='Actions'>
                <Toolbar.Item
                    size='icon'
                    isDisabled={!canUndo || !editor.isEditable()}
                    isSelected={false}
                    onPress={() => editor.dispatchCommand(UNDO_COMMAND, undefined)}
                >
                    <IconUndo />
                </Toolbar.Item>
                <Toolbar.Item
                    size='icon'
                    isDisabled={!canRedo || !editor.isEditable()}
                    isSelected={false}
                    onPress={() => editor.dispatchCommand(REDO_COMMAND, undefined)}
                >
                    <IconRedo />
                </Toolbar.Item>
            </Toolbar.Group>
        </Toolbar>
    )
}

export { RichTextField }

toast.raw:
'use client'

import React from 'react'

import { IconCircleAlert, IconCircleCheck, IconInfo, IconTriangleAlert, IconX } from 'hq-icons'
import { motion, useAnimation } from 'motion/react'
import {
    Button,
    ButtonProps,
    Text,
    UNSTABLE_ToastContent as ToastContent,
    UNSTABLE_Toast as Toaster,
    ToastOptions,
    UNSTABLE_ToastQueue as ToastQueue,
    UNSTABLE_ToastRegion as ToastRegion
} from 'react-aria-components'

import { cn } from '@/lib/utils'

interface ToastContent {
    title: string
    description?: string
    action?: () => void
    actionLabel?: React.ReactNode
    altAction?: () => void
    altActionLabel?: React.ReactNode
    type: 'default' | 'success' | 'error' | 'info' | 'warning'
}

const queue = new ToastQueue<ToastContent>({
    wrapUpdate(fn) {
        if ('startViewTransition' in document) {
            document.startViewTransition(() => fn())
        } else fn()
    },
    maxVisibleToasts: 5
})

const ToastProvider = () => {
    const [isHovered, setIsHovered] = React.useState(false)
    return (
        <ToastRegion
            className='fixed w-full h-fit sm:w-fit flex flex-col-reverse items-center sm:items-end p-4 gap-3 sm:bottom-2 sm:right-2 top-0 sm:top-auto'
            queue={queue}
        >
            {({ toast }) => (
                <Toaster
                    key={toast.key}
                    toast={toast}
                    className={cn('sm:min-w-xs will-change-transform sm:w-fit')}
                >
                    <motion.div
                        onMouseEnter={() => setIsHovered(true)}
                        onMouseLeave={() => setIsHovered(false)}
                        key={toast.key}
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        className={cn(
                            'flex flex-col backdrop-blur-lg p-4 gap-2 rounded-lg shadow-[0_3px_10px_rgb(0,0,0,0.3)] border',
                            {
                                'border-success/20 text-success bg-success/10':
                                    toast.content.type === 'success',
                                'border-danger/20 bg-danger/5 text-danger':
                                    toast.content.type === 'error',
                                'border-primary/30 text-primary bg-primary/10':
                                    toast.content.type === 'info',
                                'border-warning/40 bg-warning/5 text-warning':
                                    toast.content.type === 'warning',
                                'bg-bg text-fg': toast.content.type === 'default'
                            }
                        )}
                    >
                        <div
                            className={cn('flex items-start gap-2', {
                                'items-center': !toast.content.description
                            })}
                        >
                            {toast.content.type === 'success' ? (
                                <IconCircleCheck className='shrink-0' />
                            ) : toast.content.type === 'error' ? (
                                <IconCircleAlert className='shrink-0' />
                            ) : toast.content.type === 'info' ? (
                                <IconInfo className='shrink-0' />
                            ) : toast.content.type === 'warning' ? (
                                <IconTriangleAlert className='shrink-0' />
                            ) : null}
                            <ToastContent className='w-full flex flex-col'>
                                <Text slot='title' className='font-medium'>
                                    {toast.content.title}
                                </Text>
                                {toast.content.description && (
                                    <Text slot='description' className='text-sm'>
                                        {toast.content.description}
                                    </Text>
                                )}
                            </ToastContent>
                            <TimeoutButton paused={isHovered} timeout={toast.timeout} slot='close'>
                                <IconX />
                            </TimeoutButton>
                        </div>
                        {(toast.content.action || toast.content.altAction) && (
                            <div className='flex items-center gap-2 justify-start'>
                                {toast.content.action && (
                                    <Button
                                        onPress={toast.content.action}
                                        slot='close'
                                        className={({ isHovered, isPressed, isFocusVisible }) =>
                                            cn(
                                                'outline-hidden cursor-pointer flex items-center border justify-center gap-x-1.5 rounded-lg px-2 py-1 text-sm *:[svg]:size-3',
                                                isHovered && 'brightness-80',
                                                isPressed && 'brightness-90',
                                                isFocusVisible && 'ring-4 ring-primary/20',
                                                {
                                                    'border-success text-success-fg bg-success':
                                                        toast.content.type === 'success',
                                                    'border-danger text-danger-fg bg-danger':
                                                        toast.content.type === 'error',
                                                    'border-primary text-primary-fg bg-primary':
                                                        toast.content.type === 'info' ||
                                                        toast.content.type === 'default',
                                                    'border-warning text-warning-fg bg-warning':
                                                        toast.content.type === 'warning'
                                                }
                                            )
                                        }
                                    >
                                        {toast.content.actionLabel || 'Action'}
                                    </Button>
                                )}
                                {toast.content.altAction && (
                                    <Button
                                        onPress={toast.content.altAction}
                                        slot='close'
                                        className={({ isHovered, isPressed, isFocusVisible }) =>
                                            cn(
                                                'outline-hidden cursor-pointer flex items-center border justify-center gap-x-1.5 rounded-lg bg-bg px-2 py-1 text-sm text-fg *:[svg]:size-3',
                                                isHovered && 'bg-muted/40',
                                                isPressed && 'bg-muted/60',
                                                isFocusVisible &&
                                                    'ring-4 ring-primary/20 border-primary'
                                            )
                                        }
                                    >
                                        {toast.content.altActionLabel || 'Cancel'}
                                    </Button>
                                )}
                            </div>
                        )}
                    </motion.div>
                </Toaster>
            )}
        </ToastRegion>
    )
}

interface TimeoutButtonProps extends ButtonProps {
    timeout: number | undefined
    paused?: boolean
    onComplete?: () => void
}

const TimeoutButton = ({ timeout, onComplete, paused, children, ...props }: TimeoutButtonProps) => {
    const controls = useAnimation()
    const startTimeRef = React.useRef<number | null>(null)
    const elapsedRef = React.useRef<number>(0)
    const size = 28
    const stroke = 2
    const radius = (size - stroke) / 2
    const circumference = 2 * Math.PI * radius

    const animate = (duration: number) => {
        controls
            .start({
                strokeDashoffset: 0,
                transition: { duration, ease: 'linear' }
            })
            .then(() => {
                onComplete?.()
            })
    }

    React.useEffect(() => {
        if (timeout === undefined) return
        startTimeRef.current = performance.now()
        animate(timeout / 1000)
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [timeout])

    React.useEffect(() => {
        if (timeout === undefined) return

        if (paused) {
            controls.stop()
            if (startTimeRef.current) {
                elapsedRef.current += performance.now() - startTimeRef.current
            }
        } else {
            const remaining = timeout - elapsedRef.current
            if (remaining > 0) {
                startTimeRef.current = performance.now()
                animate(remaining / 1000)
            }
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [paused])

    return (
        <Button
            className={({ isHovered, isPressed, isFocusVisible }) =>
                cn(
                    'relative shrink-0 outline-hidden ml-auto border size-7 rounded-full inline-flex items-center justify-center',
                    isHovered && 'bg-muted/40',
                    isPressed && 'bg-muted/60',
                    isFocusVisible && 'ring-4 ring-primary/20 border-primary'
                )
            }
            {...props}
        >
            {(values) => (
                <>
                    {typeof children === 'function' ? children(values) : children}
                    {timeout !== undefined && (
                        <svg
                            className='absolute top-0 left-0 size-full'
                            viewBox={`0 0 ${size} ${size}`}
                        >
                            <motion.circle
                                stroke='#3b82f6'
                                fill='transparent'
                                strokeWidth={stroke}
                                strokeDasharray={circumference}
                                strokeDashoffset={circumference}
                                animate={controls}
                                r={radius}
                                cx={size / 2}
                                cy={size / 2}
                            />
                        </svg>
                    )}
                </>
            )}
        </Button>
    )
}

const toast = (
    body: string,
    content?: Omit<ToastContent, 'type' | 'title'>,
    options?: ToastOptions
) => queue.add({ ...content, title: body, type: 'default' }, options)

toast.success = (
    body: string,
    content?: Omit<ToastContent, 'type' | 'title'>,
    options?: ToastOptions
) => queue.add({ ...content, title: body, type: 'success' }, options)

toast.error = (
    body: string,
    content?: Omit<ToastContent, 'type' | 'title'>,
    options?: ToastOptions
) => queue.add({ ...content, title: body, type: 'error' }, options)

toast.info = (
    body: string,
    content?: Omit<ToastContent, 'type' | 'title'>,
    options?: ToastOptions
) => queue.add({ ...content, title: body, type: 'info' }, options)

toast.warning = (
    body: string,
    content?: Omit<ToastContent, 'type' | 'title'>,
    options?: ToastOptions
) => queue.add({ ...content, title: body, type: 'warning' }, options)

export { toast, ToastProvider }

tooltip.raw:
'use client'

import type { TooltipProps as RACTooltipProps } from 'react-aria-components'
import {
    composeRenderProps,
    OverlayArrow,
    Pressable,
    Tooltip as RACTooltip,
    TooltipTrigger as RACTooltipTrigger
