'use client'

import React from 'react'

import { IconCheck, IconChevronDown } from 'hq-icons'
import type {
    ListBoxItemProps,
    ListBoxSectionProps,
    SelectProps as RACSelectProps,
    TextProps
} from 'react-aria-components'
import {
    Button,
    Collection,
    composeRenderProps,
    Header,
    ListBox,
    ListBoxItem,
    ListBoxSection,
    Popover,
    Select as RACSelect,
    SelectValue,
    Text
} from 'react-aria-components'

import { cn } from '@/lib/utils'
import type { Placement } from '@react-types/overlays'

import { Description, FieldError, FieldProps, Label } from './field'

interface SelectProps<T extends object> extends Omit<RACSelectProps<T>, 'children'>, FieldProps {
    className?: string
    items?: Iterable<T>
    children: React.ReactNode | ((item: T) => React.ReactNode)
    placement?: Placement
    prefix?: React.ReactNode
}

const Select = <T extends object>({
    label,
    description,
    placement,
    errorMessage,
    children,
    items,
    className,
    ...props
}: SelectProps<T>) => {
    return (
        <RACSelect
            className={composeRenderProps(className, (className) =>
                cn('group flex flex-col gap-y-1.5 **:data-placeholder:text-muted-fg', className)
            )}
            {...props}
        >
            {({ isInvalid, isDisabled, isOpen, isFocusVisible, isFocused }) => (
                <>
                    {label && (
                        <Label isInvalid={isInvalid || !!errorMessage} isDisabled={isDisabled}>
                            {label}
                        </Label>
                    )}
                    <Button
                        className={composeRenderProps(className, (className, { isHovered }) =>
                            cn(
                                'flex h-10 w-full cursor-default items-center gap-4 gap-x-2 rounded-lg border p-2 transition outline-hidden',
                                {
                                    'border-primary/70 ring-primary/20 ring-4':
                                        isFocusVisible || isOpen || isFocused
                                },
                                isHovered && 'border-primary/70 invalid:border-danger/70',
                                isInvalid && 'border-danger/70 ring-danger/20',
                                isDisabled && 'opacity-50',
                                className
                            )
                        )}
                    >
                        {props.prefix ? (
                            <span className='ml-2 has-[button]:ml-0 text-muted-fg'>
                                {props.prefix}
                            </span>
                        ) : null}
                        <SelectValue className='grid grid-cols-[auto_1fr] items-center text-base **:data-avatar:-mx-0.5 **:data-avatar:mr-2 **:data-avatar:size-6 **:data-[slot=icon]:mr-2 sm:text-sm **:data-[slot=description]:hidden' />
                        <IconChevronDown
                            data-slot='chevron'
                            className={cn(
                                'text-muted-fg size-4 transition ml-auto',
                                isOpen && '-rotate-180'
                            )}
                        />
                    </Button>
                    {description && <Description>{description}</Description>}
                    <FieldError>{errorMessage}</FieldError>
                    <Popover
                        trigger='Select'
                        placement={placement}
                        className={({ isEntering, isExiting }) =>
                            cn(
                                'group max-h-72 overflow-y-auto border p-1 shadow bg-bg w-full max-w-(--trigger-width) rounded-lg transition outline-hidden',
                                isEntering &&
                                    'fade-in animate-in zoom-in-95 placement-left:slide-in-from-right-2 placement-right:slide-in-from-left-2 placement-top:slide-in-from-bottom-2 placement-bottom:slide-in-from-top-2',
                                isExiting &&
                                    'fade-out animate-out zoom-out-95 placement-left:slide-out-to-right-2 placement-right:slide-out-to-left-2 placement-top:slide-out-to-bottom-2 placement-bottom:slide-out-to-top-2'
                            )
                        }
                    >
                        <ListBox
                            aria-label='items'
                            items={items}
                            className='grid outline-hidden w-full grid-cols-[auto_1fr_1.5rem_0.5rem_auto] gap-y-1 overflow-y-auto rounded-lg'
                        >
                            {children}
                        </ListBox>
                    </Popover>
                </>
            )}
        </RACSelect>
    )
}

const SelectItem = ({ className, children, ...props }: ListBoxItemProps) => {
    const textValue = typeof children === 'string' ? children : undefined

    return (
        <ListBoxItem
            textValue={textValue}
            {...props}
            className={composeRenderProps(
                className,
                (className, { isHovered, isFocused, isSelected, isDisabled, isFocusVisible }) =>
                    cn(
                        'group relative grid grid-cols-subgrid col-span-full outline-hidden',
                        'rounded-md px-2 py-1.5 text-base sm:text-sm/6 select-none',
                        '**:[svg]:size-4 *:[svg]:mr-2 *:[svg]:my-1 *:data-avatar:mr-2 *:data-avatar:size-6',
                        { 'bg-primary/10 text-primary': isFocused || isFocusVisible || isHovered },
                        isSelected && '**:data-[slot=icon]:hidden **:data-avatar:hidden',
                        isDisabled && 'pointer-events-none opacity-50',
                        className
                    )
            )}
        >
            {({ isSelected }) => (
                <>
                    {isSelected && <IconCheck data-slot='checked' />}
                    {typeof children === 'string' ? (
                        <Text slot='label' className='col-start-2'>
                            {children}
                        </Text>
                    ) : (
                        children
                    )}
                </>
            )}
        </ListBoxItem>
    )
}

const SelectSection = <T extends object>({
    className,
    ...props
}: ListBoxSectionProps<T> & { title?: string }) => (
    <ListBoxSection
        className={cn('col-span-full text-sm grid grid-cols-[auto_1fr] mt-2', className)}
    >
        {'title' in props && (
            <Header className='text-muted-fg text-xs py-1 px-2 col-span-full pointer-events-none'>
                {props.title}
            </Header>
        )}
        <Collection items={props.items}>{props.children}</Collection>
    </ListBoxSection>
)

interface SelectDetailsProps extends TextProps {
    label?: TextProps['children']
    description?: TextProps['children']
}

const SelectDetails = ({ label, description, ...props }: SelectDetailsProps) => {
    const { children, title, ...restProps } = props
    return (
        <div data-slot='item-details' className='col-start-2 flex flex-col gap-y-1' {...restProps}>
            {label && (
                <Text data-slot='label' className='font-medium sm:text-sm'>
                    {label}
                </Text>
            )}
            {description && (
                <Text data-slot='description' className='text-muted-fg text-xs' {...restProps}>
                    {description}
                </Text>
            )}
            {!title && children}
        </div>
    )
}

Select.Item = SelectItem
Select.Details = SelectDetails
Select.Section = SelectSection

export { Select }

sheet.raw:
'use client'

import React from 'react'

import { IconX } from 'hq-icons'
import { AnimatePresence, motion } from 'motion/react'
import type {
    DialogProps,
    DialogTriggerProps,
    HeadingProps,
    ModalOverlayProps,
    TextProps
} from 'react-aria-components'
import {
    Button,
    Dialog,
    DialogTrigger,
    Heading,
    ModalOverlay,
    OverlayTriggerStateContext,
    Modal as RACModal,
    Text
} from 'react-aria-components'

import { cn } from '@/lib/utils'

const Modal = motion.create(RACModal)
const Overlay = motion.create(ModalOverlay)

const Sheet = (props: DialogTriggerProps) => <DialogTrigger {...props} />

interface SheetContentProps
    extends Omit<ModalOverlayProps, 'className' | 'children' | 'isDismissable'>,
        Pick<DialogProps, 'aria-label' | 'aria-labelledby' | 'role' | 'children' | 'className'> {
    closeButton?: boolean
    isFloating?: boolean
    isBlurred?: boolean
    className?: string
    style?: React.CSSProperties
    side?: 'top' | 'bottom' | 'left' | 'right'
}

const SheetContent = ({
    side = 'right',
    closeButton = true,
    isFloating = false,
    isBlurred = true,
    children,
    className,
    ...props
}: SheetContentProps) => {
    const state = React.use(OverlayTriggerStateContext)!

    return (
        <AnimatePresence>
            {(props?.isOpen || state?.isOpen) && (
                <Overlay
                    isDismissable
                    isOpen={props?.isOpen || state?.isOpen}
                    onOpenChange={props?.onOpenChange || state?.setOpen}
                    initial={{ backgroundColor: 'rgba(0, 0, 0, 0)', backdropFilter: 'blur(0px)' }}
                    animate={{
                        backgroundColor: 'rgba(0, 0, 0, 0.5)',
                        backdropFilter: isBlurred ? 'blur(4px)' : 'blur(0px)'
                    }}
                    exit={{ backgroundColor: 'rgba(0, 0, 0, 0)', backdropFilter: 'blur(0px)' }}
                    className='fixed inset-0 z-50 [--visual-viewport-vertical-padding:32px] will-change-auto'
                >
                    {({ state }) => (
                        <Modal
                            className={cn(
                                'fixed will-change-transform max-h-full touch-none bg-bg text-fg align-middle shadow-sm overflow-hidden',
                                side === 'top' &&
                                    `${
                                        isFloating
                                            ? 'top-2 inset-x-2 rounded-lg border-b-0'
                                            : 'inset-x-0 top-0 rounded-b-2xl border-b'
                                    }`,
                                side === 'right' &&
                                    `max-w-xs w-full overflow-y-auto **:[[slot=header]]:text-left ${
                                        isFloating
                                            ? 'inset-y-2 right-2 rounded-lg border'
                                            : 'inset-y-0 right-0 h-auto border-l'
                                    }`,
                                side === 'bottom' &&
                                    `${
                                        isFloating
                                            ? 'bottom-2 inset-x-2 rounded-lg border-t-0'
                                            : 'inset-x-0 bottom-0 rounded-t-2xl border-t'
                                    }`,
                                side === 'left' &&
                                    `max-w-xs w-full overflow-y-auto **:[[slot=header]]:text-left ${
                                        isFloating
                                            ? 'inset-y-2 left-2 rounded-lg border'
                                            : 'inset-y-0 left-0 h-auto border-r'
                                    }`,
                                className
                            )}
                            initial={{
                                x: side === 'left' ? '-100%' : side === 'right' ? '100%' : 0,
                                y: side === 'top' ? '-100%' : side === 'bottom' ? '100%' : 0
                            }}
                            animate={{ x: 0, y: 0 }}
                            exit={{
                                x: side === 'left' ? '-100%' : side === 'right' ? '100%' : 0,
                                y: side === 'top' ? '-100%' : side === 'bottom' ? '100%' : 0
                            }}
                            drag={side === 'left' || side === 'right' ? 'x' : 'y'}
                            whileDrag={{ cursor: 'grabbing' }}
                            dragConstraints={{ top: 0, bottom: 0, left: 0, right: 0 }}
                            dragTransition={{ bounceStiffness: 600, bounceDamping: 20 }}
                            transition={{ duration: 0.15, ease: 'easeInOut' }}
                            onDragEnd={(_, { offset, velocity }) => {
                                if (
                                    side === 'bottom' &&
                                    (velocity.y > 150 || offset.y > screen.height * 0.25)
                                ) {
                                    state.close()
                                }
                                if (
                                    side === 'top' &&
                                    (velocity.y < -150 || offset.y < screen.height * 0.25)
                                ) {
                                    state.close()
                                }
                                if (side === 'left' && velocity.x < -150) {
                                    state.close()
                                }
                                if (side === 'right' && velocity.x > 150) {
                                    state.close()
                                }
                            }}
                            dragElastic={{
                                top: side === 'top' ? 1 : 0,
                                bottom: side === 'bottom' ? 1 : 0,
                                left: side === 'left' ? 1 : 0,
                                right: side === 'right' ? 1 : 0
                            }}
                            dragPropagation
                        >
                            <Dialog
                                aria-label='Sheet'
                                role='dialog'
                                className={cn(
                                    'relative flex flex-col overflow-hidden outline-hidden will-change-auto',
                                    side === 'top' || side === 'bottom'
                                        ? 'max-h-[calc(var(--visual-viewport-height)-var(--visual-viewport-vertical-padding))]'
                                        : 'max-h-full'
                                )}
                            >
                                {children as React.ReactNode}
                                {closeButton && (
                                    <Button
                                        aria-label='Close'
                                        slot='close'
                                        className={({ isPressed, isHovered, isFocusVisible }) =>
                                            cn(
                                                'absolute top-2 right-2 bg-bg shrink-0 inline-flex size-8 items-center justify-center rounded-md text-muted-fg outline-hidden',
                                                isHovered && 'bg-muted/40',
                                                isFocusVisible && 'ring-4 ring-primary/20',
                                                isPressed && 'bg-muted/50'
                                            )
                                        }
                                    >
                                        <IconX />
                                    </Button>
                                )}
                            </Dialog>
                        </Modal>
                    )}
                </Overlay>
            )}
        </AnimatePresence>
    )
}

const Header = ({ className, ...props }: React.HTMLAttributes<HTMLDivElement>) => {
    return (
        <div
            slot='header'
            className={cn('flex flex-col p-4 sm:text-left text-center', className)}
            {...props}
        />
    )
}

const Title = ({ className, ...props }: HeadingProps) => (
    <Heading slot='title' className={cn('font-semibold text-lg/8', className)} {...props} />
)

const Description = ({ className, ...props }: TextProps) => (
