                    return { id: Math.random(), name }
                })
            )

            if (e.target.dropPosition === 'before') {
                list.insertBefore(e.target.key, ...items)
            } else if (e.target.dropPosition === 'after') {
                list.insertAfter(e.target.key, ...items)
            }
        }
    })

    return (
        <GridList
            aria-label='Droppable list'
            items={list.items}
