collections/table/table-sorting-demo.raw:
'use client'

import { IconLoader } from 'hq-icons'

import { Card, Table } from '@/components/ui'
import { useAsyncList } from '@react-stately/data'

interface Character {
    title: string
    director: number
    producer: number
    release_date: number
}

export default function TableSortingDemo() {
    const list = useAsyncList<Character>({
        async load({ signal }) {
            const res = await fetch(`https://swapi.py4e.com/api/films`, {
                signal
            })
            const json = await res.json()
            return {
                items: json.results
            }
        },
        async sort({ items, sortDescriptor }) {
            return {
                items: items.sort((a, b) => {
                    // @ts-expect-error unknown-type
                    const first = a[sortDescriptor.column]
                    // @ts-expect-error unknown-type
                    const second = b[sortDescriptor.column]
                    let cmp = (parseInt(first) || first) < (parseInt(second) || second) ? -1 : 1
                    if (sortDescriptor.direction === 'descending') {
                        cmp *= -1
                    }
                    return cmp
                })
            }
        }
    })
    return (
        <Card>
            <Table
                aria-label='Movies'
                selectionMode='multiple'
                sortDescriptor={list.sortDescriptor}
                onSortChange={list.sort}
            >
                <Table.Header>
                    <Table.Column id='title' isRowHeader>
                        Title
                    </Table.Column>
                    <Table.Column id='director' allowsSorting>
                        Director
                    </Table.Column>
                    <Table.Column id='producer'>Producer</Table.Column>
                    <Table.Column id='release_date' allowsSorting>
                        Release Date
                    </Table.Column>
                </Table.Header>
                <Table.Body
                    items={list.items}
                    renderEmptyState={() => (
                        <div className='grid place-content-center p-10'>
                            <IconLoader className='animate-spin' />
                        </div>
                    )}
                >
                    {(item) => (
                        <Table.Row id={item.title}>
                            <Table.Cell>{item.title}</Table.Cell>
                            <Table.Cell>{item.director}</Table.Cell>
                            <Table.Cell>{item.producer}</Table.Cell>
                            <Table.Cell>{item.release_date}</Table.Cell>
                        </Table.Row>
                    )}
                </Table.Body>
            </Table>
        </Card>
    )
}

