    },
    {
        country: 'France',
        count: 2134,
        percentage: 9.6
    },
    {
        country: 'Indonesia',
        count: 3567,
        percentage: 16.2
    },
    {
        country: 'United Kingdom',
        count: 2234,
        percentage: 4.5
    },
    {
        country: 'Brazil',
        count: 1967,
        percentage: 4.2
    },
    {
        country: 'Canada',
        count: 4276,
        percentage: 22.0
    }
]

const config = {
    count: {
        label: 'Count',
        color: 'hsl(var(--primary-chart))'
    }
} satisfies ChartConfig

export default function BarChartAnalyticDemo() {
    return (
        <Card>
            <Card.Header>
                <Card.Title>Bar Chart - Custom Label</Card.Title>
                <Card.Description>
                    The number of visitors to the website has increased by 15.5% this year
                </Card.Description>
            </Card.Header>
            <Card.Content>
                <Chart className='min-h-[250px] max-h-[250px] w-full' config={config}>
                    <BarChart
                        accessibilityLayer
                        data={data}
                        layout='vertical'
                        barSize={30}
                        margin={{ left: 0, right: 0 }}
                    >
                        <YAxis dataKey='name' type='category' hide />
                        <XAxis dataKey='count' type='number' hide />
