multi-select.raw:
'use client'

import React from 'react'

import { IconChevronDown } from 'hq-icons'
import { useFilter } from 'react-aria'
import {
    ComboBox,
    Group,
    type ComboBoxProps,
    type Key,
    type ValidationResult
} from 'react-aria-components'
import { useListData, type ListData } from 'react-stately'

import { Button } from './button'
import { Description, FieldError, Input, Label } from './field'
import { ListBox } from './list-box'
import { Popover } from './popover'
import { Tag, type RestrictedVariant } from './tag-group'
import { VisuallyHidden, cn } from './utils'

interface FieldState {
    selectedKey: Key | null
    inputValue: string
}

interface SelectedKey {
    id: Key
    textValue: string
}

interface MultipleSelectProps<T extends object>
    extends Omit<
        ComboBoxProps<T>,
        | 'children'
        | 'validate'
        | 'allowsEmptyCollection'
        | 'selectedKey'
        | 'inputValue'
        | 'className'
        | 'value'
        | 'onSelectionChange'
        | 'onInputChange'
    > {
    label?: string
    description?: string
    variant?: RestrictedVariant
    items: Array<T>
    selectedList: ListData<T>
    className?: string
    onItemAdd?: (key: Key) => void
    onItemRemove?: (key: Key) => void
    renderEmptyState?: (inputValue: string) => React.ReactNode
    tag: (item: T) => React.ReactNode
    children: React.ReactNode | ((item: T) => React.ReactNode)
    max?: number
    min?: number
    errorMessage?: string | ((validation: ValidationResult) => string)
    portal?: Element
}

const MultiSelect = <T extends SelectedKey>({
    children,
    variant = 'primary',
    items,
    selectedList,
    onItemRemove,
    onItemAdd,
    className,
    name,
    renderEmptyState,
    max,
    min,
    errorMessage,
    ...props
}: MultipleSelectProps<T>) => {
    const tagGroupId = React.useId()
    const triggerRef = React.useRef<HTMLDivElement | null>(null)
    const [width, setWidth] = React.useState(0)

    const { contains } = useFilter({ sensitivity: 'base' })
    const selectedKeys = selectedList.items.map((i) => i.id)

    const filter = React.useCallback(
        (item: T, filterText: string) =>
            !selectedKeys.includes(item.id) && contains(item.textValue, filterText),
        [contains, selectedKeys]
    )

    const accessibleList = useListData({
        initialItems: items,
        filter
    })

    const [fieldState, setFieldState] = React.useState<FieldState>({
        selectedKey: null,
        inputValue: ''
    })

    const onRemove = React.useCallback(
        (keys: Set<Key>) => {
            if (min !== undefined && selectedList.items.length <= min) return

            const key = keys.values().next().value
            selectedList.remove(key as Key)
            setFieldState({
                inputValue: '',
                selectedKey: null
            })
            onItemRemove?.(key as Key)
        },
        [selectedList, onItemRemove, min]
    )

    const onSelectionChange = (id: Key | null) => {
        if (!id) return

        const item = accessibleList.getItem(id)

        if (!item) return

        if (!selectedKeys.includes(id) && (max === undefined || selectedList.items.length < max)) {
            selectedList.append(item)
            setFieldState({
                inputValue: '',
                selectedKey: id
            })
            onItemAdd?.(id)
        }

        accessibleList.setFilterText('')
    }

    const onInputChange = (v: string) => {
        setFieldState((prevState) => ({
            inputValue: v,
            selectedKey: v === '' ? null : prevState.selectedKey
        }))

        accessibleList.setFilterText(v)
    }

    const deleteLast = React.useCallback(() => {
        if (
            selectedList.items.length == 0 ||
            (min !== undefined && selectedList.items.length <= min)
        ) {
            return
        }

        const lastKey = selectedList.items[selectedList.items.length - 1]

        if (lastKey !== null) {
            selectedList.remove(lastKey.id)
            onItemRemove?.(lastKey.id)
        }

        setFieldState({
            inputValue: '',
            selectedKey: null
        })
    }, [selectedList, onItemRemove, min])

    const onKeyDownCapture = React.useCallback(
        (e: React.KeyboardEvent<HTMLInputElement>) => {
            if (e.key === 'Backspace' && fieldState.inputValue === '') {
                deleteLast()
            }
        },
        [deleteLast, fieldState.inputValue]
    )

    React.useEffect(() => {
        const trigger = triggerRef.current
        if (!trigger) return

        const observer = new ResizeObserver((entries) => {
            for (const entry of entries) {
                setWidth(entry.target.clientWidth)
            }
        })

        observer.observe(trigger)
        return () => {
            observer.unobserve(trigger)
        }
    }, [triggerRef])

    const triggerButtonRef = React.useRef<HTMLButtonElement | null>(null)

    return (
        <Group className={cn('group flex w-full min-w-fit flex-col gap-1', className)}>
            {props.label && <Label>{props.label}</Label>}
            <div
                ref={triggerRef}
                className={cn(
                    'relative px-1 flex min-h-10 flex-row flex-wrap items-center rounded-lg shadow-sm border transition',
                    'has-[input[data-focused=true]]:border-primary',
                    'has-[input[data-invalid=true][data-focused=true]]:border-danger has-[input[data-invalid=true]]:border-danger',
                    'has-[input[data-focused=true]]:ring-4 has-[input[data-focused=true]]:ring-primary/20',
                    className
                )}
            >
                <Tag.Group
                    aria-label='Selected items'
                    variant={variant}
                    id={tagGroupId}
                    onRemove={onRemove}
                >
                    <Tag.List
                        items={selectedList.items}
                        className={cn(
                            selectedList.items.length !== 0 && 'px-1 py-1.5',
                            '[&_.tag]:rounded-[calc(var(--radius)-2.5px)] last:[&_.tag]:-mr-1 outline-none gap-1.5'
                        )}
                    >
                        {props.tag}
                    </Tag.List>
                </Tag.Group>
                <ComboBox
                    {...props}
                    aria-label='Available items'
                    allowsEmptyCollection
                    className={cn('group peer flex flex-1', className)}
                    items={accessibleList.items}
                    selectedKey={fieldState.selectedKey}
                    inputValue={fieldState.inputValue}
                    onSelectionChange={onSelectionChange}
                    onInputChange={onInputChange}
                >
                    <div
                        className={cn('inline-flex flex-1 flex-wrap items-center px-0', className)}
                    >
                        <FieldError>{errorMessage}</FieldError>
                        <Input
                            className='flex-1 py-1 px-0.5 ml-1 shadow-none ring-0'
                            onBlur={() => {
                                setFieldState({
                                    inputValue: '',
                                    selectedKey: null
                                })
                                accessibleList.setFilterText('')
                            }}
                            onKeyDownCapture={onKeyDownCapture}
                        />

                        <VisuallyHidden>
                            <Button
                                slot='remove'
                                aria-label='Remove'
                                variant='ghost'
                                size='icon'
                                ref={triggerButtonRef}
                            >
                                <IconChevronDown />
                            </Button>
                        </VisuallyHidden>
                    </div>
                    <Popover.Picker
                        UNSTABLE_portalContainer={props.portal}
                        className='max-w-none'
                        style={{ width: `${width}px` }}
                        triggerRef={triggerRef}
                        trigger='ComboBox'
                    >
                        <ListBox.Picker
                            renderEmptyState={() =>
                                renderEmptyState ? (
                                    renderEmptyState(fieldState.inputValue)
                                ) : (
                                    <Description className='p-3 block'>
                                        {fieldState.inputValue ? (
                                            <>
                                                No results found for:{' '}
                                                <strong className='font-semibold'>
                                                    {fieldState.inputValue}
                                                </strong>
                                            </>
                                        ) : (
                                            `No options`
                                        )}
                                    </Description>
                                )
                            }
                            selectionMode='multiple'
                        >
                            {children}
                        </ListBox.Picker>
                    </Popover.Picker>
                </ComboBox>
                <div className='relative px-1 ml-auto flex items-center justify-center peer-data-[open]:[&_button>svg]:rotate-180 [&_button>svg]:transition'>
                    <button
                        type='button'
                        className='size-8 -mr-2 grid place-content-center rounded-lg hover:text-foreground focus:text-foreground text-muted-foreground'
                        onClick={() => triggerButtonRef.current?.click()}
                        tabIndex={-1}
                    >
                        <IconChevronDown className='size-4' />
                    </button>
                </div>
            </div>

            {name && <input hidden name={name} value={selectedKeys.join(',')} readOnly />}

            {props.description && <Description>{props.description}</Description>}
        </Group>
    )
}

const MultiSelectItem = ListBox.Item

MultiSelect.Item = MultiSelectItem
MultiSelect.Tag = Tag.Item

export { MultiSelect, type SelectedKey }

carousel.raw:
'use client'

import React from 'react'

import useEmblaCarousel, { type UseEmblaCarouselType } from 'embla-carousel-react'
import { IconChevronLeft, IconChevronRight } from 'hq-icons'
import {
    ListBox,
    ListBoxItem,
    ListBoxSection,
    type ListBoxItemProps,
    type ListBoxSectionProps
} from 'react-aria-components'

import { Button, type ButtonProps } from './button'
import { cn } from './utils'

type CarouselApi = UseEmblaCarouselType[1]
type UseCarouselParameters = Parameters<typeof useEmblaCarousel>
type CarouselOptions = UseCarouselParameters[0]
type CarouselPlugin = UseCarouselParameters[1]

type CarouselContextProps = {
    carouselRef: ReturnType<typeof useEmblaCarousel>[0]
    api: ReturnType<typeof useEmblaCarousel>[1]
    scrollPrev: () => void
    scrollNext: () => void
    canScrollPrev: boolean
    canScrollNext: boolean
} & CarouselProps

const CarouselContext = React.createContext<CarouselContextProps | null>(null)

const useCarousel = () => {
    const context = React.useContext(CarouselContext)

    if (!context) {
        throw new Error('useCarousel must be used within a <Carousel />')
    }

    return context
}

interface CarouselRootProps {
    CarouselContent?: typeof CarouselContent
    CarouselHandler?: typeof CarouselHandler
    CarouselItem?: typeof CarouselItem
    CarouselButton?: typeof CarouselButton
}

interface CarouselProps extends React.HTMLAttributes<HTMLDivElement>, CarouselRootProps {
    opts?: CarouselOptions
    plugins?: CarouselPlugin
    orientation?: 'horizontal' | 'vertical'
    setApi?: (api: CarouselApi) => void
}

const Carousel = ({
    orientation = 'horizontal',
    opts,
    setApi,
    plugins,
    className,
    children,
    ...props
}: CarouselProps) => {
    const [carouselRef, api] = useEmblaCarousel(
        {
            ...opts,
            axis: orientation === 'horizontal' ? 'x' : 'y'
        },
        plugins
    )
    const [canScrollPrev, setCanScrollPrev] = React.useState(false)
    const [canScrollNext, setCanScrollNext] = React.useState(false)

    const onSelect = React.useCallback((api: CarouselApi) => {
        if (!api) {
            return
        }

        setCanScrollPrev(api.canScrollPrev())
        setCanScrollNext(api.canScrollNext())
    }, [])

    const scrollPrev = React.useCallback(() => {
        api?.scrollPrev()
    }, [api])

    const scrollNext = React.useCallback(() => {
        api?.scrollNext()
    }, [api])

    const handleKeyDown = React.useCallback(
        (event: React.KeyboardEvent<HTMLDivElement>) => {
            if (event.key === 'ArrowLeft') {
                event.preventDefault()
                scrollPrev()
            } else if (event.key === 'ArrowRight') {
                event.preventDefault()
                scrollNext()
            }
        },
        [scrollPrev, scrollNext]
    )

    React.useEffect(() => {
        if (!api || !setApi) {
            return
        }

        setApi(api)
    }, [api, setApi])

    React.useEffect(() => {
        if (!api) {
            return
        }

        onSelect(api)
        api.on('reInit', onSelect)
        api.on('select', onSelect)

        return () => {
            api?.off('select', onSelect)
        }
    }, [api, onSelect])

    return (
        <CarouselContext.Provider
            value={{
                carouselRef,
                api: api,
                opts,
                orientation: orientation || (opts?.axis === 'y' ? 'vertical' : 'horizontal'),
                scrollPrev,
                scrollNext,
                canScrollPrev,
                canScrollNext
            }}
        >
            <div
                onKeyDownCapture={handleKeyDown}
                className={cn('relative', className)}
                role='region'
                aria-roledescription='carousel'
                {...props}
            >
                {children}
            </div>
        </CarouselContext.Provider>
    )
}

const CarouselContent = <T extends object>({ className, ...props }: ListBoxSectionProps<T>) => {
    const { carouselRef, orientation } = useCarousel()

    return (
        <ListBox
            layout={orientation === 'vertical' ? 'stack' : 'grid'}
            aria-label='Slides'
            orientation={orientation}
            ref={carouselRef}
            className='overflow-hidden'
        >
            <ListBoxSection
                className={cn(
                    'flex',
                    orientation === 'horizontal' ? '-ml-4' : '-mt-4 flex-col',
                    className
                )}
                {...props}
            />
        </ListBox>
    )
}

const CarouselItem = ({ className, ...props }: ListBoxItemProps) => {
    const { orientation } = useCarousel()

    return (
        <ListBoxItem
            aria-label={`Slide ${props.id}`}
            aria-roledescription='slide'
            className={cn(
                'min-w-0 shrink-0 focus:outline-none grow-0 basis-full focus-visible:outline-none',
                orientation === 'horizontal' ? 'pl-4' : 'pt-4',
                className
            )}
            {...props}
        />
    )
}

const CarouselHandler = React.forwardRef<HTMLDivElement, React.HTMLAttributes<HTMLDivElement>>(
    ({ className, ...props }, ref) => {
        const { orientation } = useCarousel()
        return (
            <div
                ref={ref}
                className={cn(
                    'mt-6 z-10 relative flex items-center gap-x-2',
                    orientation === 'horizontal' ? 'justify-end' : 'justify-center',
                    className
                )}
                {...props}
            />
        )
    }
)
CarouselHandler.displayName = 'CarouselHandler'

const CarouselButton = ({
    slot,
    className,
    variant = 'outline',
    shape = 'square',
    size = 'icon',
    ...props
}: ButtonProps & { slot: 'previous' | 'next' }) => {
    const { orientation, scrollPrev, canScrollPrev, scrollNext, canScrollNext } = useCarousel()
    const isNext = slot === 'next'
    const canScroll = isNext ? canScrollNext : canScrollPrev
    const scroll = isNext ? scrollNext : scrollPrev
    const Icon = isNext ? IconChevronRight : IconChevronLeft

    return (
        <Button
            aria-label={isNext ? 'Next slide' : 'Previous slide'}
            slot={slot}
            variant={variant}
            size={size}
            shape={shape}
            className={cn(orientation === 'vertical' ? 'rotate-90' : '', className)}
            isDisabled={!canScroll}
            onPress={scroll}
            {...props}
        >
            <Icon className='size-4' />
        </Button>
    )
}

Carousel.Content = CarouselContent
Carousel.Handler = CarouselHandler
Carousel.Item = CarouselItem
Carousel.Button = CarouselButton

export { Carousel, type CarouselApi }

