    classNames,
    hideSearchIndicator = false,
    hideCloseButton = false,
    messageOnEmpty,
    value,
    onValueChange,
    children,
    isBlurred = false,
    ...props
}: CommandProps) => {
    const isDesktop = useMediaQuery('(min-width: 1024px)')

    return (
        <CommandContext.Provider value={{ hideSearchIndicator, hideCloseButton, messageOnEmpty }}>
