        >
            {(values) => (
                <div data-slot='accordion-item-content' className={className}>
                    <DisclosureGroupContext.Provider value={{ hideIndicator, hideBorder }}>
                        {typeof children === 'function' ? children(values) : children}
                    </DisclosureGroupContext.Provider>
