indicator.raw:
'use client'

import type { ComponentPropsWithRef, ReactNode } from 'react'
import { type VariantProps, tv } from 'tailwind-variants'

const indicatorStyle = tv({
    base: 'absolute right-0.5 left-auto z-10 inline-flex w-auto translate-x-1/2 select-none items-center justify-center whitespace-nowrap border-2 py-[3px]',
    variants: {
        variant: {
            primary: '[--bg:var(--color-primary)] [--fg:var(--color-primary-fg)]',
            secondary: '[--bg:var(--color-muted-fg)] [--fg:var(--color-muted)]',
            danger: '[--bg:var(--color-danger)] [--fg:var(--color-danger-fg)]',
            outline: '[--bg:var(--color-bg)] [--fg:var(--color-fg)]'
        },
        position: {
            top: '-translate-y-1/2 top-0.5 bottom-auto',
            bottom: 'top-auto bottom-0.5 translate-y-1/2'
        },
        isInverse: {
            true: 'border-(--bg) bg-(--fg) text-(--bg)',
            false: 'border-(--fg) bg-(--bg) text-(--fg)'
        },
        shape: {
            circle: 'rounded-full',
            square: 'rounded-md'
        },
        size: {
            xs: 'size-3',
            sm: 'size-4',
            md: 'size-5',
            lg: 'size-6'
        },
        isText: {
            true: 'size-auto px-1.5 text-xs has-[svg]:px-[3px] *:[svg]:size-4 *:[svg]:shrink-0'
        }
    },
    defaultVariants: {
        variant: 'primary',
        position: 'top',
        isInverse: false,
        shape: 'circle',
        size: 'sm'
    }
})

interface IndicatorProps extends ComponentPropsWithRef<'div'>, VariantProps<typeof indicatorStyle> {
    text?: ReactNode
}

const Indicator = ({ className, children, ...props }: IndicatorProps) => {
    return (
        <div className='relative inline-block w-fit'>
            {children}
            <span className={indicatorStyle({ className, isText: !!props.text, ...props })}>{props.text}</span>
        </div>
    )
}

export { Indicator }

number-field.raw:
'use client'

import type { Ref } from 'react'

import { IconChevronDown, IconChevronUp, IconMinus, IconPlus } from 'hq-icons'
import {
    Button,
    type ButtonProps,
    Input,
    NumberField as RACNumberField,
    type NumberFieldProps as RACNumberFieldProps,
    composeRenderProps
} from 'react-aria-components'

import { useIsMobile } from '@/lib/hooks'
import { cn } from '@/lib/utils'
import { Description, FieldError, FieldGroup, type FieldProps, Label } from './form'

interface NumberFieldProps extends RACNumberFieldProps, FieldProps {
    placeholder?: string
    ref?: Ref<HTMLDivElement>
}

const NumberField = ({ label, placeholder, description, className, errorMessage, ref, ...props }: NumberFieldProps) => {
    const isMobile = useIsMobile()
    return (
        <RACNumberField
            className={composeRenderProps(className, (className) =>
                cn('group/field flex flex-col gap-y-1.5', className)
            )}
            ref={ref}
            {...props}
        >
            {label && <Label>{label}</Label>}
            <FieldGroup
                isInvalid={props.isInvalid || !!errorMessage}
                isDisabled={props.isDisabled}
                className='overflow-hidden'
            >
                {isMobile && (
                    <Stepper className='border-r' slot='decrement'>
                        <IconMinus />
                    </Stepper>
                )}
                <Input className='text-center tabular-nums sm:text-left' placeholder={placeholder} />
                {isMobile ? (
                    <Stepper className='border-s' slot='increment'>
                        <IconPlus />
                    </Stepper>
                ) : (
                    <div className='flex h-full flex-col divide-y border-s'>
                        <Stepper slot='increment' className='size-5'>
                            <IconChevronUp />
                        </Stepper>
                        <Stepper slot='decrement' className='size-5'>
                            <IconChevronDown />
                        </Stepper>
                    </div>
                )}
            </FieldGroup>
            {description && <Description>{description}</Description>}
            <FieldError>{errorMessage}</FieldError>
        </RACNumberField>
    )
}

interface StepperProps extends ButtonProps {
    slot: 'increment' | 'decrement'
    className?: string
}

const Stepper = ({ slot, className, ...props }: StepperProps) => {
    return (
        <Button
            className={composeRenderProps(className, (className, { isDisabled, isPressed }) =>
                cn(
                    'flex size-12 items-center justify-center text-muted-fg outline-hidden',
                    isPressed &&
                        'bg-primary text-primary-fg group-has-invalid/field:bg-danger group-has-invalid/field:text-danger-fg',
                    isDisabled && 'opacity-50',
                    className
                )
            )}
            slot={slot}
            {...props}
        />
    )
}

export { NumberField }

