    type DateValue,
    DateField as RACDateField,
    type DateFieldProps as RACDateFieldProps,
    DateInput as RACDateInput,
    composeRenderProps
} from 'react-aria-components'

import { cn } from '@/lib/utils'
import { Description, FieldError, FieldGroup, type FieldProps, Label } from './form'

interface DateFieldProps<T extends DateValue> extends RACDateFieldProps<T>, FieldProps {
    prefix?: ReactNode
    suffix?: ReactNode
    ref?: Ref<HTMLDivElement>
}

const DateField = <T extends DateValue>({
    prefix,
    suffix,
    label,
    description,
    errorMessage,
    className,
    ref,
    ...props
}: DateFieldProps<T>) => {
    return (
        <RACDateField
            isInvalid={props.isInvalid || !!errorMessage}
            className={composeRenderProps(className, (className) =>
                cn('group/field flex flex-col gap-y-1.5', className)
            )}
            ref={ref}
            {...props}
        >
            {label && <Label>{label}</Label>}
            <FieldGroup>
                {prefix ? <span data-slot='prefix'>{prefix}</span> : null}
                <DateInput />
                {suffix ? <span data-slot='suffix'>{suffix}</span> : null}
            </FieldGroup>
            {description && <Description>{description}</Description>}
            <FieldError>{errorMessage}</FieldError>
        </RACDateField>
    )
}

const DateInput = ({ className, ...props }: Omit<DateInputProps, 'children'>) => {
    return (
        <RACDateInput
            className={composeRenderProps(className, (className) =>
                cn('bg-transparent p-2 text-base disabled:opacity-50 lg:text-sm', className)
            )}
            {...props}
        >
            {(segment) => (
                <DateSegment
                    segment={segment}
                    className={cn(
                        'inline shrink-0 rounded-sm p-0.5 type-literal:px-0 text-fg tabular-nums tracking-wider caret-transparent outline-0 sm:text-sm sm:uppercase',
                        segment.isPlaceholder && 'text-muted-fg',
                        'focus:font-bold focus:text-primary',
                        'invalid:not-type-literal:text-danger invalid:focus:text-primary-fg'
                    )}
                />
            )}
        </RACDateInput>
    )
}

export { DateField, DateInput }

drawer.raw:
'use client'

import { AnimatePresence, motion } from 'motion/react'
import { use } from 'react'
import type { ButtonProps, DialogProps, DialogTriggerProps, ModalOverlayProps } from 'react-aria-components'
import {
    Button,
    Dialog,
    DialogTrigger,
    ModalOverlay,
    OverlayTriggerStateContext,
    Modal as RACModal
} from 'react-aria-components'

import { cn } from '@/lib/utils'
import { DialogBody, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from './dialog'

const Modal = motion.create(RACModal)
const Overlay = motion.create(ModalOverlay)

const Drawer = (props: DialogTriggerProps) => <DialogTrigger {...props} />

interface DrawerContentProps
    extends Omit<ModalOverlayProps, 'className' | 'children' | 'isDismissable'>,
        Pick<DialogProps, 'aria-label' | 'aria-labelledby' | 'role' | 'children' | 'className'> {
    isFloating?: boolean
    className?: string
    style?: React.CSSProperties
    side?: 'top' | 'bottom' | 'left' | 'right'
    withNotch?: boolean
}

const DrawerContent = ({
    side = 'bottom',
    isFloating = false,
    withNotch = true,
    children,
    className,
    ...props
}: DrawerContentProps) => {
    const state = use(OverlayTriggerStateContext)!

    return (
        <AnimatePresence>
            {(props?.isOpen || state?.isOpen) && (
                <Overlay
                    isDismissable
                    isOpen={props?.isOpen || state?.isOpen}
                    onOpenChange={props?.onOpenChange || state?.setOpen}
                    initial={{ backgroundColor: 'rgba(0, 0, 0, 0)', backdropFilter: 'blur(0px)' }}
                    animate={{
                        backgroundColor: 'rgba(0, 0, 0, 0.5)',
                        backdropFilter: 'blur(4px)'
                    }}
                    exit={{ backgroundColor: 'rgba(0, 0, 0, 0)', backdropFilter: 'blur(0px)' }}
                    className='fixed inset-0 z-50 will-change-auto [--visual-viewport-vertical-padding:32px]'
                >
                    {({ state }) => (
                        <Modal
                            className={cn(
                                'fixed max-h-full touch-none overflow-hidden bg-bg align-middle text-fg shadow-sm will-change-transform',
                                side === 'top' &&
                                    `${
                                        isFloating
                                            ? 'inset-x-2 top-2 rounded-lg border'
                                            : 'inset-x-0 top-0 rounded-b-2xl border-b'
                                    }`,
                                side === 'right' &&
                                    `w-full max-w-xs overflow-y-auto **:[[slot=header]]:text-left ${
                                        isFloating
                                            ? 'inset-y-2 right-2 rounded-lg border'
                                            : 'inset-y-0 right-0 h-auto border-l'
                                    }`,
                                side === 'bottom' &&
                                    `${
                                        isFloating
                                            ? 'inset-x-2 bottom-2 rounded-lg border'
                                            : 'inset-x-0 bottom-0 rounded-t-2xl border-t'
                                    }`,
                                side === 'left' &&
                                    `w-full max-w-xs overflow-y-auto **:[[slot=header]]:text-left ${
                                        isFloating
                                            ? 'inset-y-2 left-2 rounded-lg border'
                                            : 'inset-y-0 left-0 h-auto border-r'
                                    }`,
                                className
                            )}
                            initial={{
                                x: side === 'left' ? '-100%' : side === 'right' ? '100%' : 0,
                                y: side === 'top' ? '-100%' : side === 'bottom' ? '100%' : 0
                            }}
                            animate={{ x: 0, y: 0 }}
                            exit={{
                                x: side === 'left' ? '-100%' : side === 'right' ? '100%' : 0,
                                y: side === 'top' ? '-100%' : side === 'bottom' ? '100%' : 0
                            }}
                            drag={side === 'left' || side === 'right' ? 'x' : 'y'}
                            whileDrag={{ cursor: 'grabbing' }}
                            dragConstraints={{ top: 0, bottom: 0, left: 0, right: 0 }}
                            dragTransition={{ bounceStiffness: 600, bounceDamping: 20 }}
                            transition={{ duration: 0.15, ease: 'easeInOut' }}
                            onDragEnd={(_, { offset, velocity }) => {
                                if (side === 'bottom' && (velocity.y > 150 || offset.y > screen.height * 0.25)) {
                                    state.close()
                                }
                                if (side === 'top' && (velocity.y < -150 || offset.y < screen.height * 0.25)) {
                                    state.close()
                                }
                                if (side === 'left' && velocity.x < -150) {
                                    state.close()
                                }
                                if (side === 'right' && velocity.x > 150) {
                                    state.close()
                                }
                            }}
                            dragElastic={{
                                top: side === 'top' ? 1 : 0,
                                bottom: side === 'bottom' ? 1 : 0,
                                left: side === 'left' ? 1 : 0,
                                right: side === 'right' ? 1 : 0
                            }}
                            dragPropagation
                        >
                            <Dialog
                                aria-label='Drawer'
                                role='dialog'
                                className={cn(
                                    'relative flex flex-col overflow-hidden outline-hidden will-change-auto',
                                    side === 'top' || side === 'bottom'
                                        ? 'mx-auto max-h-[calc(var(--visual-viewport-height)-var(--visual-viewport-vertical-padding))] max-w-lg'
                                        : 'h-full'
                                )}
                            >
                                {withNotch && side === 'bottom' && (
                                    <div className='notch sticky top-0 mx-auto mt-2.5 h-1.5 w-10 shrink-0 touch-pan-y rounded-full bg-fg/20' />
                                )}
                                {children as React.ReactNode}
                                {withNotch && side === 'top' && (
                                    <div className='notch sticky bottom-0 mx-auto mb-2.5 h-1.5 w-10 shrink-0 touch-pan-y rounded-full bg-fg/20' />
                                )}
                            </Dialog>
                        </Modal>
                    )}
                </Overlay>
            )}
        </AnimatePresence>
    )
}

Drawer.Trigger = (props: ButtonProps) => <Button {...props} />

Drawer.Content = DrawerContent

Drawer.Header = DialogHeader
Drawer.Title = DialogTitle
Drawer.Description = DialogDescription
Drawer.Body = DialogBody
Drawer.Footer = DialogFooter

export { Drawer }
export type { DrawerContentProps }

keyboard.raw:
'use client'

import { Keyboard as RACKeyboard } from 'react-aria-components'

import { cn } from '@/lib/utils'
import type { ComponentPropsWithRef } from 'react'

type ShortcutKeyResult = {
    symbol: string
    readable: string
}

const shortcutKeyMap: Record<string, ShortcutKeyResult> = {
    mod: { symbol: '', readable: 'Command/Control' },
    win: { symbol: '', readable: 'Win' },
    meta: { symbol: '', readable: 'Meta' },
    alt: { symbol: '', readable: 'Option/Alt' },
    shift: { symbol: '', readable: 'Shift' },
    space: { symbol: '', readable: 'Space' },
    tab: { symbol: '', readable: 'Tab' },
    delete: { symbol: '', readable: 'Delete' },
    capslock: { symbol: '', readable: 'Caps Lock' },
    up: { symbol: '', readable: 'Up' },
    right: { symbol: '', readable: 'Right' },
    down: { symbol: '', readable: 'Down' },
    left: { symbol: '', readable: 'Left' },
    pageup: { symbol: '', readable: 'Page Up' },
    pagedown: { symbol: '', readable: 'Page Down' },
    home: { symbol: '', readable: 'Home' },
    end: { symbol: '', readable: 'End' },
    enter: { symbol: '', readable: 'Enter' },
    esc: { symbol: '', readable: 'Escape' },
    fn: { symbol: 'Fn', readable: 'Function' }
}

const getShortcutKey = (key: string): ShortcutKeyResult =>
    shortcutKeyMap[key.toLowerCase()] || { symbol: key, readable: key }

const Keyboard = ({
    keys,
    className,
    ref,
    ...props
}: ComponentPropsWithRef<typeof RACKeyboard> & { keys: string[] }) => {
    if (!keys) return null
    return (
        <RACKeyboard
            ref={ref}
            className={cn(
                '!font-sans hidden w-fit space-x-0.5 justify-self-end rounded-sm border px-1.5 py-0.5 text-current/70 text-xs shadow group-hover:text-current group-focus:text-current group-disabled:opacity-50 sm:inline-flex rtl:space-x-reverse',
                className
            )}
            {...props}
        >
            {keys.map((key) => (
                <abbr className='capitalize' key={key} aria-label={getShortcutKey(key).readable}>
                    {getShortcutKey(key).symbol}
                </abbr>
            ))}
        </RACKeyboard>
    )
}

export { Keyboard }

meter.raw:
'use client'

import { IconCircleAlert } from 'hq-icons'
import { Meter as RACMeter, type MeterProps as RACMeterProps } from 'react-aria-components'

import { cn } from '@/lib/utils'
import { Label } from './form'

interface MeterProps extends RACMeterProps {
    label?: string
}

const Meter = ({ label, className, ...props }: MeterProps) => {
    return (
        <RACMeter {...props} className={cn('flex flex-col gap-y-1.5', className)}>
            {({ percentage, valueText }) => (
                <>
                    <div className='flex w-full justify-between gap-2'>
                        {label && <Label>{label}</Label>}
                        <span
                            className={`flex items-center text-sm tabular-nums ${percentage >= 80 ? 'text-danger' : 'text-muted-fg'}`}
                        >
                            {percentage >= 80 && (
                                <IconCircleAlert
                                    aria-label='Alert'
                                    className='mr-1 inline size-4 fill-invalid text-danger'
                                />
                            )}
                            {valueText}
                        </span>
                    </div>
                    <div className='relative h-2 min-w-64 rounded-lg bg-muted outline-hidden'>
                        <div
                            className='absolute top-0 left-0 h-full rounded-full transition-[width,background-color]'
                            style={{
                                width: `${percentage}%`,
                                backgroundColor: getColor(percentage)
                            }}
                        />
                    </div>
                </>
            )}
        </RACMeter>
    )
}

const getColor = (percentage: number) => {
    if (percentage < 25) {
        return 'blue'
    }
    if (percentage < 50) {
        return 'green'
    }
    if (percentage < 75) {
        return 'orange'
    }
    return 'var(--danger)'
}

export { Meter }

tag-group.raw:
'use client'

import { type ReactNode, type RefObject, createContext, useContext } from 'react'

import { IconX } from 'hq-icons'
import type { TagGroupProps as RACTagGroupProps, TagListProps, TagProps } from 'react-aria-components'
import { Button, Tag as RACTag, TagGroup as RACTagGroup, TagList, composeRenderProps } from 'react-aria-components'
import { tv } from 'tailwind-variants'

import { cn } from '@/lib/utils'
import { badgeStyle } from './badge'
import { Description, Label } from './form'

type TagGroupStyles = {
    variant: keyof Omit<typeof badgeStyle.variants.variant, 'outline'>
}

const TagGroupContext = createContext<TagGroupStyles>({
    variant: 'primary'
})

interface TagGroupProps extends Omit<RACTagGroupProps, 'children'> {
    variant?: TagGroupStyles['variant']
    errorMessage?: string
    label?: string
    description?: string
    ref?: RefObject<HTMLDivElement>
    className?: string
}

const TagGroup = <T extends object>({
    label,
    description,
    items,
    className,
    ref,
    children,
    renderEmptyState,
    ...props
}: TagGroupProps & TagListProps<T>) => {
    return (
        <RACTagGroup ref={ref} className={cn('flex flex-col flex-wrap', className)} {...props}>
            {label && <Label className='mb-1'>{label}</Label>}
            <TagGroupContext.Provider
                value={{
                    variant: props.variant || 'primary'
                }}
            >
                <TagList
                    items={items}
                    renderEmptyState={renderEmptyState}
                    className='flex flex-wrap gap-2 *:data-badge:bg-primary'
                >
                    {children}
                </TagList>
            </TagGroupContext.Provider>
            {description && <Description>{description}</Description>}
        </RACTagGroup>
    )
}

const tagStyle = tv({
    extend: badgeStyle,
    variants: {
        isSelected: { false: '!bg-(--bg)/10 !text-(--bg)' },
        isFocusVisible: {
            true: 'outline-0 ring-2 ring-primary ring-offset-2'
        },
        isDisabled: { true: 'cursor-default opacity-50', false: 'cursor-pointer' },
        allowsRemoving: { true: 'pr-1' }
    }
})

const Tag = (props: TagProps) => {
    const textValue = typeof props.children === 'string' ? props.children : undefined
    const groupContext = useContext(TagGroupContext)

    return (
        <RACTag
            textValue={textValue}
            {...props}
            className={composeRenderProps(props.className, (className, renderProps) =>
                tagStyle({
                    ...renderProps,
                    variant: groupContext.variant,
                    className
                })
            )}
        >
            {({ allowsRemoving }) => {
                return (
                    <>
                        {props.children as ReactNode}
                        {allowsRemoving && (
                            <Button
                                slot='remove'
                                className='-mr-0.5 grid size-3.5 place-content-center rounded-lg focus:outline-none focus-visible:ring-1 focus-visible:ring-primary'
                            >
                                <IconX className='size-3 shrink-0' />
                            </Button>
                        )}
                    </>
                )
            }}
        </RACTag>
    )
}

Tag.Group = TagGroup
Tag.List = TagList

export { Tag }
export type { TagGroupProps, TagGroupStyles }

