'use client'

import { useEffect, useState } from 'react'

import { IconLoader, IconSearch } from 'hq-icons'
import type { AutocompleteProps, ButtonProps, MenuProps, ModalOverlayProps } from 'react-aria-components'
import { Autocomplete, Button, Group, Input, Menu, TextField } from 'react-aria-components'

import { cn, fuzzyMatch } from '@/lib/utils'
import { Keyboard } from './keyboard'
import { MenuItem, MenuLabel, MenuSection, MenuSeparator } from './menu'
import { ModalContent } from './modal'

interface CommandProps<T> extends MenuProps<T>, Pick<AutocompleteProps, 'inputValue' | 'onInputChange'> {
    isPending?: boolean
}

const Command = <T extends object>({ ...props }: CommandProps<T>) => {
    return (
        <div data-command className={cn('rounded-lg border', props.className)}>
            <Autocomplete filter={fuzzyMatch} inputValue={props.inputValue} onInputChange={props.onInputChange}>
                <TextField autoFocus className='border-b p-1' aria-label='Search'>
                    <Group className='flex items-center px-2'>
                        {props.isPending ? (
                            <IconLoader className='size-4 shrink-0 animate-spin text-muted-fg' />
                        ) : (
                            <IconSearch className='size-4 shrink-0 text-muted-fg' />
                        )}
                        <Input className='w-full p-2 outline-hidden' placeholder='Search...' />
                    </Group>
                </TextField>
                <Menu
                    renderEmptyState={() => (
                        <div className='col-span-full p-4 text-center text-muted-fg'>No results found</div>
                    )}
                    className='grid w-full grid-cols-[auto_1fr_auto] gap-y-1 overflow-y-auto p-2 outline-hidden sm:max-h-[30rem]'
                    {...props}
                />
            </Autocomplete>
        </div>
    )
}

interface CommandModalProps<T> extends CommandProps<T>, Pick<ModalOverlayProps, 'isOpen' | 'onOpenChange'> {
    shortcut?: {
        modifiers?: 'alt' | 'mod' | 'mod+alt' | 'mod+shift' | 'mod+alt+shift' | 'alt+shift'
        key: string
    }
}

const CommandModal = <T extends object>({ shortcut, ...props }: CommandModalProps<T>) => {
    const [shortcutOpen, setShortcutOpen] = useState<boolean>(false)

    useEffect(() => {
        if (!shortcut) return
        const handleKeyDown = (e: KeyboardEvent) => {
            if (
                e.key === shortcut?.key &&
                (shortcut.modifiers === 'alt'
                    ? e.altKey
                    : shortcut.modifiers === 'mod+alt'
                      ? e.altKey && (e.ctrlKey || e.metaKey)
                      : shortcut.modifiers === 'mod+shift'
                        ? e.shiftKey && (e.ctrlKey || e.metaKey)
                        : shortcut.modifiers === 'mod+alt+shift'
                          ? e.altKey && e.shiftKey && (e.ctrlKey || e.metaKey)
                          : shortcut.modifiers === 'alt+shift'
                            ? e.altKey && e.shiftKey
                            : e.ctrlKey || e.metaKey)
            ) {
                e.preventDefault()
                if (props.onOpenChange) {
                    return props.onOpenChange(!props.isOpen)
                }
                setShortcutOpen(!shortcutOpen)
            } else if (e.key === 'Escape') {
                e.preventDefault()
                if (props.onOpenChange) {
                    return props.onOpenChange(false)
                }
                setShortcutOpen(false)
            }
        }

        document.addEventListener('keydown', handleKeyDown)
        return () => document.removeEventListener('keydown', handleKeyDown)
    })

    return (
        <ModalContent
            isOpen={props.isOpen || shortcutOpen}
            onOpenChange={props.onOpenChange || setShortcutOpen}
            aria-label='Commands'
            className='h-[70dvh] **:data-command:border-0 sm:h-auto sm:min-h-0'
        >
            <Command {...props} />
        </ModalContent>
    )
}

const CommandTrigger = (props: ButtonProps) => <Button {...props} />

Command.Modal = CommandModal

Command.Trigger = CommandTrigger

Command.Item = MenuItem
Command.Label = MenuLabel
Command.Section = MenuSection
Command.Separator = MenuSeparator

Command.Shortcut = Keyboard

export { Command }

avatar.raw:
'use client'

import { type ComponentProps, useEffect, useState } from 'react'

import { Collection, type CollectionProps } from '@react-aria/collections'
import { IconUser } from 'hq-icons'
import { type VariantProps, tv } from 'tailwind-variants'

import { cn } from '@/lib/utils'

interface AvatarGroupProps<T extends object> extends CollectionProps<T> {
    className?: string
}

const getInitials = (name: string): string =>
    name.split(' ').slice(0, 2).length > 1
        ? name
              .split(' ')
              .slice(0, 2)
              .map((part) => part.charAt(0))
              .join('')
        : name.split('').slice(0, 2).join('')

const AvatarGroup = <T extends object>({ className, ...props }: AvatarGroupProps<T>) => {
    return (
        <div
            className={cn(
                '-space-x-2 flex items-center justify-center **:data-avatar:ring **:data-avatar:ring-border',
                '**:data-avatar:transition-transform **:data-avatar:hover:z-30 **:data-avatar:hover:scale-110',
                className
            )}
        >
            <Collection {...props} />
        </div>
    )
}

const avatarStyle = tv({
    base: [
        'inline-grid shrink-0 items-center justify-center align-middle *:col-start-1 *:row-start-1',
        '-outline-offset-1 text-center outline-1 outline-fg/20'
    ],
    variants: {
        shape: {
            square: 'rounded-lg *:rounded-lg',
            circle: 'rounded-full *:rounded-full'
        },
        size: {
            xs: 'size-6',
            sm: 'size-7',
            md: 'size-8',
            lg: 'size-9',
            xl: 'size-12',
            '2xl': 'size-14',
            '3xl': 'size-16',
            '4xl': 'size-20',
            '5xl': 'size-24'
        }
    },
    defaultVariants: {
        shape: 'circle',
        size: 'lg'
    }
})

interface AvatarProps extends VariantProps<typeof avatarStyle> {
    src?: string | undefined
    alt?: string
    className?: string
}

const Avatar = ({ src, shape, size, alt, className, ...props }: AvatarProps & ComponentProps<'img'>) => {
    const [error, setError] = useState(!src)

    function handleError() {
        setError(true)
    }

    useEffect(() => {
        setError(!src)
    }, [src])

    if (error) {
        return (
            <span data-avatar={true} className={avatarStyle({ shape, size, className })}>
                <FallbackImage alt={alt} />
            </span>
        )
    }
    return (
        // biome-ignore lint/a11y/useAltText: <explanation>
        <img
            src={src}
            alt={alt || 'Avatar'}
            onError={handleError}
            data-avatar
            className={avatarStyle({ shape, size, className })}
            {...props}
        />
    )
}

const FallbackImage = ({ alt }: { alt: string | undefined }) => {
    return alt ? (
        <svg
            className='size-full select-none bg-bg fill-current p-[5%] font-medium text-[48px] uppercase'
            viewBox='0 0 100 100'
            aria-hidden='true'
        >
            {alt && <title>{alt}</title>}
            <text x='50%' y='50%' alignmentBaseline='middle' dominantBaseline='middle' textAnchor='middle' dy='.125em'>
                {getInitials(alt)}
            </text>
        </svg>
    ) : (
        <IconUser className='size-full place-self-center bg-bg p-1.5' />
    )
}

export { Avatar, AvatarGroup }
export type { AvatarProps }

toggle.raw:
'use client'

import { type RefObject, createContext, use } from 'react'

import type { ToggleButtonGroupProps, ToggleButtonProps } from 'react-aria-components'
import { ToggleButton, ToggleButtonGroup, composeRenderProps } from 'react-aria-components'
import type { VariantProps } from 'tailwind-variants'
import { tv } from 'tailwind-variants'

import { cn } from '@/lib/utils'

type ToggleGroupContextProps = {
    isDisabled?: boolean
    gap?: 0 | 1 | 2 | 3 | 4
    variant?: 'solid' | 'outline' | 'ghost'
    orientation?: 'horizontal' | 'vertical'
    size?: 'xs' | 'sm' | 'md' | 'lg'
    icon?: boolean
}

const ToggleGroupContext = createContext<ToggleGroupContextProps>({
    gap: 0,
    variant: 'outline',
    orientation: 'horizontal',
    size: 'md',
    icon: false
})

interface ToggleGroupProps extends ToggleButtonGroupProps, ToggleGroupContextProps {
    ref?: RefObject<HTMLDivElement>
}

const toggleGroupStyles = tv({
    variants: {
        orientation: {
            horizontal: 'flex flex-row [-ms-overflow-style:none] [scrollbar-width:none] [&::-webkit-scrollbar]:hidden',
            vertical: 'grid items-start'
        },
        gap: {
            0: 'gap-0 rounded-md **:rounded-none',
            1: 'gap-1',
            2: 'gap-2',
            3: 'gap-3',
            4: 'gap-4'
        }
    },
    defaultVariants: {
        orientation: 'horizontal',
        gap: 0
    },
    compoundVariants: [
        {
            gap: 0,
            orientation: 'vertical',
            className: '*:[button]:-mt-px *:[button]:first:rounded-t-md *:[button]:last:rounded-b-md'
        },
        {
            gap: 0,
            orientation: 'horizontal',
            className: '*:-mr-px *:first:*:rounded-s-md *:first:rounded-s-md *:last:*:rounded-e-md *:last:rounded-e-md'
        }
    ]
})

const ToggleGroup = ({
    className,
    ref,
    variant,
    gap,
    icon,
    size = 'md',
    orientation = 'horizontal',
    ...props
}: ToggleGroupProps) => {
    return (
        <ToggleGroupContext.Provider value={{ variant, gap, orientation, size, icon, isDisabled: props.isDisabled }}>
            <ToggleButtonGroup
                ref={ref}
                orientation={orientation}
                className={composeRenderProps(className, (className) =>
                    cn(
                        toggleGroupStyles({
                            gap,
                            orientation
                        }),
                        className
                    )
                )}
                {...props}
            />
        </ToggleGroupContext.Provider>
    )
}

const toggleStyles = tv({
    base: [
        'cursor-pointer items-center gap-x-2 rounded-md border outline-hidden backdrop-blur-2xl transition sm:text-sm',
        'ring-ring focus-visible:border-primary/70 focus-visible:ring-2',
        'disabled:cursor-default disabled:opacity-50'
    ],
    variants: {
        variant: {
            solid: 'bg-bg/80 pressed:bg-primary/15 selected:bg-primary selected:text-primary-fg hover:bg-primary/10',
            ghost: 'border-transparent bg-transparent pressed:bg-muted/50 text-fg hover:bg-muted/40',
