'use client'

import { type ReactNode, type Ref, useState } from 'react'

import { IconEye, IconEyeClosed, IconLoaderCircle } from 'hq-icons'
import {
    Button,
    Input,
    TextField as RACTextField,
    type TextFieldProps as RACTextFieldProps,
    composeRenderProps
} from 'react-aria-components'

import { cn } from '@/lib/utils'
import type { FieldProps } from './form'
import { Description, FieldError, FieldGroup, Label } from './form'

interface TextFieldProps extends RACTextFieldProps, FieldProps {
    prefix?: ReactNode
    suffix?: ReactNode
    isPending?: boolean
    placeholder?: string
    ref?: Ref<HTMLInputElement>
}

const TextField = ({
    label,
    placeholder,
    description,
    errorMessage,
    className,
    type,
    ref,
    ...props
}: TextFieldProps) => {
    const [masked, setMasked] = useState<boolean>(type === 'password')
    const inputType = type === 'password' ? (masked ? 'password' : 'text') : type

    return (
        <RACTextField
            type={inputType}
            className={composeRenderProps(className, (className) =>
                cn('group/field flex flex-col gap-y-1.5', className)
            )}
            ref={ref}
            {...props}
        >
            {(values) => (
                <>
                    {label && <Label>{label}</Label>}
                    <FieldGroup
                        isInvalid={!!errorMessage || values.isInvalid}
                        isDisabled={values.isDisabled}
                        data-loading={props.isPending ? 'true' : undefined}
                    >
                        {props.prefix ? (
                            <span data-prefix className='ml-2 text-muted-fg has-[button]:ml-0'>
                                {props.prefix}
                            </span>
                        ) : null}
                        <Input placeholder={placeholder} />
                        {type === 'password' ? (
                            <Button
                                type='button'
                                aria-label='Toggle visibility'
                                onPress={() => setMasked((e) => !e)}
                                className='mr-2 inline-flex items-center justify-center text-muted-fg outline-hidden focus-visible:text-primary'
                            >
                                <IconEye
                                    aria-hidden
                                    className={cn('size-4 scale-0 transition-transform', masked && 'scale-100')}
                                />
                                <IconEyeClosed
                                    aria-hidden
                                    className={cn(
                                        'absolute size-4 scale-0 transition-transform',
                                        !masked && 'scale-100'
                                    )}
                                />
                            </Button>
                        ) : props.isPending ? (
                            <IconLoaderCircle className='mr-2 size-3.5 animate-spin text-muted-fg' data-suffix />
                        ) : props.suffix ? (
                            <span data-suffix className='mr-2 text-muted-fg has-[button]:mr-0'>
                                {props.suffix}
                            </span>
                        ) : null}
                    </FieldGroup>
                    {description && <Description>{description}</Description>}
                    <FieldError>{errorMessage}</FieldError>
                </>
            )}
        </RACTextField>
    )
}

export { TextField }

collections.raw:
'use client'

import { DropIndicator, isTextDropItem, useDragAndDrop } from 'react-aria-components'
import type { DragAndDropOptions, DropOperation } from 'react-aria-components'
import { type ListData, type ListOptions, useListData as RACListData } from 'react-stately'

const useList = <T extends object>(options: ListOptions<T>) => RACListData(options)
const useDND = <T extends object>({
    list,
    operation = 'move'
}: {
    list: ListData<T & { name: string }>
    operation?: DropOperation
    DragAndDropOptions?: DragAndDropOptions
}) =>
    useDragAndDrop({
        getItems: (items) =>
            [...items].map((item) => ({
                'text/plain': list.getItem(item)?.name ?? '',
                'custom-app-type': JSON.stringify(list.getItem(item as string)!)
            })),
        onReorder: (e) => {
            if (e.target.dropPosition === 'before') {
                list.moveBefore(e.target.key, e.keys)
            } else if (e.target.dropPosition === 'after') {
                list.moveAfter(e.target.key, e.keys)
            }
        },
        acceptedDragTypes: ['custom-app-type'],
        getDropOperation: () => operation,
        onInsert: async (e) => {
            const processedItems = await Promise.all(
                e.items.filter(isTextDropItem).map(async (item) => JSON.parse(await item.getText('custom-app-type')))
            )
            if (e.target.dropPosition === 'before') {
                list.insertBefore(e.target.key, ...processedItems)
            } else if (e.target.dropPosition === 'after') {
                list.insertAfter(e.target.key, ...processedItems)
            }
        },
        onRootDrop: async (e) => {
            const processedItems = await Promise.all(
                e.items.filter(isTextDropItem).map(async (item) => JSON.parse(await item.getText('custom-app-type')))
            )
            list.append(...processedItems)
        },
        onDragEnd: (e) => {
            if (e.dropOperation === 'move' && !e.isInternal) {
                list.remove(...e.keys)
            }
        },
        renderDropIndicator: (e) => (
            <DropIndicator
                target={e}
                className={({ isDropTarget }) =>
                    isDropTarget ? 'col-span-full rounded-md outline outline-primary' : ''
                }
            />
        ),
        renderDragPreview: (items) => (
            <ul className='w-full rounded-md bg-primary p-2 text-primary-fg'>
                {items.map((item, i) => (
                    <li className='text-sm' key={i}>
                        {i + 1}. {item['text/plain']}
                    </li>
                ))}
            </ul>
        )
    })

export { useDND, useList }

