button.raw:
'use client'

import type { Ref } from 'react'

import { IconLoaderCircle } from 'hq-icons'
import { Button as RACButton, composeRenderProps } from 'react-aria-components'
import type { ButtonProps as RACButtonProps } from 'react-aria-components'
import { type VariantProps, tv } from 'tailwind-variants'

const buttonStyle = tv({
    base: [
        'inline-flex items-center justify-center gap-2 whitespace-nowrap font-medium text-sm outline-hidden transition-[box-shadow,opacity,color]',
        '**:[svg]:pointer-events-none **:[svg]:shrink-0',
        'focus-visible:border-primary focus-visible:ring-4 focus-visible:ring-ring',
        'cursor-pointer disabled:pointer-events-none disabled:opacity-50'
    ],
    variants: {
        variant: {
            primary: 'bg-primary pressed:bg-primary/95 text-primary-fg shadow-xs hover:bg-primary/90',
            danger: 'bg-danger text-white shadow-xs hover:bg-danger/90 focus-visible:ring-danger/25',
            outline: 'border bg-bg pressed:bg-muted/50 text-fg shadow-xs hover:bg-muted/40',
            secondary: 'bg-secondary text-secondary-fg shadow-xs hover:bg-secondary/80',
            ghost: 'pressed:bg-muted/50 hover:bg-muted/40'
        },
        size: {
            xs: 'gap-x-1.5 px-2 text-xs [--height:theme(spacing.6)] [--width:auto] **:[svg]:size-3.5',
            sm: 'gap-x-1.5 px-3 text-sm [--height:theme(spacing.8)] [--width:auto] **:[svg]:size-4',
            md: 'gap-x-2 px-4 text-sm [--height:theme(spacing.9)] [--width:auto] **:[svg]:size-4',
            lg: 'gap-x-2.5 px-5 text-base [--height:theme(spacing.10)] [--width:auto] **:[svg]:size-5'
        },
        icon: {
            true: 'h-(--height) w-(--height) shrink-0 px-0',
            false: 'h-(--height) w-(--width)'
        },
        shape: {
            square: 'rounded-md',
            circle: 'rounded-full'
        },
        isPending: {
            true: 'pointer-events-none cursor-default opacity-50 *:data-[slot=icon]:hidden'
        }
    },
    defaultVariants: {
        variant: 'primary',
        size: 'md',
        shape: 'square'
    }
})

interface ButtonProps extends RACButtonProps, VariantProps<typeof buttonStyle> {
    ref?: Ref<HTMLButtonElement>
    isPending?: boolean
}

const Button = ({ className, children, variant, size, icon, isPending, ...props }: ButtonProps) => (
    <RACButton
        className={composeRenderProps(className, (className) =>
            buttonStyle({ variant, size, icon, isPending, className })
        )}
        {...props}
    >
        {(values) => (
            <>
                {isPending && <IconLoaderCircle data-slot='loader' className='animate-spin' />}
                {typeof children === 'function' ? children(values) : children}
            </>
        )}
    </RACButton>
)

export { Button, buttonStyle }

table.raw:
'use client'

import { IconChevronDown, IconMenu } from 'hq-icons'
import { type ReactNode, type Ref, createContext, useContext } from 'react'
import type {
    CellProps,
    ColumnProps,
    ColumnResizerProps,
    TableHeaderProps as HeaderProps,
    TableProps as RACTableProps,
    RowProps,
    TableBodyProps
} from 'react-aria-components'
import {
    Button,
    Cell,
    Collection,
    Column,
    ColumnResizer as RACColumnResizer,
    Table as RACTable,
    TableBody as RACTableBody,
    TableHeader as RACTableHeader,
    ResizableTableContainer,
    Row,
    composeRenderProps,
    useTableOptions
} from 'react-aria-components'

import { cn } from '@/lib/utils'
import { Checkbox } from './checkbox'

interface TableProps extends RACTableProps {
    className?: string
    allowResize?: boolean
}

const TableContext = createContext<TableProps>({
    allowResize: false
})

const useTableContext = () => useContext(TableContext)

const Table = ({ className, ...props }: TableProps) => {
    const renderTable = (
        <RACTable
            className={cn('w-full min-w-full caption-bottom border-spacing-0 text-sm outline-hidden', className)}
            {...props}
        />
    )
    return (
        <TableContext.Provider value={props}>
            <div slot='table' className='relative w-full overflow-auto rounded-lg border'>
                {props.allowResize ? (
                    <ResizableTableContainer className='overflow-auto'>{renderTable}</ResizableTableContainer>
                ) : (
                    renderTable
                )}
            </div>
        </TableContext.Provider>
    )
}

interface TableHeaderProps<T extends object> extends HeaderProps<T> {
    className?: string
    ref?: Ref<HTMLTableSectionElement>
}

const TableHeader = <T extends object>({ children, ref, className, columns, ...props }: TableHeaderProps<T>) => {
    const { selectionBehavior, selectionMode, allowsDragging } = useTableOptions()
    return (
        <RACTableHeader ref={ref} className={cn('border-b bg-primary/10 text-fg', className)} {...props}>
            {allowsDragging && <Column className='w-0' />}
            {selectionBehavior === 'toggle' && (
                <Column className='w-0 pl-4'>{selectionMode === 'multiple' && <Checkbox slot='selection' />}</Column>
            )}
            <Collection items={columns}>{children}</Collection>
        </RACTableHeader>
    )
}

const ColumnResizer = ({ className, ...props }: ColumnResizerProps) => (
    <RACColumnResizer
        {...props}
        className={composeRenderProps(className, (className, { isResizing, resizableDirection }) =>
            cn(
                'absolute top-0 right-0 bottom-0 w-px touch-none px-1.5',
                resizableDirection === 'left' && 'cursor-w-resize',
                resizableDirection === 'right' && 'cursor-e-resize',
                resizableDirection === 'both' && 'cursor-ew-resize',
                isResizing ? '*:bg-primary' : '*:bg-muted',
                className
            )
        )}
    >
        <div className='h-full w-px' />
    </RACColumnResizer>
)

const TableBody = <T extends object>(props: TableBodyProps<T>) => (
    <RACTableBody
        {...props}
        className={cn(
            '**:data-drop-target:py-2 **:data-drop-target:outline **:data-drop-target:outline-primary',
            props.className
        )}
    />
)

interface TableCellProps extends CellProps {
    className?: string
}

const TableCell = ({ children, className, ...props }: TableCellProps) => {
    const { allowResize } = useTableContext()
    return (
        <Cell
            {...props}
            className={composeRenderProps(className, (className) =>
                cn(
                    'group whitespace-nowrap px-3 py-3 outline-hidden',
                    'first:pl-6 last:pr-6',
                    allowResize && 'overflow-hidden truncate',
                    className
                )
            )}
        >
            {children}
        </Cell>
    )
}

interface TableColumnProps extends ColumnProps {
    className?: string
    isResizable?: boolean
}

const TableColumn = ({ isResizable = false, className, ...props }: TableColumnProps) => {
    return (
        <Column
            {...props}
            className={composeRenderProps(className, (className, { isResizing, allowsSorting }) =>
                cn(
                    'relative whitespace-nowrap p-3 text-left font-medium outline-hidden first:pl-6 last:pr-6 **:has-[slot=selection]:pr-0',
                    isResizable && 'overflow-hidden truncate',
                    isResizing && 'cursor-grabbing',
                    allowsSorting && 'cursor-pointer',
                    className
                )
            )}
        >
            {({ allowsSorting, sortDirection, isHovered }) => (
                <div className='flex items-center gap-2'>
                    <>
                        {props.children as ReactNode}
                        {allowsSorting && (
                            <IconChevronDown
                                className={cn(
                                    'size-3.5 shrink-0 text-muted-fg transition-transform',
                                    sortDirection === 'ascending' ? '-rotate-180' : '',
                                    isHovered && 'text-primary'
                                )}
                            />
                        )}
                        {isResizable && <ColumnResizer />}
                    </>
                </div>
            )}
        </Column>
    )
}

interface TableRowProps<T extends object> extends RowProps<T> {
    className?: string
    ref?: Ref<HTMLTableRowElement>
}

const TableRow = <T extends object>({ children, className, columns, id, ref, ...props }: TableRowProps<T>) => {
    const { selectionBehavior, allowsDragging } = useTableOptions()
    return (
        <Row
            ref={ref}
            id={id}
            className={composeRenderProps(
                className,
                (className, { isSelected, isHovered, isFocusVisible, isDisabled, isFocusVisibleWithin }) =>
                    cn(
                        'group relative cursor-default not-last:border-b',
                        'href' in props && 'cursor-pointer',
                        isSelected && 'bg-primary/15 text-primary',
                        isHovered && 'bg-primary/10 text-primary',
                        {
                            'bg-primary/15 text-primary outline outline-primary': isFocusVisible || isFocusVisibleWithin
                        },
                        isDisabled && 'pointer-events-none opacity-50',
                        className
                    )
            )}
            {...props}
        >
            {allowsDragging && (
                <Cell className='cursor-grab pr-0 outline-primary data-dragging:cursor-grabbing'>
                    <Button className='py-1.5 pl-3.5' slot='drag'>
                        <IconMenu />
                    </Button>
                </Cell>
            )}
            {selectionBehavior === 'toggle' && (
                <Cell className='pl-4'>
                    <span
                        aria-hidden
                        className='absolute inset-y-0 left-0 hidden h-full w-0.5 bg-primary/70 group-selected:block'
                    />
                    <Checkbox slot='selection' />
                </Cell>
            )}
            <Collection items={columns}>{children}</Collection>
        </Row>
    )
}

Table.Body = TableBody
Table.Cell = TableCell
Table.Column = TableColumn
Table.Header = TableHeader
Table.Row = TableRow

export { Table }
export type { TableBodyProps, TableCellProps, TableColumnProps, TableProps, TableRowProps }

carousel.raw:
'use client'

import {
    type ComponentPropsWithRef,
    type KeyboardEvent,
    createContext,
    use,
    useCallback,
    useEffect,
    useState
} from 'react'

import useEmblaCarousel, { type UseEmblaCarouselType } from 'embla-carousel-react'
import { IconChevronLeft, IconChevronRight } from 'hq-icons'
import type { ListBoxItemProps, ListBoxSectionProps } from 'react-aria-components'
import { Button, ListBox, ListBoxItem, ListBoxSection, composeRenderProps } from 'react-aria-components'

import { cn } from '@/lib/utils'

type CarouselApi = UseEmblaCarouselType[1]
type UseCarouselParameters = Parameters<typeof useEmblaCarousel>
type CarouselOptions = UseCarouselParameters[0]
type CarouselPlugin = UseCarouselParameters[1]

type CarouselContextProps = {
    carouselRef: ReturnType<typeof useEmblaCarousel>[0]
    api: ReturnType<typeof useEmblaCarousel>[1]
    scrollPrev: () => void
    scrollNext: () => void
    canScrollPrev: boolean
    canScrollNext: boolean
} & CarouselProps

const CarouselContext = createContext<CarouselContextProps | null>(null)

const useCarousel = () => {
    const context = use(CarouselContext)

    if (!context) {
        throw new Error('useCarousel must be used within a <Carousel />')
    }

    return context
}

interface CarouselRootProps {
    CarouselContent?: typeof CarouselContent
    CarouselHandler?: typeof CarouselHandler
    CarouselItem?: typeof CarouselItem
}

interface CarouselProps extends CarouselRootProps, ComponentPropsWithRef<'div'> {
    opts?: CarouselOptions
    plugins?: CarouselPlugin
    orientation?: 'horizontal' | 'vertical'
    setApi?: (api: CarouselApi) => void
}

const Carousel = ({
    orientation = 'horizontal',
    opts,
    setApi,
    plugins,
    className,
    children,
    ...props
}: CarouselProps) => {
    const [carouselRef, api] = useEmblaCarousel(
        {
            ...opts,
            axis: orientation === 'horizontal' ? 'x' : 'y'
        },
        plugins
    )
    const [canScrollPrev, setCanScrollPrev] = useState(false)
    const [canScrollNext, setCanScrollNext] = useState(false)

    const onSelect = useCallback((api: CarouselApi) => {
        if (!api) {
            return
        }

        setCanScrollPrev(api.canScrollPrev())
        setCanScrollNext(api.canScrollNext())
    }, [])

    const scrollPrev = useCallback(() => {
        api?.scrollPrev()
    }, [api])

    const scrollNext = useCallback(() => {
        api?.scrollNext()
    }, [api])

    const handleKeyDown = useCallback(
        (event: KeyboardEvent<HTMLDivElement>) => {
            if (event.key === 'ArrowLeft') {
                event.preventDefault()
                scrollPrev()
            } else if (event.key === 'ArrowRight') {
                event.preventDefault()
                scrollNext()
            }
        },
        [scrollPrev, scrollNext]
    )

    useEffect(() => {
        if (!api || !setApi) {
            return
        }

        setApi(api)
    }, [api, setApi])

    useEffect(() => {
        if (!api) {
            return
        }

        onSelect(api)
        api.on('reInit', onSelect)
        api.on('select', onSelect)

        return () => {
            api?.off('select', onSelect)
        }
    }, [api, onSelect])

    return (
        <CarouselContext.Provider
            value={{
                carouselRef,
                api: api,
                opts,
                orientation: orientation || (opts?.axis === 'y' ? 'vertical' : 'horizontal'),
                scrollPrev,
                scrollNext,
                canScrollPrev,
                canScrollNext
            }}
        >
            <div onKeyDownCapture={handleKeyDown} className={cn('relative', className)} role='region' {...props}>
                {children}
            </div>
        </CarouselContext.Provider>
    )
}

const CarouselContent = <T extends object>({ className, ...props }: ListBoxSectionProps<T>) => {
    const { carouselRef, orientation } = useCarousel()

    return (
        <ListBox
            layout={orientation === 'vertical' ? 'stack' : 'grid'}
            aria-label='Slides'
            orientation={orientation}
            ref={carouselRef}
            className='overflow-hidden'
        >
            <ListBoxSection
                className={cn('flex', orientation === 'horizontal' ? '-ml-4' : '-mt-4 flex-col', className)}
                {...props}
            />
        </ListBox>
    )
}

const CarouselItem = ({ className, ...props }: ListBoxItemProps) => {
    const { orientation } = useCarousel()

    return (
        <ListBoxItem
            aria-label={`Slide ${props.id}`}
            className={composeRenderProps(className, (className) =>
                cn(
                    'group relative min-w-0 shrink-0 grow-0 basis-full outline-hidden',
                    orientation === 'horizontal' ? 'pl-4' : 'pt-4',
                    className
                )
            )}
            {...props}
        />
    )
}

const CarouselHandler = ({ className, ...props }: ComponentPropsWithRef<'div'>) => {
    const { orientation, scrollPrev, canScrollPrev, scrollNext, canScrollNext } = useCarousel()
    return (
        <div
            slot='carousel-handler'
            className={cn(
                'relative z-10 my-4 flex items-center gap-x-2',
                orientation === 'horizontal' ? 'justify-end' : 'justify-center',
                className
            )}
            {...props}
        >
            <Button
                aria-label='Previous Slide'
                data-handler='previous'
                className='inline-flex size-7 shrink-0 orientation-vertical:rotate-90 items-center justify-center rounded-md border border-muted/40 bg-bg pressed:bg-muted/50 text-muted-fg shadow-sm outline-hidden hover:bg-muted/40 focus-visible:ring-2 focus-visible:ring-ring disabled:pointer-events-none disabled:opacity-50'
                isDisabled={!canScrollPrev}
                onPress={scrollPrev}
            >
                <IconChevronLeft />
            </Button>
            <Button
                aria-label='Next Slide'
                data-handler='next'
                className='inline-flex size-7 shrink-0 orientation-vertical:rotate-90 items-center justify-center rounded-md border border-muted/40 bg-bg pressed:bg-muted/50 text-muted-fg shadow-sm outline-hidden hover:bg-muted/40 focus-visible:ring-2 focus-visible:ring-ring disabled:pointer-events-none disabled:opacity-50'
                isDisabled={!canScrollNext}
                onPress={scrollNext}
            >
                <IconChevronRight />
            </Button>
        </div>
    )
}

Carousel.Content = CarouselContent
Carousel.Handler = CarouselHandler
Carousel.Item = CarouselItem

export { Carousel }
export type { CarouselApi }

