forms/input-otp/input-otp-04.raw:
'use client'

import { useEffect, useId, useState } from 'react'
import { InputOTP, InputOTPGroup, InputOTPSlot } from '@/components/ui/input-otp'
import { Label } from '@/components/ui/label'

const InputOTPWithResendTimerDemo = () => {
  const [timeLeft, setTimeLeft] = useState(30)
  const id = useId()

  useEffect(() => {
    if (timeLeft > 0) {
      const timer = setTimeout(() => {
        setTimeLeft((prev) => prev - 1)
      }, 1000)

      return () => clearTimeout(timer)
    }
  }, [timeLeft])

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60)
    const secs = seconds % 60

    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`
  }

  const handleResend = () => {
    setTimeLeft(60)
  }

  return (
    <div className='space-y-3'>
      <Label htmlFor={id}>Input OTP with resend timer</Label>
      <InputOTP id={id} maxLength={4}>
        <InputOTPGroup>
          <InputOTPSlot index={0} />
          <InputOTPSlot index={1} />
          <InputOTPSlot index={2} />
          <InputOTPSlot index={3} />
        </InputOTPGroup>
      </InputOTP>
      <p className='text-muted-foreground text-xs'>
        {timeLeft > 0 ? (
          `Resend available in ${formatTime(timeLeft)}`
        ) : (
          <a
            className='underline hover:text-primary'
            href='#'
            onClick={(e) => {
              e.preventDefault()
              handleResend()
            }}
          >
            Resend code
          </a>
        )}
      </p>
    </div>
  )
}

export default InputOTPWithResendTimerDemo

forms/input-otp/input-otp-11.raw:
'use client'

import { IconDeviceMobile } from '@tabler/icons-react'
import { type FormEvent, startTransition, useEffect, useState } from 'react'
import { Form } from 'react-aria-components'
import { toast } from 'sonner'
import { Button } from '@/components/ui/button'
import { InputOTP, InputOTPGroup, InputOTPSeparator, InputOTPSlot } from '@/components/ui/input-otp'

const Example = () => {
  const [countdown, setCountdown] = useState(60)
  const [canResend, setCanResend] = useState(false)

  useEffect(() => {
    if (countdown > 0) {
      const timer = setTimeout(() => {
        setCountdown((prev) => prev - 1)
      }, 1000)
      return () => clearTimeout(timer)
    } else {
      startTransition(() => {
        setCanResend(true)
      })
    }
  }, [countdown])

  function onSubmit(e: FormEvent<HTMLFormElement>) {
    e.preventDefault()
    toast.success('Phone number verified successfully!')
  }

  const handleResend = () => {
    setCountdown(60)
    setCanResend(false)
    toast.info('Verification code resent')
  }

  return (
    <div className='w-full max-w-sm space-y-6'>
      <div className='flex flex-col items-center space-y-2 text-center'>
        <div className='flex h-12 w-12 items-center justify-center rounded-full bg-primary/10'>
          <IconDeviceMobile className='h-6 w-6 text-primary' />
        </div>
        <h2 className='font-semibold text-2xl'>Verify your phone</h2>
        <p className='text-muted-foreground text-sm'>
          We sent a code to <span className='font-medium'>+1 (555) 123-4567</span>
        </p>
      </div>
      <Form className='space-y-6' onSubmit={onSubmit}>
        <InputOTP maxLength={6} name='code'>
          <InputOTPGroup>
            <InputOTPSlot index={0} />
            <InputOTPSlot index={1} />
            <InputOTPSlot index={2} />
          </InputOTPGroup>
          <InputOTPSeparator />
          <InputOTPGroup>
            <InputOTPSlot index={3} />
            <InputOTPSlot index={4} />
            <InputOTPSlot index={5} />
          </InputOTPGroup>
