          {!isIndeterminate ? (
            <div
              className='absolute top-0 left-0 h-full rounded-full bg-primary transition-[width] duration-200 ease-linear will-change-[width] motion-reduce:transition-none'
              data-slot='bar'
              style={{ width: `${percentage}%` }}
