          ) : (
            <div
              className='absolute top-0 h-full animate-[progress-slide_2000ms_ease-in-out_infinite] rounded-full bg-primary'
              data-slot='bar'
              style={{ width: '40%' }}
            />
          )}
        </div>
      </div>
    </span>
  )
}

