Sheet.Body = SheetBody
Sheet.Footer = SheetFooter
Sheet.Header = SheetHeader
Sheet.Overlay = SheetOverlay
Sheet.Title = SheetTitle
Sheet.Description = SheetDescription
Sheet.X = SheetX

export {
  Sheet,
  SheetTrigger,
  SheetContent,
  SheetBody,
  SheetDescription,
  SheetFooter,
  SheetHeader,
  SheetOverlay,
  SheetTitle,
  SheetX
}

